css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sto-forum.perfectworld.com") {

body {background: #000000;}
.main-background {
    background: url("http://images-cdn.perfectworld.com/en/sto/redesign2012/framework/main-background.jpg") no-repeat scroll center top rgba(0, 0, 0, 0) !important; /*important for Chrome*/
}
.bg-body-content {
    background: none repeat scroll 0 0 !important; /*important for Chrome*/
    background-color: rgba(0, 0, 0, 0) !important; /*important for Chrome*/
    box-shadow: none !important; /*important for Chrome*/
}
.bg-media {
    height: 90px !important; /*important for Chrome*/
}
.header {
    background: none repeat scroll 0 0 #141213;
}

#topbar-container ul.topWrap {
    background: none repeat scroll 0 0 #131313 !important; /*important for Chrome*/
}
.page {
    background: none repeat scroll 0 0 #000000;
}
.alt1, .alt1Active {
    background: none repeat scroll 0 0 #0F1010;
}
.alt2, .alt2Active {
    background: none repeat scroll 0 0 #0F1010;
}
.vbmenu_control {
    background: none repeat scroll 0 0 #131313;
}
a.lnkLogo {
    background: url("http://images-cdn.perfectworld.com/en/sto/redesign2012/framework/bg-lnkHome.png") no-repeat scroll 0 0 rgba(0, 0, 0, 0) !important; /*important for Chrome*/
    display: block !important; /*important for Chrome*/
    height: 210px !important; /*important for Chrome*/
    margin: 0 auto !important; /*important for Chrome*/
    width: 290px !important; /*important for Chrome*/
    background-position: 50% 70% !important; /*important for Chrome*/
    
}
.PW-postbit-content {
    width: 765px !important; /*important for Chrome*/
}
.pagenav td {
    padding: 10px 4px;
}
div.pagenav > table > tbody > tr > td.alt1 > a.smallfont {
    font: 14px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;
    padding: 10px 5px;
    font-weight:bold;
}
td.alt1Active > table > tbody > tr > td > div > a > strong {
    font: 16px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;
    padding: 10px 5px;
    font-weight:bold;
}
.alt2 > span.smallfont > strong, .alt2 > div > strong {
    font: 14px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;
    color: #4E7EDB;
    font-weight:bold;
}
.alt2 > div.smallfont > strong {
    font: 14px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;
    color: #ccc !important;
    font-weight:bolder;
}
.navbar strong {
    font: 14px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;
    color: #4E7EDB;
    font-weight:bold;
}
.alt2 > div:nth-of-type(2) {
    font-style: normal !important;
}
.bigusername {
    font: 16px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;
    font-weight:bold;
}
.forum-index li.even {
    background: url("http://images-cdn.perfectworld.com/en/sto/redesign2012/forum/ico-thread.png") no-repeat scroll 10px center #0F1011;
}
.forum-index li.odd {
    background: url("http://images-cdn.perfectworld.com/en/sto/redesign2012/forum/ico-thread.png") no-repeat scroll 10px center #1B1B1B;
}
.title {
    font: 16px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;
    font-weight:bold;
}
.page {
    color:#ccc;
}
.panel > div:nth-of-type(1) {
    max-width: 900px !important;
}
.panel > div > table {
    max-width: 900px !important;
    width: 900px !important;
}
.vBulletin_editor {
    min-height: 300px;
}
#vB_Editor_QR_textarea {
    min-height: 265px;
    background-color: #EEEEEE;
    width: 888px !important;
}
#vB_Editor_QR {
    background-color: #555;
}
#vB_Editor_001 {
    background-color: #555;
}
#vB_Editor_001_textarea {
    min-height: 500px !important;
    background-color: #EEEEEE;
    width: 830px !important;
}
#vB_Editor_QE_1_textarea {
    background-color: #EEEEEE;
    width: 720px !important;
}
td.vBulletin_editor {
    background-color: #555;
}
td.panelsurround > div.panel > div {
    min-width: 900px;
}
td.panelsurround > div.panel {
    background: none repeat scroll 0 0 #181A1D;
}
td.panelsurround {
    background: none repeat scroll 0 0 #181A1D;
}
table.panel {
    background: none repeat scroll 0 0 #181A1D;
}
td.vbmenu_option {
    background: none repeat scroll 0 0 #181A1D;
    color: #ccc;
}
.alt1 > div > a {
    font: 14px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;
    color: #fff;
}
.alt1 > div.smallfont > span, .alt1 > div > span, .alt1 > div > span > a {
    color: #777 !important;
}
div.PW-postbit-userinfo {
    width: 178px;
    border-right: 1px solid #282B31;
}
div.PW-postbit {
    border-left: 1px solid #282B31;
    border-right: 1px solid #282B31;
    width: 975px;
}
div.infraction {
    margin: 0px !important;
    width: 0px !important;
}
div.ip {
    margin: 0px !important;
    width: 0px !important;
}
div.avatar {
    width: 180px !important;
}
div.avatar > a > img {
    display: block;
    margin-left: auto;
    margin-right: auto;
    margin-top: 5px;
}
div.ads {
    width: 0px;
    height: 0px;
}
table.tborder > tbody > tr > td.alt2 > a {
    color: #f00;
    font-weight: bolder;
    padding: 20px 40px;
    font: 16px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;
}
table.tborder > tbody > tr > td.alt2 > a.smallfont {
    color: #4E7EDB;
    font-weight: bolder;
    padding: 6px 45px 6px 15px;
    font: 12px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;
}
td.vbmenu_option > a {
    color: #fff !important;
    padding: 2px 5px;
}
td.vbmenu_hilite_alink > a {
    color: #000 !important;
    padding: 2px 5px;
    text-decoration: underline !important;
}
#notifications > strong {
    color: #f00;
}
div.PW-postbit-content > div.header > div.topic > a {
    color: #4E7EDB;
}
