css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("du00.com") {
/* reset default style */
body {
    font-family: monospace;
    font-size: initial;
}
#linkleft, #linkright {
    height: normal;
    width: auto;
}
#title {
    line-height: normal;
}
#title h2 {
    margin: 0 auto;
}
#content {
    padding: inherit;
    line-height: normal;
}
#content br {
    display: block;
}
#pagecontent {
    padding: inherit;
    font-family: monospace;
    line-height: normal;
    letter-spacing: normal;
}

/* space saver */
#content>font:first-of-type, 
#pagecontent>font:first-of-type {
    display: none;
}

/* AD hider */
body > :not(br):not(#linkleft):not(#linkright):not(#title):not(#content):not(#footlink) {
    display: none;
}
}
