/* ***** styling for 'big' usernames on postbit etc. ***** */
.bigusername { font-size: 11pt; font-weight: bold; }

/* ***** small padding on 'thead' elements ***** */
td.thead, th.thead, div.thead { padding: 4px; }

/* ***** basic styles for multi-page nav elements */
.pagenav a { text-decoration: none; }
.pagenav td { padding: 2px 4px 2px 4px; }

/* ***** de-emphasized text */
.shade, a.shade:link, a.shade:visited { color: #777777; text-decoration: none; }
a.shade:active, a.shade:hover { color: #FF4400; text-decoration: underline; }
.tcat .shade, .thead .shade, .tfoot .shade { color: #DDDDDD; }

/* ***** define margin and font-size for elements inside panels ***** */
.fieldset { margin-bottom: 6px; }
.fieldset, .fieldset td, .fieldset p, .fieldset li { font-size: 11px; }

/* ***** custom headings */
h1 { font: bold 22px/27px "trebuchet ms", trebuchet, arial, verdana; margin: 0px; color: #000000; display: inline; }
h2 { font: bold 17px/16px "trebuchet ms", trebuchet, arial, verdana; margin: 0px; color: #112244; display: inline; border-bottom: 1px solid #dddddd; width: 100%; }
h2.title { font: bold 22px/27px "trebuchet ms", trebuchet, arial, verdana; margin: 0px; color: #000000; display: inline; border: 0px; width: auto; }
h2.noline { font: bold 17px/16px "trebuchet ms", trebuchet, arial, verdana; margin: 0px; color: #112244; display: inline; border: 0px; width: 100%; }
h2.review { font: bold 18px/16px "trebuchet ms", trebuchet, arial, verdana; margin: 0px; color: #112244; display: inline; border: 0px; }
h3 { font: bold 15px/16px "trebuchet ms", trebuchet, arial, verdana; margin: 0px; color: #223355; display: inline; }
h4 { font: bold 13px/14px verdana, arial, sans-serif; margin: 0px; color: #334466; display: inline; }
h5 { font: bold 12px/14px verdana, arial, sans-serif; margin: 0px; color: #223355; display: inline; }
h1 span, h2 span, h3 span, h4 span, h5 span { font-weight: normal; }
div.boxheader { font: bold 15px trebuchet ms; color: black; padding: 2px 5px; }

/* ***** sitenav title */
div.sitenav { font: bold 11px verdana, arial, sans-serif; color: #ffffff; position: absolute; top: 75px; margin: 0px; padding-left: 20px; width: 670px; overflow: hidden; text-overflow: ellipsis; white-space: nowrap; }
div.sitenav h1 { font: bold 11px verdana, arial, sans-serif; color: #ffffff; display: inline; }
div.sitenav span { font-weight: normal; }
div.sitenav1024 { font: bold 11px verdana, arial, sans-serif; color: #ffffff; position: absolute; top: 66px; margin: 0px; padding-left: 20px; width: 670px; overflow: hidden; text-overflow: ellipsis; white-space: nowrap; }
div.sitenav1024 h1 { font: bold 11px verdana, arial, sans-serif; color: #ffffff; display: inline; }
div.sitenav1024 span { font-weight: normal; }
div.sitenavshort { font: bold 11px verdana, arial, sans-serif; color: #ffffff; position: absolute; top: 75px; margin: 0px; padding-left: 20px; width: 220px; overflow: hidden; text-overflow: ellipsis; white-space: nowrap; }
div.sitenavshort h1 { font: bold 11px verdana, arial, sans-serif; color: #ffffff; display: inline; }
div.sitenavshort span { font-weight: normal; }

div.vb_userinfo { text-align: right; border-bottom: 1px solid #e5e5e5; margin-bottom: 1px; padding: 2px 4px 2px 0px; }
div.vb_userinfo label { float: left; }

div.button, div.submit {
	display: inline;
	margin-right: 2px;
	padding: 0px 1px;
	background: transparent url(http://images4.static-bluray.com/button_side.gif) repeat-x;
}

div.button a, div.submit input {
	margin: 0px;
	font: 12px verdana;
	white-space: nowrap;
	color: #223355;
	background: transparent url(http://images.static-bluray.com/button_mid.gif) repeat-x;
}

div.button a {
	padding: 2px 10px;
	text-decoration: none;
}

div.submit input {
	height: 18px;
	border: 0px;
	padding: 2px 3px;
	cursor: hand; cursor: pointer;
}

div.auto_complete { width: 346px; background: #fff; }
div.auto_complete ul { width: 100%;  margin: 0; padding: 0; border: 1px solid #000; list-style-type: none; }
div.auto_complete ul li { font: 11px/13px verdana, arial, sans-serif; color: #333333; margin: 0; padding-left: 3px; }
div.auto_complete ul li.selected { color: #fff; background-color: #36c; }

.salebutton {
	-moz-box-shadow:inset 0px 1px 0px 0px #f5978e;
	-webkit-box-shadow:inset 0px 1px 0px 0px #f5978e;
	box-shadow:inset 0px 1px 0px 0px #f5978e;
	background:-webkit-gradient( linear, left top, left bottom, color-stop(0.05, #f24537), color-stop(1, #c62d1f) );
	background:-moz-linear-gradient( center top, #f24537 5%, #c62d1f 100% );
        background-color:#f24537;
	-moz-border-radius:6px;
	-webkit-border-radius:6px;
	border-radius:6px;
	border:1px solid #d02718;
	display:inline-block;
	color:#ffffff;
	font-family:Arial;
	font-size:15px;
	font-weight:bold;
	padding:2px 24px;
	text-decoration:none;
	text-shadow:1px 1px 0px #810e05;
}
.salebutton:hover {
	text-shadow:0px 0px 0px transparent;
	background:-webkit-gradient( linear, left top, left bottom, color-stop(0.05, #c62d1f), color-stop(1, #f24537) );
	background:-moz-linear-gradient( center top, #c62d1f 5%, #f24537 100% );
       	background-color:#c62d1f; }
.salebutton:active {
	position:relative;
	top:1px;
}

.newlogo {
	background: #4396da url(http://images4.static-bluray.com/newlogo4.jpg) no-repeat;
}

.bgmenu {
	background: #eff4f8 url(http://images4.static-bluray.com/bgmenu.gif);
}

.product_rating { background: url(http://images.blu-ray.com/ratingstars.png) repeat-x; background-position: 0px 0px; position: relative; width: 120px; height: 24px; }
.product_rating .average_rating { background: url(http://images.blu-ray.com/ratingstars.png) repeat-x; background-position: 0px -24px; position: absolute; height: 24px; top: 0px; left: 0px; }

#menu { margin:0px; padding:0px; list-style:none; display:inline-block; z-index:1000; }
#menu a { text-decoration:none; color: #223355; }
#menu > li {position: relative; cursor:pointer; padding:0px 5px; }
/* sub-menus*/
#menu ul { padding:0px; margin:0px; display:inline; }
#menu li ul { position: absolute; top:0px; left:0px; margin-top:28px; width:100px; line-height:25px; background-color:#c7d5e7; color:#0395CC; /* IE */ display:none; border: 1px solid #9ba2b4; border-top: 0px; box-shadow: 2px 2px 9px rgba(0,0,0,0.25); }
#menu li:hover ul { display:block; }
#menu li ul li { display:block; margin:0px 0px; padding: 0px 0px; list-style-type:none; }
#menu li ul li:first-child { border-top: none; }
#menu li ul li a { display:block; border-top: 1px dotted #9ba2b4; padding-left: 8px; }
#menu li ul li a:hover { color:#d60; }

