@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("oper.ru") {
    .banner,.banners,#odStageContainer,.mega_block,.recommend{
       display:none;
    }
    body {
       background-color: #000;
    }
    #container {
        max-width: 1400px;
    }
    #container > table > tbody > tr {
	   background-color: #222; 
    }
    .body iframe {
       width:100% !important;
       height:500px !important;
    }
   .bottomh {
      padding-left: 10px;
      padding-bottom: 20px;
   }
}
