css:
@-moz-document  domain("thepiratebay.se"), domain("thepiratebay.cr")  {
body {background:#202020 !important;color:#666 !important;}
a {color:#738A91 !important;border:none !important;}
a:hover {color:rgb(99, 185, 218) !important;border:none !important;text-decoration:none !important;}
h2 {margin:15px 0 0 !important;background:#202020 !important;border-bottom:0px solid #272727 !important;color:#202020 !important;}
#fp {margin-top:50px !important;}
#topright {display:none !important;}
#foot {margin-top:30px !important;}
#bnr {display:none;}

#q {
vertical-align: bottom;
text-align: center;
margin: 0;
}
#header{
  width:700px !important; background: #212121!important; 
margin-left:auto;
margin-right:auto;
padding:20px; 

}
table {
border-collapse: separate;
border-spacing: 2px;
border-color: transparent!important;
}
input, input[type="password"], input[type="search"], isindex {
-webkit-appearance: textfield;
padding:1px;
background-color: rgb(60,60,60) !important;
color: rgb(200,200,200) !important;
box-shadow: 0px 0px rgb(40,40,40) inset !important;
border-radius: 3px !important;
border: 0px !important;
    

border: 4px solid rgb(60,60,60)!important;
}
#main-content {
margin-right: 0px !important;
}

h1 a {width:350px !important;height:350px !important;pointer-events:none;cursor:default;visibility:hidden;}
img[alt="The Pirate Bay"] {width:0;padding:0px !important;}
img[src="http://static.thepiratebay.se/img/icon-magnet.gif"] {width:0;padding:6px !important;background:url("http://i.imgur.com/NEuxo.png") !important;}
img[src="http://static.thepiratebay.se/img/dl.gif"] {width:0 !important;height:0 !important;margin-left:-2px !important;padding:6px !important;background:url("http://i.imgur.com/m8qXN.png") !important;}
img[src="http://static.thepiratebay.se/img/icon_comment.gif"] {width:0;padding:6px !important;background:url("http://i.imgur.com/ZvdtA.png") !important;}
img[src="http://static.thepiratebay.se/img/icon_image.gif"] {width:0;padding:6px !important;background:url("http://i.imgur.com/HMeXC.png") !important;}
img[alt="VIP"] {width:0 !important;height:0 !important;padding:6px !important;background:url("http://i.imgur.com/5PHCA.png") !important;}
img[alt="Trusted"] {width:0 !important;height:0 !important;padding:6px !important;background:url("http://i.imgur.com/WSp8H.png") !important;}
img[alt="Helper"] {width:0 !important;height:0 !important;padding:6px !important;background:url("http://i.imgur.com/CAiMw.png") !important;}
img[alt="Next"], img[alt=">"] {width:0 !important;padding:6px !important;background:url("http://i.imgur.com/2ewBn.png") !important;}
img[alt="Previous"] {width:0 !important;padding:6px !important;background:url("http://i.imgur.com/pesLH.png") !important;}
#details a.comments {background:url("http://i.imgur.com/UIZ8F.png") no-repeat !important;}
img[alt="Bytelove"] {display:none;}
a.rss {display:none;}
a.kopimi {display:none;}
#icons {display:none;}
img[alt="//static.thepiratebay.se/img/comments.gif"] {width:0;padding:10px 12px !important;background:url("http://i.imgur.com/bHUzH.png") !important;}


select#category {border:0px solid #272727 !important;background-color:#222 !important;color:#7d7d7d !important;}
select#category:focus {outline:0 !important;border-color:#999 !important;}
#main-content {margin-bottom:0 !important;margin-left:0 !important;padding-right:0 !important;}
#main-content iframe, iframe, #sky-right, .ads, .ad {display:none !important;}
#content {width:960px !important;margin-top:0 !important;}
#searchResult {margin-top:16px !important;border:0px solid #272727 !important;}
thead#tableHead th {border:0px solid #272727 !important;background:#222 !important;color:#666 !important;padding:6px!important;}
thead#tableHead a {color:#adadad !important;}
table#searchResult {margin-bottom:15px !important;}
table#searchResult a {color:#888 !important;}
table#searchResult tr {background:#222 !important;}
table#searchResult tr:hover {background:#272727 !important;text-decoration:none !important;color:#6D8A92    !important;
    }
 table#searchResult td a:hover   {color:rgb(99, 185, 218) !important;}
table#searchResult td {border:0px solid #272727 !important;padding:6px!important;}
table#searchResult td.vertTh {color:#777 !important;}
.detLink {color:#ccc !important}
a.detDesc {color:#878787 !important;}
a.detDesc:hover {color:#0af !important;text-decoration:none !important;}


#detailsouterframe {width:684px !important;}
#detailsframe {width:680px !important;margin:30px auto 0px auto !important;border:0px solid #191919 !important;}
#detailsframe #title {padding:10px !important;border-bottom:1px solid #202020 !important;background:#232323 !important;}
#details .col1 {width:320px !important;margin:10px !important;margin-left:0 !important;margin-bottom:30px !important;}
#details .col2 {width:320px !important;margin:10px !important;margin-right:0 !important;margin-bottom:30px !important;}
.torpicture {width:320px !important;margin:10px !important;margin-right:0 !important;text-align:right;}
.torpicture img {margin:0 !important;}
#social {display:none !important;}
img[alt="Toolbar"], div.spons-link {display:none !important;}
#details {padding-left:10px !important;background:#232323 !important;}
#details dt, dd {border:none !important;color:#666 !important;}
#CommentDiv {width:636px !important;height:100% !important;margin:10px 0 0 0 !important;border:1px solid #272727 !important;background:#111 !important;}
p.info {margin:0 !important;padding:0 !important;}
p.info textarea {width:628px !important;max-width:628px !important;background:#171717 !important;color:#adadad !important;}
.download a[title="Get this torrent"] {background-image:url('http://i.imgur.com/NEuxo.png') !important;border:none !important;color:#080 !important;}
.download a[title="Torrent File"] {background-image:url('http://i.imgur.com/m8qXN.png') !important;border:none !important;color:#080 !important;}
.download a:hover {color:#080 !important;}
.nfo {width:636px;min-height:0 !important;margin:15px 0 !important;border:0px solid #212121 !important;background:#191919 !important;color:#888 !important;}
#details pre {margin:0 !important;}
#artistDetails, .detailartist {display:none !important;}
#filelistContainer {min-height:0 !important;margin:20px 0 !important;background:#222 !important;border:0px solid #272727 !important;}
#details h4 {color:#999 !important;}
#comments p.byline {margin-left:0 !important;padding-left:3px !important;background:#212121 !important;border:1px solid transparent !important;color:#474747 !important;}
#comments .comment {margin-top:0 !important;padding:5px !important;border:1px solid transparent !important;border-top:none !important;background:#212121 !important;}
.browse-coms {margin:10px 0 0 !important;}
.browse-coms a {color:#666 !important;}


#blog {width:500px !important;margin:30px auto !important;}
.post {margin-bottom:30px !important;border:2px solid #272727 !important;background:#171717 !important;}
.post h3 {margin-top:0 !important;padding:10px !important;border-bottom:2px solid #272727 !important;background:#111 !important;}
.post p {margin-left:10px !important;margin-right:10px !important;}
#blog #comments dt {padding-left:5px !important;border:2px solid #272727 !important;background:#111 !important;color:#474747 !important;}
#blog #comments dd {padding:10px !important;border:2px solid #272727 !important;border-top:none !important;background:#222 !important;color:#ddd !important;}


#browseContainer a {color:#999;}
#browseContainer a:hover {border-bottom:1px solid #999;}
dt#upload a {color:#080 !important;}
#categoriesTable {background:#222222;border:0px solid #272727;}
.uldetails label {color:#999;}
.uldetails td {border-bottom:2px solid #272727 !important;color:#999;}
.uldetails text, #tname, #torrent {width:300px;}


#loginForm {width:500px !important;margin:30px auto !important;padding:10px !important;border:2px solid #272727;background:#171717;}
#cpl {width:524px !important;margin:0 auto !important;padding:10px !important;border:2px solid #272727;background:#171717;}
#registerTable {width:524px !important;margin:30px auto !important;padding:10px !important;border:2px solid #272727;background:#171717;}
#legal-threats {width:500px !important;margin:30px auto !important;padding:10px !important;border:2px solid #272727;background:#171717;}
#about {width:500px;margin:30px auto !important;padding:10px;border:2px solid #272727;background:#171717;}
#about img {max-width:475px !important;height:100% !important;}
.policy {width:500px !important;}
#tpbpromo {width:500px !important;margin:30px auto !important;padding:10px;border:2px solid #272727;background:#171717;}
textarea#aboutyou {width:360px !important;max-width:360px !important;}
#irc {width:500px !important;margin:30px auto !important;padding:10px !important;border:2px solid #272727;background:#171717;}
#contact {width:500px !important;padding:10px;border:2px solid #272727 !important;background:#171717;}
#searchcloud {width:660px !important;margin:30px auto !important;padding:10px !important;border:2px solid #272727 !important;background:#171717 !important;}

/* CHROME SCROLL */
.uiScrollableArea {
   background-color: #212121!important;
}
::-webkit-scrollbar {
  width: 9px;
  height: 9px;
}
::-webkit-scrollbar-track-piece {
  background-color: #212121;
}
body::-webkit-scrollbar-track-piece {
  background-color: #212121;
}
::-webkit-scrollbar-track-piece:no-button {}
::-webkit-scrollbar-thumb {
  background-color: #444;
  border-radius: 3px;
}
::-webkit-scrollbar-thumb:hover {
  background-color: #777;
}
::-webkit-scrollbar-thumb:active {
  background-color: #666;
}

/* AGENT_SHEET */
scrollbar{
    -moz-appearance: none !important;
    background: none #212121 !important;
    padding: 2px !important;
    width: 9px !important}
scrollbar[orient="vertical"]{
    padding: 2px !important;}
scrollbar[orient="horizontal"]{
    height: 9px !important;}
scrollbarbutton[sbattr="scrollbar-up-top"],
scrollbarbutton[sbattr="scrollbar-down-top"],
scrollbarbutton[sbattr="scrollbar-up-bottom"],
scrollbarbutton[sbattr="scrollbar-down-bottom"]{
    -moz-appearance: none !important;
    display: none !important}
scrollbar thumb{
    -moz-appearance: none !important;
    background: none #444 !important;
    border: none !important;
    border-radius: 5px; }
scrollbar thumb:hover{
    background: none #555 !important;}
scrollbar thumb:active{
    background: none #555 !important}

}
