css:
/* 

[replacement 1x1 #333] = http://i.imgur.com/Y6NuqoC.png
category-23px-light.png = http://i.imgur.com/u18iZmN.png
xenforo-ui-sprite.png = http://i.imgur.com/35K4rOv.png
form-button-white-25px.png = http://i.imgur.com/6BDeKpH.png

*/

@-moz-document domain(redcafe.net) {
    
html,
body {
    background-color: #222!important;
    color: #EEE;
}
#logo {
    background: url(http://i.imgur.com/8A3J87C.png) no-repeat!important;
    height: 0!important;
    padding-left: 358px!important;
    padding-top: 95px!important;
    width: 0!important;
}
.nodeList .categoryStrip {
    background: url(http://i.imgur.com/Y6NuqoC.png) repeat-x scroll center top #444!important;
}
.nodeList .categoryStrip .nodeTitle a {
    color: #FFF!important;
}
.navTabs {
    background-color: #333;
    border: 1px solid #666;
}
.navTabs .navTab.selected .navLink {
    background: none repeat scroll 0 0 #433!important;
}
.navTabs .navTab.selected,
.tabLinks {
    background: none repeat scroll 0 0 #333!important;
    border: 1px solid #FB0;
}
.navTabs .navTab.selected .tabLinks a:hover,
.navTabs .navTab.selected .tabLinks a:focus {
    background-color: #777;
    color: #FB0;
}
.formPopup {
    background-color: #333;
    border: 2px solid #999;
}
.formPopup .controlsWrapper {
    background: url(http://i.imgur.com/Y6NuqoC.png) repeat-x scroll center top #555;
}
.navTabs .navTab.PopupClosed:hover .SplitCtrl {
    background: url(http://i.imgur.com/35K4rOv.png) no-repeat scroll -128px 5px #666;
}
.PageNav a,
.itemPageNav a {
    background-color: #000!important;
    color: #FB0!important;
}
.PageNav a:hover,
.itemPageNav a:hover {
    background-color: #111!important;
    color: #FB0!important;
}
.discussionList .sectionHeaders a {
    color: #CCC!important;
}
.messageList li.message {
    background-color: #333;
    border: 1px solid #333;
}
.messageList li.message .messageWrapper {
    background-color: initial;
    border: 0;
    border-radius: 0;
}
.messageUserBlock,
.messageUserBlock .extraUserInfo {
    background-color: #222!important;
}
.bbCodeQuote .attribution {
    background: url(http://i.imgur.com/Y6NuqoC.png) repeat-x scroll center top #555!important;
    color: #CCC;
}
.bbCodeQuote blockquote {
    background: url(http://i.imgur.com/Y6NuqoC.png) repeat-x scroll center top #666!important;
}
.redactor_toolbar {
    background: url(http://i.imgur.com/Y6NuqoC.png) repeat-x scroll center top #666;
}
.sectionFooter {
    background: url(http://i.imgur.com/Y6NuqoC.png) repeat-x scroll center top #333;
    color: #FFF;
}
.subHeading {
    background: url(http://i.imgur.com/Y6NuqoC.png) repeat-x scroll center top #666;
    color: #FB0;
}
.heading,
.xenForm .formHeader {
    background-color: #333;
    border-bottom: 1px solid #444;
}
.button {
    background: url(http://i.imgur.com/6BDeKpH.png) repeat-x scroll center top #333;
    box-shadow: 0 1px 1px 0 #777;
    color: #FFF;
    text-shadow: none;
}
.button:hover,
.button[href]:hover,
.buttonProxy:hover .button {
    background-color: #555;
    border-color: #FFF #FFF #BEBEAA;
    color: #DDD;
}
.sidebar .section .secondaryContent .footnote,
.sidebar .section .secondaryContent .minorHeading {
    color: #AAA;
}
.xenOverlay .formOverlay .heading {
    background-color: #444;
    border: 1px solid #555;
}
.xenOverlay .formOverlay .textCtrl {
    background-color: #888;
}
.xenOverlay .formOverlay .textCtrl:focus,
.xenOverlay .formOverlay .textCtrl.Focus {
    background: none repeat scroll 0 0 #888;
}
.xenOverlay .section,
.xenOverlay .sectionMain {
    border: 20px solid rgba(1, 1, 1, 0.5);
}
#loginBar {
    background-color: #470101!important;
}
#loginBar .textCtrl:focus {
    background-color: #FFF!important;
}
#loginBar .xenForm .ctrlUnit dd .textCtrl:focus,
#loginBar .passwordOptions:focus {
    color: #111;
}
#loginBar #loginBarHandle {
    background-color: #470101;
    box-shadow: none;
}
.PanelScroller .panel,
.PanelScrollerOff .panel {
    background: url(http://www.redcafe.net/styles/default/xenforo/gradients/category-23px-light.png) repeat-x scroll center top #555;
}
.tabs {
    background: url(http://www.redcafe.net/styles/default/xenforo/gradients/form-button-white-25px.png) repeat-x scroll center top #333;
}
.matchPost.matchUpdate {
    background-color: #AAA;
}
.messageList .newMessagesNotice {
    background: none;
}
#content .pageContent,
.breadcrumb .crust a.crumb,
.sectionMain,
.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo,
.footer .pageContent,
.Menu .primaryContent,
.Menu .secondaryContent,
.discussionListItem,
#DiscussionListOptionsHandle a,
.Menu .sectionFooter,
.navPopup .listPlaceholder ol.secondaryContent.Unread {
    background-color: #333;
}
.breadcrumb,
.node .nodeLastPost,
.secondaryContent {
    background: url(http://i.imgur.com/Y6NuqoC.png) repeat-x scroll center top #333;
}
.breadcrumb .crust:hover a.crumb,
.profilePage .tabs.mainTabs,
.button.primary,
.matchPost.matchUpdate .time,
.featuredLink a {
    background-color: #555;
}
.primaryContent a,
.secondaryContent a,
a:link,
a:visited,
.Popup .PopupControl:hover,
.Popup.PopupContainerControl:hover,
.Popup .PopupControl.PopupOpen,
.Popup.PopupContainerControl.PopupOpen,
.blockLinksList a,
.blockLinksList label,
.textWithCount.subHeading .text,
.blockLinksList a:active,
.blockLinksList a.selected,
.blockLinksList label:active,
.blockLinksList label.selected {
    color: #FB0;
}
.navTabs ul li a:hover,
.navTabs .navTab.PopupClosed:hover,
.postTitle,
.rulesReminder,
.blockLinksList a:hover,
.blockLinksList a:focus,
.blockLinksList li.kbSelect a,
.blockLinksList label:hover,
.blockLinksList label:focus,
.blockLinksList li.kbSelect label,
.navPopup .PopupItemLinkActive:hover,
.badge,
.badge.highlight {
    background-color: #666;
}
#QuickSearch,
.discussionList .sectionHeaders dd a[href]:hover {
    background-color: #999;
}
.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3,
.sidebar .section .primaryContent h3 a,
.sidebar .section .secondaryContent h3 a,
.listBlock.stats a,
.searchResult .snippet a,
.textHeading,
.xenForm .sectionHeader,
.subForumsPopup .PopupOpen .dt {
    color: #FFF;
}
.discussionList .sectionHeaders,
.discussionListItem .lastPost {
    background: url(http://i.imgur.com/Y6NuqoC.png) repeat-x scroll center top #333!important;
}
.discussionListItem .lastPostInfo .username,
.searchResult .meta a,
.forumListing ._depth0,
body .dimmed,
body a.dimmed,
body .dimmed a {
    color: #CCC;
}
.primaryContent,
.PanelScroller .scrollContainer,
.PanelScrollerOff .panel {
    background-color: #444;
}
.importantMessage {
    background-color: hsl(0, 0%, 40%);  
    color: white;
}
.EmbeddedTweet-tweet {
    /*color: #444;*/
    background-color: #666;
}
.Identity-screenName,
.Tweet-alert,
.Tweet-metadata,
.TweetAction,
.TweetAction:visited {
    color: #bbb;
}
.Icon--reply,
.Icon--retweet,
.Icon--favorite {
    filter: hue-rotate(200deg);
    -webkit-filter: hue-rotate(200deg);
}
.hasJs .bbCodeSpoilerText {
    background-color: initial;
}

}
