@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("delicious.com"){
 a, label {
	background-image:none !important;
	color:#DDD !important}
 body {
	background-color:#000 !important;
	color:#DDD !important}
 input, textarea, select, h3, div#caption, div.yui-b#sidebar, div.yui-b#sidebar a, div.yui-b#sidebar h4, div.yui-b#sidebar span, div#sidenav-title, li a, div.bookmark, em, div#searchbar, ul#globalnav, ul#viewlistoptions, li.listoption-item {
	background-color:#040 !important;
	background-image:none!important;
	border-color:#010 !important;
	color:#FFF !important}
 form#saveitem, ul#recommendations, li.panel, a.tag-list-tag, a#viewact, span.savedon, form.inlineeditor, li.loading {
	background-color:#060!important;
	border:none!important;
	color:#DDD!important}
a.tag-chain-item-link, span.tag-chain-item-span, div#pagination a, div#content {
	background-color:#060!important;
	background-image:none!important;
	border:none!important;
	color:#DDD!important}
 div.field#notesfield {
	height:50px!important}

 div.yui-ac-content, a.tabSelect#act-people-rec, h3#tagBundles, span.txt, div.search-box, div#banner, a.tabSelect, div#ttl, div#ft, div.beacon, a.delNav, div#display-and-rss-options, div#actions, div.clr, ul#tagscopenav, div#player-full, div#searcharea {
	display:none!important} }
