css:
@-moz-document url("http://forums.d2jsp.org/"), url-prefix("http://forums.d2jsp.org/f"), url-prefix("http://forums.d2jsp.org/r"), url-prefix("http://forums.d2jsp.org/s"), url-prefix("http://forums.d2jsp.org/l"), url-prefix("http://forums.d2jsp.org/g"), url-prefix("http://forums.d2jsp.org/e"), url-prefix("http://forums.d2jsp.org/t"), url-prefix("http://forums.d2jsp.org/p"), url-prefix("http://forums.d2jsp.org/c"), url-prefix("http://forums.d2jsp.org/i"), url-prefix("http://www.d2jsp.org"), url-prefix("http://ladderslasher.d2jsp.org/"), url-prefix("http://d2jsp.org") {
    @font-face {
      font-family: 'Exo';
      font-style: normal;
      font-weight: 400;
      src: local('Exo Regular'), local('Exo-Regular'), url(http://themes.googleusercontent.com/static/fonts/exo/v2/wocyXRLWPo2Av-yUTmmbTA.woff) format('woff');
    }
    
    @font-face {
      font-family: 'Exo';
      font-style: normal;
      font-weight: 700;
      src: local('Exo Bold'), local('Exo-Bold'), url(http://themes.googleusercontent.com/static/fonts/exo/v2/RokIQ-knd7LT9uhmuUjL2g.woff) format('woff');
    }
    
    body {
        color:#CCC;
        font-family:Exo;
        background-image:url('http://subtlepatterns.com/patterns/asfalt.png');
    }
    
    .head, .bar, #sss, .p9, dt, dd, .ftb, dd table tr td, dd table tr th, .foot, .news, .pager, .pager a, .pager b, .bc1, .quote1 {
        background-color:#333 !important;
        font-family:Exo;
    }
    
    textarea {
        background-color:#444 !important;
        color:#CCC;
        border:1px solid #000;
    }
    
    a, dt a, a.cl {
        color:#CCC;
        text-decoration:none;
        border-bottom:1px dotted #CCC;
    }
    
    a:hover, dt a:hover {
        color:#F33;
        text-decoration:none;
        border-bottom:1px dotted #F33;
    }
    
    div.lD a {
        border:none;
    }
    
    p, b, i, em, dt, dt a, div.p3, td, .desc, th {
        color:#CCC;
    }
    
    .quote1, .quote2, .code2 {
        border-left:3px solid #F33;
        border-right:none;
        border-bottom:none;
    }
    
    .quote1 {
        border-top:3px solid #444;
    }
    
    .pud {
        border-right:1px solid #000;
    }
    
    .upc {
        padding-left:10px;
    }
    
    .bts.ppc {
        border-top:1px solid #000;
    }
    
    .bts, .bts TD, .foot {
        border-top:3px solid #111 !important;   
    }
    
    dt {
       border-top-left-radius:5px;
       border-top-right-radius:5px;
    }
    
    #frb dd.p3 a, div.bc1.p3 div a img, #iMsg a{
        background-color:#FFF;
        padding:2px 0px;
        width:17px;
        height:17px;
    }
}
