css:
@-moz-document domain(localhost) {

html, body, #yacysearch { 
    background-color: #222222;
}

a {
    color: #cccccc !important;
    text-decoration: none !important;
}

a:hover {
    color: #eeeeee !important;
    text-decoration: underline;
}

#ysearch .fancy, input {
    vertical-align: middle;
    border: 0px solid #888888;
    display: inline-block;
    position: relative;
    margin: 5px;
    padding: 3px 15px;
    text-align: left;
    text-decoration: none;
    font: bold 14px/25px Arial, sans-serif;
    text-shadow: 1px 1px 1px rgba(255,255,255, .22);
    -webkit-border-radius: 30px;
    border-radius:30px;
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0, .29), inset 1px 1px 1px rgba(255,255,255, .44);
    box-shadow: 1px 1px 1px rgba(0,0,0, .29), inset 1px 1px 1px rgba(255,255,255, .44);
    -webkit-transition: all 0.15s ease;
    -moz-transition: all 0.15s ease;
    -o-transition: all 0.15s ease;
    -ms-transition: all 0.15s ease;
    transition: all 0.15s ease;
    color: #333333;
    background: #d3d3d3;
    background: -moz-linear-gradient(bottom,  #d3d3d3 0%, #8a8a8a 100%);
}

input:hover { 
    border: 0px solid #888888;
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,.29), inset 0px 0px 2px rgba(0,0,0, .5);
    box-shadow: 1px 1px 1px rgba(0,0,0,.29), inset 0px 0px 2px rgba(0,0,0, .5);
}
    
#search {
    border: 0px solid #888888;
    display: inline-block;
    position: relative;
    margin: 10px;
    padding: 5px 20px;
    text-align: left;
    text-decoration: none;
    font: bold 14px/25px Arial, sans-serif;
    text-shadow: 1px 1px 1px rgba(255,255,255, .22);
    -webkit-border-radius: 30px;
    border-radius: 30px;
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0, .29), inset 1px 1px 1px rgba(255,255,255, .44);
    box-shadow: 1px 1px 1px rgba(0,0,0, .29), inset 1px 1px 1px rgba(255,255,255, .44);
    -webkit-transition: all 0.15s ease;
    -moz-transition: all 0.15s ease;
    -o-transition: all 0.15s ease;
    -ms-transition: all 0.15s ease;
    transition: all 0.15s ease;
    color: #333333;
    background: #d3d3d3;
    background: -moz-linear-gradient(bottom, #d3d3d3 0%, #8a8a8a 100%);
}

#yacysearch > div:nth-child(5) > div:nth-child(7) {
    width: 220px !important;
    margin-top: 20px;
}
    
#yacysearch > div:nth-child(5) > div:nth-child(7) > div:nth-child(1) {
    margin-bottom: 30px !important;
}

#sidebarProtocols > dl:nth-child(2),
#sidebarFiletypes > dl:nth-child(2),
#sidebarDomains > dl:nth-child(2)
{
    width: 183px !important;
    background-color: #333333;
    border: 1px solid #444444;
    border-radius: 1px;
    padding-top: 10px;
    margin-left: 10px;
    color: #888888;
    border-radius: 3px;
}

div#tagcloud > div{
    width: 220px;
    margin-left: 0px;
}
    
fieldset {
    border: none;
    background: none;
    text-align: center;
}

h4 {
    font-size: 1.0em;
}
    
h4.linktitle {
    margin: 2px;
    padding-left: 20px;
    padding-bottom: 6px;
    border-bottom: 0px solid #444444;
}

.ProgressBar {
    margin: 5px;
    border: 1px solid #666666;
}

.urlinfo {
    font-size: 8px !important;
    color: #666666 !important;
}

.linktitle {
    font-size: 16px !important;
}

div.searchresults {
    padding: 5px;
    margin-top: 15px;
    margin-left: 30px;
    border: none;
}

div.searchresults .url a {
    font-size: 14px !important;
    color: #666666 !important;
    border: none;
}

.ProgressBarFill { 
    background-color: #556699;
	border: 1px solid #666666;
}

.snippet {
    padding-left: 32px;
    color: #888888;
}

#resProgBar { 
    padding-top: 30px;
	padding-bottom: 20px;
}

.ProgressBar {
    border: none;
}

div.yacysearch {
    margin-top: 0em;
    text-align: center;
}

.yacys { border: none;
	background-color: #222222;
}

.bplike dl { 
    margin-top: 11px;
	margin-left: 8px;
}

.bplike dd { 
    margin-top: 3px;
	margin-left: 3px;
}
    
.bplike dt { 
    height: 16px;
	margin-top: 0px;
	margin-left: 3px;
}

.ui-widget {
    font-size: 90%;
}

ul#sidebarTopics {
    list-style: circle inside;
    float: right;
    padding: 8px !important;
    display: block;
    clear: both;
    margin-right: 0px;
    padding: 8px;
    background-color: #333333;
    border-radius: 3px;
    border: 1px solid #444444;
    width: 120px;
    color: #aaaaaa;
}

#sidebarTopics > li > a {
    color: #888888 !important;
}
    


.ui-state-active, 
.ui-widget-content .ui-state-active {
    border: 0px solid #ACDD4A;
    background-color: #556677;
    font-weight: normal;
    color: #FFF;
    outline: medium none;
    margin-left: 10px;
    width: 194px;
}

   
.ui-state-default,
.ui-state-active,
.ui-widget-content .ui-state-active {
    border: 1px solid #444444;
    font-weight: normal;
    color: #FFF;
    outline: medium none;
    width: 194px;
    margin-left: 10px;
}

.ui-state-default,
.ui-widget-content .ui-state-default, 
.ui-widget-header .ui-state-default {
    border: 1px solid #556677;
    background-color: #445566;
    font-weight: normal;
    color: #FFF;
    margin-left: 10px;
    width: 194px;
}
    
ul.menu {
    position: relative;
    float: right;
    left: -8px;
    margin: 0px;
}

.ui-widget-content {
    border: none;
    background: none;
    color: #666666;
}

form.search.small {
    text-align: left;
    margin-bottom: -20px;
    white-space: nowrap;
}

}
