css:
@-moz-document domain("instagram.com") {

.timelineCenter {
    max-width: 640px !important;
    margin-left: 160px !important;
}
.timelineSidebar {
    width: 160px !important;
}
.DesktopPPage .rcWrapper {
    max-width: 1071px !important;
}
.MediaModal.igDialogContent {
    max-width: 1071px !important;
}
.timelineBookmarkInfo {
    margin-left: -60px !important;
}
.avatar-action, .avatar-container .followButtonActions {
    width: 128px !important;
}
.avatar-profile, .user-avatar, .avatar-profile img, .user-avatar img {
    width: 128px !important;
    height: 128px !important;
}
.profile-user h1 {
    padding-left: 158px !important;
}
.profile-user .user-bio {
    padding-left: 158px !important;
}
.avatar-action .button, .avatar-container .followButtonActions .button {
    width: 126px !important;
}
.compNoComp .compInnerWrapper {
    padding-bottom: 106px !important;
}
.timelineBookmarkLocation {
      width: 150%;
      text-align: right !important;
      max-width: 150% !important;
      overflow: visible !important;
      white-space: normal !important;
}
.mmPaginationWrapper {
    max-width: 1167px !important;
}
.FollowButtonContainer {
    width: 128px !important;
}
.upuiBio {
    padding-left: 158px !important;
    text-align: left !important;
    width: 480px !important;
}
.upuiBio strong, .upuiBio a {
    display: table-cell !important;
    margin-bottom: -1px !important;
    clear: both !important;
}
.upuiUsername {
    padding-left: 158px !important;
}
.dropdown ul.dropdownOptionList, .dropdown ul {
    display: inline-block !important;
}

.dOpen.Dropdown {
  margin: 20px 0px !important;
}
}
