@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* --- Appmenu button --- */
#appmenu-button {
  padding-left: 5px !important;
  padding-bottom:3px !important;
  background: none !important;
  border-color: transparent !important;
  border: 0px !important;
  box-shadow: none !important}
#appmenu-button > .box-inherit.button-box > .box-inherit > .button-icon {
  margin-top: 2px !important;
  list-style-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAAIElEQVQ4jWNgoBTYO7n+pwRTbsDAg9EwGA0DBobhEAYAojXigV3giFkAAAAASUVORK5CYII=) !important}
#appmenu-button > .box-inherit.button-box > .button-menu-dropmarker {
  display: none !important}

/* --- About:Addons Show NavBar --- */
#main-window[disablechrome] #navigator-toolbox[tabsontop="true"] > #nav-bar { visibility:visible !important;}

/* --- Square Corner Tabs --- */
.tabbrowser-tab,
.tabs-newtab-button {
    -moz-border-image: url("data:image/png;base64,") 4 3 0 fill repeat stretch !important;} 

/* --- Tab stuff --- */
.tabbrowser-tab:not([selected="true"]) {
    color: rgba(0, 0, 0, .8) !important;
    background-image: 
    -moz-linear-gradient(bottom, rgba(26, 26, 26, .4) 1px, transparent 1px),
    -moz-linear-gradient(top, rgba(184, 194, 208, .5), rgba(184, 194, 208, .5)) !important}
.tabbrowser-tab:not([selected="true"]):hover {
    color: black !important;
    text-shadow: none !important;
    background-image: 
    -moz-linear-gradient(bottom, rgba(26, 26, 26, .4) 1px, transparent 1px),
    -moz-linear-gradient(top,rgba(255,255,255,.45),rgba(255,255,255,.45)) !important}
.tabbrowser-tab[pinned][titlechanged]:not([selected="true"]) {
    background-image: -moz-radial-gradient(center 3px, circle cover, rgba(255,255,255,1) 3%, rgba(186,221,251,.75) 40%, rgba(127,179,255,.5) 80%, rgba(127,179,255,.25)) !important}
.tabbrowser-tab[pinned][titlechanged]:not([selected="true"]):hover {
    background-image: -moz-linear-gradient(hsla(0,0%,100%,.4), hsla(0,0%,75%,.4) 80%),
                    -moz-radial-gradient(center 3px, circle cover, rgba(255,255,255,1) 3%, rgba(186,221,251,.75) 40%, rgba(127,179,255,.5) 80%, rgba(127,179,255,.25)) !important}
.tabbrowser-tab[selected="true"] {
    background-image: -moz-linear-gradient(white, white) !important}
/* --- Move Tabs --- */
.tabbrowser-tab{
    margin-right:-1px !important;
    margin-bottom:-1px !important;
    padding-bottom:5px !important;
    text-shadow: none !important;}
#tabbrowser-tabs{
    margin-left:-4px !important;}
/* --- New tab button --- */
.tabs-newtab-button,
#new-tab-button {
    background: none !important;
    border: none !important}
.tabs-newtab-button > .toolbarbutton-icon,#new-tab-button > .toolbarbutton-icon  {
    padding-top: 2px !important;
    opacity: .75 !important;
    -moz-transition: visibility .25s, opacity .25s ease-out !important}
.tabs-newtab-button:hover > .toolbarbutton-icon, #new-tab-button:hover > .toolbarbutton-icon {
    opacity: 1 !important}
/* --- Dim close button --- */
.tab-close-button {
    opacity: 0 !important;
    -moz-transition: visibility .25s, opacity .25s ease-out !important;
    -moz-transition-delay: .25s!important}
.tabbrowser-tab:hover .tab-close-button {
    opacity: 1 !important;
    -moz-transition: visibility .25s, opacity .25s ease-out !important}

/* --- Navigation stuff --- */
#nav-bar {
    border-radius: 0 !important;
    padding: 3px 5px 3px 3px !important;
    background-color: white !important}
#urlbar {
    border-radius: 0px !important;
    border: rgb(200, 200, 200) solid 1px !important;}

/* --- IdentityBox --- */
#identity-box:not(.verifiedIdentity):not(.verifiedDomain){
  border-right: 1px solid rgba(0,0,0,.18) !important;
  margin-right: 4px !important;
  background: none !important;}

/* --- Searchbox --- */
#main-window .searchbar-textbox {
    border-radius : 0 !important;
    height: 24px !important;}
/* --- Search Engine Icon --- */
.searchbar-engine-button .searchbar-engine-image {visibility:hidden !Important;}
.searchbar-engine-button{
    background:url("data:image/png;base64,") !important;
    background-repeat: no-repeat !important;
    background-position:50% 50% !important;}
/* --- Remove useless stuff on url bar and search bar --- */
.ac-type-icon/*, #star-button*/,.autocomplete-history-dropmarker,#feed-button,.search-go-button,.search-go-button-stack,#go-button,.search-go-button ,.searchbar-dropmarker-image{
     display: none !important}

/* --- TOOLBAR BUTTON dropmarker hide ---*/
toolbarbutton:not([class*="tabs-alltabs-button"]) dropmarker {display: none !important;}
/* --- GO & RELOAD & STOP BUTTON ---*/
#urlbar {
    padding-right:0px !important;}
#urlbar-go-button, #urlbar-reload-button, #urlbar-stop-button{
    margin-left:2px !important;
    border-left:1px solid #c8c8c8 !important;
    padding-left:3px !important;
    padding-right:3px !important;}
#urlbar-go-button:hover, #urlbar-reload-button:hover, #urlbar-stop-button:hover{
    background-image: -moz-linear-gradient(#ECF1FD,#E5EAF6,#D5D9E6) !important;}
#urlbar-reload-button{
	list-style-image: url("data:image/png;base64,")!important;
	-moz-image-region: rect(0, 18px, 16px, 0)!important;}
#urlbar-stop-button{
	list-style-image: url("data:image/png;base64,")!important;
	-moz-image-region: rect(0, 36px, 16px, 18px)!important;}
#urlbar-go-button{
	list-style-image: url("data:image/png;base64,")!important;
	-moz-image-region: rect(0, 53px, 16px, 35px)!important;}

/* --- Downloads Button --- */
#downloads-button{
    -moz-appearance: none !important;
    border: 1px solid  rgba(165,175,185,0.8)!important;
    min-height: 24px !important;
    max-height: 24px !important;
    min-width: 25px !important;
    max-width: 25px !important;
    margin-bottom: 2px !important;
    margin-top: 2px !important;
    border-radius: 0px !important;
    border-left:0 !important;
    margin-left: -3px !important;    
    margin-right:4px !important;}

/* --- backward & forward button --- */
#back-button,#forward-button {
    -moz-appearance: none !important;
    border: 1px solid  rgba(165,175,185,0.8)!important;
    min-height: 24px !important;
    max-height: 24px !important;
    min-width: 26px !important;
    max-width: 26px !important;
    margin-bottom: 2px !important;
    margin-top: 2px !important}
#back-button {
    border-radius: 0px; 
    border-top-right-radius: 0px !important;
    border-bottom-right-radius: 0px !important;
    margin-left: 3px !important;    
    margin-right: -4px !important}
#forward-button {
    border-radius: 0px; 
    background-color: white !important;
    margin-left: 3px !important;
    margin-right: -4px !important}
#downloads-button:hover,#back-button:hover,#forward-button:hover {
    background-image: -moz-linear-gradient(#ECF1FD,#E5EAF6,#D5D9E6) !important;}
#forward-button[disabled="true"] {
    display: none !important}
#back-button[disabled="true"]:hover {
    background:white !important;}
#back-button{
	list-style-image: url("data:image/png;base64,")!important;
	-moz-image-region: rect(1px, 18px, 17px, 0)!important;}
#forward-button{
	list-style-image: url("data:image/png;base64,")!important;
	-moz-image-region: rect(1px, 36px, 17px, 18px)!important;}

/* --- TOOLBAR BUTTON change Icon ---*/
/* -- Bookmarks ICON -- */
#bookmarks-menu-button{
	list-style-image: url("data:image/png;base64,")!important;
	-moz-image-region: rect(0, 16px, 16px, 0)!important;}
/* -- Star Button Icon --  */
#star-button {
	list-style-image: url(data:image/png;base64,) !important;
	-moz-image-region: rect(0, 15px, 16px, 0) !important;
	margin-bottom: 1px !important;}
#star-button:not([starred]):hover {
	-moz-image-region: rect(0, 31px, 16px, 16px) !important;}
#star-button[starred] {
	-moz-image-region: rect(0, 47px, 16px, 32px) !important;}
/* -- GreaseMonkey ICON -- */
#greasemonkey-tbb toolbarbutton{
	list-style-image:url("data:image/png;base64,") !important;
}
/* -- Stylish ICON -- */
#stylish-toolbar-button{
	list-style-image:url("data:image/png;base64,")!important;
}
/* --- Bookmark bar --- */
#PersonalToolbar {
    margin-top:-3px !important;
    padding: 0px 3px 1px 3px !important;
    background-color: white !important;}

