.bookmark-item[container]:not([query="true"]){
list-style-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAAcklEQVQ4je2TwQkDQQwDt6ZU64d3exBWA1dAgtl6BJtX8snhyyW/EME8PSCQm5ltAETyBQAys61VcXdFxK4gIuTuKgVrrcsRpaCqUPGsB0CZqTnnKTJTANRInj5+QPIv+BHB10MaY1w/nXLv/Vb+yTu5A8y404asiprhAAAAAElFTkSuQmCC) !important;
-moz-image-region: rect(0 16px 16px 0) !important;
}
