css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("koding.com") {

    .kdview, aside, h3, body, SELECT{ /*main background color*/
        background-color:#303030 !important;
    }
    
    .kdcontenteditableview > div:nth-child(1) { /*What's new?*/
        background-color: #8A8A8A !important;
    }
    
    .avatar-area h3, .avatar-area > section:nth-child(2) > h3:nth-child(2), .avatar-area, .groups > span:nth-child(2), a.notifications > span:nth-child(2), a.help > span:nth-child(2), DIV.kdview.account-menu, DIV.account-area, #fatih-launcher > span:nth-child(1){ /* Topbar extras */
        background-color:#33383A !important;
    }
    
    .activity-item, .comment-header, time.kdview, a.action-link, SPAN.kdview.logged-in.action-container{ /*status updates */
        background-color:#545454 !important;
    }
    
    DIV#main-nav.kdview.kdlistview.kdlistview-navigation, a span.icon, DIV.appmeta.clearfix, DIV.bottom{ /*topbar*/
        background-color:black !important;
    }
    
    TEXTAREA.kdinput.text.autogrow.hitenterview.active {
        background-color:#545454;
    }
    
    textarea  {
        color:white !important;
    }
    
    aside {
        border-left: 0px !important;
    }
    
    DIV.kdview.kdlistitemview.kdlistitemview-comment, DIV.comment-contents.clearfix, DIV.kdview.kdlistitemview.kdlistitemview-comment a.action-link  { /*comments*/
        background-color: #3D3D3D !important;
    }
    
    p, span {
        color:white;
    }
    
    div.appmeta h3 {
        color:white !important;
    }
    
}
