css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://trello.com/b/") {
    
   /*change the background color of the body with the following two styles*/
        .body-board-background {
            background-color: #312347 !important;
        }

        #content {
            background-color: #312347 !important;
            padding-left: 20px !important;
        }

        /*-----------------------------*/

        /*to display number of cards in a list*/
        .list-header-num-cards {
            display: inline-block !important;
            float: right;
        }
        /*-----------------------------*/

        /*Card title styles. This is what you see in a List before you open a card.*/
        .list-card-title {
            margin: 0;
            overflow: hidden;
            padding: 0 18px 0 0;
            text-decoration: none;
            word-wrap: break-word;
        }

        .list-card-details {
            border-bottom: 1px dotted #C5C2C2 !important;
            font-size: 12px !important;
            margin-bottom: 6px !important;
            padding: 1px 4px !important;
        }


        .list-card {
            background-color: rgba(0, 0, 0, 0) !important;
            border-bottom: medium none !important;
            border-radius: 3px!important;
            margin-bottom: 0 !important;
            max-width: 300px;
        }

        .list {
            background: none repeat scroll 0 0 #FEFEFE !important;
            border-radius: 1px !important;
            margin: 0 5px !important;
            padding: 0 !important;
        }

        .list-header {
            background-color: #DADADA !important;
            border-bottom: 2px solid #999797 !important;
            min-height: 19px !important;
            padding: 6px 26px 2px 6px !important;
        }
        /*-----------------------------*/

        /*The modal window you see when you click on a card*/
        .window-wrapper {
            background: none repeat scroll 0 0 #F9F9F9 !important;
            border-radius: 1px;
        }
        /*-----------------------------*/

        /*Making the buttons sleekier*/
        .button-link {
            font-size: 12px !important;
            border-radius: 1px !important;
            border: 1px solid #BFBFBF !important;
        }

            .button-link:hover {
                background: none repeat scroll 0 0 #176FCC !important;
                border-color: #1162B7 !important;
                background-image: none !important;
            }

        input.primary[type="submit"], input.primary[type="button"], button.primary, .button.primary {
            background: none repeat scroll 0 0 #009903;
            border-radius: 1px;
            color: #FFFFFF;
            font-weight: normal;
        }


            input.primary[type="submit"]:hover, input.primary[type="button"]:hover, button.primary:hover, .button.primary:hover, input.primary[type="submit"]:focus, input.primary[type="button"]:focus, button.primary:focus {
                background: none repeat scroll 0 0 #048507;
            }

        #header-search .header-btn {
            border-radius: 0 1px 1px 0 !important;
        }

        #header-search input[type="text"] {
            border-radius: 1px 0 0 1px !important;
        }

        /*-----------------------------*/

        /*search box. making it sleek*/
        textarea, input:not([type="file"]), button {
            border-radius: 1px !important;
        }
        /*-----------------------------*/

        /*The "Add a card.." feature at the bottom of a list.*/
        .open-card-composer:hover {
            background: none repeat scroll 0 0 #F9E377;
            border-top: 2px solid #E8CF53;
            color: #222222;
            margin: 0;
            text-decoration: none;
        }
        /*-----------------------------*/

        /*These styles are specific to the Board List sidebar. Works better when it's pinned open*/

        /*Hiding recent board. Doesn't make much sense if you have only a few boards. Disable if you prefer.*/
        .js-recent-boards {
            display: none !important;
        }

        /*Hiding board search.*/
        .js-search-boards {
            display: none !important;
        }

        /*Hiding the label "Board"! Enable if you prefer.*/
        .sidebar-section-header {
            display: none !important;
        }

        .js-show-important {
            display: none !important;
        }

        /*Sets the BG color and width of the Boards sidebar.*/
        .boards-drawer-pinned {
            background: none repeat scroll 0 0 #312347 !important;
            width: 150px !important;
            padding-top: 85px;
        }

        /*The colored square label you see to the left of the board title in Board List. I just made it very thin to look minimal.*/
        .sidebar-boards-list .thumbnail {
            background-size: cover;
            border-radius: 0 !important;
            display: block;
            height: 36px;
            left: 0;
            opacity: 0.7;
            position: absolute;
            top: 0;
            width: 6px !important;
            z-index: 2;
        }

        /*The title of the board in board list*/
        .sidebar-boards-list .details {
            margin-left: 8px !important;
            padding: 9px 10px;
            z-index: 2;
        }

        .boards-drawer-pinned .boards-drawer-header {
            display: none !important;
        }

        /*-----------------------------*/

        /*This section holds all your List contents. Set the width to the Board list width to have continuity.*/
        #surface {
            margin-left: 150px !important;
        }

        #header {
            background-color: #250F3E !important;
        }

        .header-btn {
            border-radius: 0;
            font-weight: normal;
            height: 29px;
        }

            /*Hiding the Profile Label/Pic in the header. Remove this if you want the deature.*/
            .header-btn .member {
                display: none !important;
            }   
}
