css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://soundcloud.com"), url-prefix("https://soundcloud.com") {

/*** GENERAL ***/

html, .social-sign-in-separator span {
    background: #000 !important}
body, .site, .dashbox, .listenContent__inner, .sound__soundActions, .l-footer, .activity__sharingNote-container, .commentForm, .commentForm__inputWrapper, .createPlaylist:hover, .createPlaylist.focused, .uploadStatus, .activeUpload__section, .activeUpload__quietTeaser, .activeUpload__blockTable.threeCell {
    background: transparent !important}
.commentForm, .commentForm__inputWrapper, .sc-button-group, .sound__footer.sc-border-light-bottom, .header__soundInput {
    border: none !important}
.dashbox, .listenDetails__login, article.whoToFollowModule {
    display: none !important}
#flashAudioObject-container {
    visibility: hidden !important}

body, .sc-text, .resultCounts, .g-nav-link {
    color: #BBB !important}
.sc-link-verylight, .sc-text-verylight {
    color: #444 !important}
.sc-link-light:hover, .g-nav-item.active > .g-nav-link {
    color: #FFF !important}
.sc-link-dark, .dropdownList__loading, .sound__uploadTime {
    color: #666 !important}
.sc-link-dark:hover, .sc-link-verylight:hover, .trackPreviewItem__titleLink:hover, .g-tabs-item:not(.active) .g-tabs-link:hover, .g-link-problem:hover, .activeUpload__sectionTitle, .blockCheckbox__title {
    color: #DDD !important}
.userBadge.large .userBadge__title, .userBadge.large .userBadge__usernameLink {
    color: #06C !important}
.active .soundTitle__title, .userBadge.large .userBadge__usernameLink:hover, .g-nav-link:hover {
    color: #F60 !important}

#app h1:not(.sc-type-h2), h1.widgetCustomizationContent__title {
    color: #06C !important;
    text-shadow: 0 1px 0 #38F;
    position: relative}
#app h1:not(.sc-type-h2):after, h1.widgetCustomizationContent__title:after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    background: linear-gradient(rgba(0,0,0,0) 40%, rgba(0,0,0,.8))}
#app h1.userNetworkTop__title:after, #app h1.groupNetworkTop__title:after, #app h1.listenNetworkTop__title:after {background: none}
#app h1.userNetworkTop__title, #app h1.groupNetworkTop__title, #app h1.listenNetworkTop__title {-webkit-mask-image: linear-gradient(#000 40%, rgba(0,0,0,.2)) !important}
#app h1.stream__noticeTitle:after {background: linear-gradient(rgba(17,17,17,0) 40%, #111)}
h1.widgetCustomizationContent__title:after {background: linear-gradient(rgba(21,21,21,0) 25%, #151515)}

h1 .soundTitle__title {
    text-shadow: 0 0 10px #19F !important;
    font-family: "Champagne & Limousines","eurofurence light","Interstate","Lucida Grande","Lucida Sans Unicode","Lucida Sans",Garuda,Verdana,Tahoma,sans-serif !important}
.waveform__emptyMessage, .waveform__playHint {
    color: #EEE !important;
    text-shadow: 0 0 5px !important}

a {text-shadow: 0 0 #000; transition: color, text-shadow, 0.2s}
.sc-link-dark:hover, .sc-link-light:hover, .trackPreviewItem__titleLink:hover {text-shadow: 0 0 7px}

::-moz-selection {background-color: rgba(35,35,35,.6) !important; color: #F60 !important}
:focus {outline: none !important}

.sc-border-light-top, .sc-border-dark-top, .sc-border-light-bottom, .sc-border-dark-bottom, .sc-border-light-right, .sidebarHeader, .commentsThread__item, .ownActivity__user, .userInfo, .userInfo__container,
.archive-title, .page-title, .widget-title, .entry-content th, .comment-content th, .widget-area .widget {
    border-color: #111 !important}
.g-tabs, .g-tabs-item.active, .site-content article {
    border-color: #333 !important}
.activeUpload__sectionHead {
    border-color: #222 !important;
    box-shadow: 0 1px #000 !important}


/*** BUTTONS & CO ***/

.sc-button:not(.sc-button-play), .sc-buylink {
    -moz-appearance: none !important;
    -webkit-appearance: none !important;
    background: #111 !important;
    border: 1px solid #292929 !important;
    box-shadow: inset 0 0 2px #000 !important;
    color: #777 !important;
    text-shadow: 0 1px #000 !important;
    border-radius: 0 !important;
    transition: color, border-color, background-color, ease 0.3s !important}
.sc-button.sc-button-selected, .sc-button.uploadButton.sc-button-selected {border-color: #0066CC !important}
.sc-buylink, .sc-button-small.sc-button-text {padding: 2px 7px !important}
.sc-buylink:before {margin-left: 5px !important}
.sc-buylink:before, .sc-button-medium:before {opacity: 0.3}

.sc-button:hover, .sc-buylink:hover {
    border-color: #444 !important;
    color: #999 !important}
.sc-button:active, .sc-buylink:active {
    background-color: #333 !important;
    transition-duration: 0.1s !important}
.sc-button-play {transition: ease 0.3s !important}
.sc-button-play:hover {
    border-color: #F60 !important;
    box-shadow: 0 0 7px #F60 !important}
.soundTitle.single .soundTitle__playButton .sc-button-large {margin-top: 6px !important}

input, input[type="checkbox"], input[type="radio"], textarea {
    -moz-appearance: none !important;
    -webkit-appearance: none !important;
    background: #222 !important;
    border: 1px solid #292929 !important;
    box-shadow: inset 0 0 2px #000 !important;
    color: #777 !important;
    text-shadow: 0 1px #000 !important;
    border-radius: 0 !important;
    transition: color, border-color, background-color, ease 0.3s !important}
input[type="checkbox"]:active:hover, input[type="radio"]:active:hover {
    background: #222 !important;
    border: 1px solid #292929 !important;
    color: #F70 !important}
.tokenInput__input {border: none !important}

input:hover, input:focus, textarea:hover, textarea:focus {background: #333 !important}
input:focus, textarea:focus {color: #AAA !important}

.sc-button.uploadButton {
    background: transparent !important;
    border-color: #1C1C1C !important;
    border-radius: 5px !important;
    box-shadow: 0 1px 0 #3E3E3E, inset 0 1px 0 #464646 !important;
    padding-top: 1px !important;
    height: 25px !important}
.sc-button.uploadButton:hover {box-shadow: 0 1px 0 #3E3E3E, inset 0 0 3px #090909 !important}

.radioGroup__active, .activeUpload__licenseSummary {background-color: #222 !important}

a.shuffleButton {
    background-position: 0 0 !important;
    color: #DDD !important;
    opacity: 0.3}
a.shuffleButton:hover {opacity: 0.6 !important}
a.shuffleButton.active {opacity: 1}


/*** DIALOG & CO ***/

.dialog, .imageLink.zoom, .modal__modal, #ghostery-purple-bubble {
    background: #151515 !important;
    border: 1px solid #333 !important;
    border-radius: 0 !important;
    box-shadow: 0 0 10px #000, 0 0 10px #000 !important}
.dialog__arrow {
    background: #151515 !important;
    border-color: #3C3C3C !important}
#ghostery-purple-bubble * {background: transparent !important}

.g-selectable {
    background: linear-gradient(#191919, #141414) !important;
    color: #999 !important}
.g-selectable:hover {background: linear-gradient(#222, #1B1B1B) !important}
.g-selectable.g-selected {
    background: transparent !important;
    color: #555 !important}

.dropdownList {
    background: #151515 !important;
    border-color: #333 !important}
.dropdownMenu > div {
    background: #1C1C1C !important;
    border-color: #292929 !important;
    box-shadow: 0 1px 1px #000 !important;
    text-shadow: 0 1px #000 !important}
.dropdownMenu > li:hover, .dropdownMenu li.selected {background-color: rgba(35,35,35,.6) !important; color: #F60 !important}
#suggestionsList li:hover * {color: #F60 !important}
#suggestionsList li > * > * > * > * {color: #555 !important}
.dropdownMenu li.separator {border-bottom: 1px solid #292929 !important}
.dropdownMenu ul.sc-list-nostyle, .dropdownMenu li {background: transparent !important; border: none !important}

.gritter-item-wrapper {
    background: linear-gradient(#282828, #1C1C1C) !important;
    color: #CCC !important;
    text-shadow: 0 1px #000 !important;
    border-color: #333 !important;
    box-shadow: 0 0 10px #000, 0 0 10px #000, inset 0 0 3px #111 !important}

.modal {background: repeating-linear-gradient(-45deg, rgba(11,11,11,.8), rgba(11,11,11,.8) 60px, rgba(16,16,16,.8) 60px, rgba(16,16,16,.8) 120px) !important}


/*** CONTENT ***/

header.header .header__inner {box-shadow: 0 4px 8px #000 !important; height: 46px !important}

.g-tabs-item.active .g-tabs-link, .sc-buylink:before, .sc-button-medium:before {
    filter: url(data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciPjxmaWx0ZXIgaWQ9IjEiIGNvbG9yLWludGVycG9sYXRpb24tZmlsdGVycz0ic1JHQiI+PGZlQ29sb3JNYXRyaXggdHlwZT0ibWF0cml4IiB2YWx1ZXM9Ii0xIDAgMCAwIDEgMCAtMSAwIDAgMSAwIDAgLTEgMCAxIDAgMCAwIDEgMCIvPjwvZmlsdGVyPjwvc3ZnPg==#1) !important;
    transform: translate3d(0,0,0) !important;
    -webkit-filter: invert(100%) !important;
    -webkit-transform: translate3d(0,0,0) !important}

h1.header__logo {background: transparent !important}
h1.header__logo > a {border: none !important}

.waveform__scene > canvas.waveform__layer:nth-child(1) {opacity: 0.6 !important}
.miniPlaylistIndicator__waveform canvas.waveform__layer {opacity: 0.5 !important}
.waveform, .sc-button-play:not(:hover), a:not(:hover) .image, a.soundBadge__avatarLink:not(:hover), .sc-media-image:not(:hover) img {
    filter: url(data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciPjxmaWx0ZXIgaWQ9IjEiPjxmZUNvbG9yTWF0cml4IHR5cGU9Im1hdHJpeCIgdmFsdWVzPSIwLjMzMzMgMC4zMzMzIDAuMzMzMyAwIDAgMC4zMzMzIDAuMzMzMyAwLjMzMzMgMCAwIDAuMzMzMyAwLjMzMzMgMC4zMzMzIDAgMCAwIDAgMCAxIDAiLz48L2ZpbHRlcj48L3N2Zz4=#1) !important;
    transform: translate3d(0,0,0) !important;
    -webkit-filter: grayscale(100%) !important;
    -webkit-transform: translate3d(0,0,0) !important;
    -webkit-transition: -webkit-filter 0.6s !important}

.userNetworkTop__inner, .listenNetworkTop__inner, .groupNetworkTop__inner, .search__headerInner {
    background: linear-gradient(#000, rgba(0,0,0,.95), rgba(0,0,0,.82)) !important;
    box-shadow: 0 6px 8px #000 !important}

.peopleHeader__tab, .sc-ministats-group .sc-ministats, .tagInput .tokenInput__tokenRemove, .tagInput__genreRemove, .tagInput__addTag {
    border-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAFCAQAAAAujU4UAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAVSURBVAjXY/jPwBC6iiH0P4j8zwAAKDwFUcVg7GcAAAAASUVORK5CYII=) 0 1 0 0 !important}
.g-tabs-link {
    border: none !important}
.g-tabs-item.active {
    background: #000 !important;
    border-bottom-color: #000 !important;
    margin-top: 1px !important}
.g-tabs-item.active .g-tabs-link {
    color: #222 !important;
    text-shadow: 0 0 7px #000 !important}
.g-tabs:before {
    background: linear-gradient(to left, #333, #000);
    width: 20px;
    height: 1px;
    left: 0;
    bottom: 0;
    position: absolute}
.groupNetworkTop__inner {padding-bottom: 0 !important; height: 96px !important;}
.groupNetworkTop__inner .g-tabs:before {left: -20px}
/* .userNetworkTop__inner {padding-right: 0px !important;} */

/* .sidebarHeader {border-bottom: 1px solid #111 !important} */

.tabs li.current a {
    background: #000 !important;
    color: #DDD !important;
    box-shadow: inset 0 6px 4px -6px #999, inset 6px 0 4px -6px #777, inset -6px 0 4px -6px #777 !important}

.soundBadge {
    background: transparent !important;
    border-color: transparent !important;
    border-radius: 0 !important}
.soundBadge__additional {background: linear-gradient(to right, rgba(0,0,0,.1), #000 17px) !important}
.soundBadge:not(.active):not(.compact):hover .soundBadge__additional {background: linear-gradient(to right, rgba(11,11,11,.1), #0B0B0B 17px) !important}
.soundBadge.active .soundBadge__additional {background: none !important}
.soundBadge.active, .carousel.active, .carousel.hover, .uploadMain__chooserContainer, .activeUpload__form {
    background: repeating-linear-gradient(-45deg, #151515, #151515 20px, #111 20px, #111 40px) !important;
    border-color: #151515 !important;
    box-shadow: inset 0 0 20px #000 !important}
.soundBadge.active.compact {margin-right: -6px !important}
.uploadStatus.complete .uploadStatus__progress {border-color: #151515 !important}
.soundBadge:not(.active):not(.compact):hover, .trackList__item.sc-pointer:hover {
    background: #0B0B0B !important;
    box-shadow: inset 0 0 20px #000 !important}
.listenContent__parentLink, .sortable-placeholder, .announcement, .stream__notice, .commentFormDisabled {
    background: #111 !important;
    border: 2px dashed #333 !important;
    box-shadow: inset 0 0 20px #000 !important}
.listenContent__parentLink, .announcement {border-top: none !important}
.commentFormDisabled {border-width: 1px !important}

.sc-tag {
    background: #333 !important;
    border-color: #393939 !important;
    box-shadow: inset -1px 1px rgba(0,0,0,.15) !important}
.sc-tag-selected {
    background: #DC3F00 !important;
    border-color: #DC3F00 !important}
.sc-tag:before {border-color: transparent #333 transparent !important}
.sc-tag-selected:before {border-color: transparent #DC3F00 transparent !important}
.tagInput__inputWrapper:before {
    background: none !important;
    width: 0 !important;
    height: 0 !important;
    border: 11px solid !important;
    border-left: none !important;
    border-color: transparent #333 transparent !important}
.sc-tag:after {
    background: #000 !important;
    border-color: #393939 !important;
    box-shadow: 0 0 1px rgba(0,0,0,.5) !important}
.sc-tag-selected:after {border-color: #DC3F00 !important}
.sc-tag:hover {opacity: 0.7 !important}

.sc-label-private, .groupInfo__status.sc-background-orange {
    background: repeating-linear-gradient(-45deg, #D50, #D50 6px, #B40 6px, #B40 12px) !important;
    border: 1px solid transparent !important;
    border-radius: 4px !important;
    box-shadow: inset -1px 1px rgba(0,0,0,.15) !important}

.trackPreview__post {background: #000 !important}
.trackPreview__post:before {background: linear-gradient(to right, transparent, #000) !important}

.listenContent .relativeTime:before {content: attr(title) " (" !important}
.listenContent .relativeTime:after {content: " ago)" !important}

.progressBar__outer {background-color: #333 !important}
.uploadStatus__upload {border-color: #333 !important}
.tagInput {
    background: #222 !important;
    border-color: #292929 !important;
    border-radius: 0 !important;
    border-bottom: none !important;}

.ownActivity__trackImg {border-color: #111 !important}

.playlist > .sound__coverArt {background: none !important}
.playlist > .sound__coverArt .image, .playlist > .sound__coverArt .image {box-shadow: 0 0 1px rgba(255,255,255,.35) !important}
.playlist > .sound__coverArt .image:before, .playlist > .sound__coverArt .image:after {
    content: '';
    position: absolute;
    z-index: -1 !important;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    background: #222;
    box-shadow: inset 0 0 1px #666;
    transform: rotate(10deg);
    -webkit-transform: rotate(10deg);
    transition: transform ease 1s;
    -webkit-transition: -webkit-transform ease 1s}
.playlist > .sound__coverArt .image:after {
    background: #333;
    transform: rotate(-10deg);
    -webkit-transform: rotate(-10deg)}
.playlist > .sound__coverArt:hover .image:before {
    transform: rotate(-10deg);
    -webkit-transform: rotate(-10deg)}
.playlist > .sound__coverArt:hover .image:after {
    transform: rotate(10deg);
    -webkit-transform: rotate(10deg)}

.header__userMenu > li > a.selected:before {border-bottom-color: #050505 !important}

.social-sign-in-separator {background: #333 !important}


/*** IMAGES ***/

.loading, .loading.dark {background-image: url(http://abload.de/img/throbber-cloud-greysc11uhe.gif) !important; opacity: 0.8}
.image__hasPlaceholder {background: #222 !important}
.image__hasPlaceholder-200 {background: url(http://abload.de/img/cloudx200uvuof.png) center no-repeat transparent !important}
img[src*="cloudx50-1ec56ce9.png"], .sound__coverArt img[src*="cloudx120-1ec56ce9.png"] {width: 0 !important; height: 50px !important; padding-left: 50px !important; background: url(http://abload.de/img/cloud_50bvb7w.png) center no-repeat transparent !important}
.l-fixed-narrow-fluid .sound__coverArt img[src*="cloudx120-1ec56ce9.png"], .l-fixed-fluid .sound__coverArt img[src*="cloudx120-1ec56ce9.png"], img.imageUpload__preview[src*="cloudx120-1ec56ce9.png"] {width: 0 !important; height: 120px !important; padding-left: 120px !important; background: url(http://abload.de/img/cloud_120jwrp2.png) center no-repeat transparent !important}
img[src*="cloudx200-1ec56ce9.png"] {width: 0 !important; height: 200px !important; padding-left: 200px !important; background: url(http://abload.de/img/cloudx200uvuof.png) center no-repeat transparent !important}
img[src*="userx40-1ec56ce9.png"][width="40"] {width: 0 !important; height: 40px !important; padding-left: 40px !important; background: url(http://abload.de/img/user_400wyk0.png) !important}
img[src*="userx50-1ec56ce9.png"][width="50"] {width: 0 !important; height: 50px !important; padding-left: 50px !important; background: url(http://abload.de/img/user_50uml4w.png) !important}
img[src*="userx120-1ec56ce9.png"] {width: 0 !important; height: 120px !important; padding-left: 120px !important; background: url(http://abload.de/img/user_120c2yb5.png) !important}
img[src*="userx120-1ec56ce9.png"][width="100"] {width: 0 !important; height: 100px !important; padding-left: 100px !important; background: url(http://abload.de/img/user_1007up61.png) !important}
img[src*="userx200-1ec56ce9.png"] {width: 0 !important; height: 200px !important; padding-left: 200px !important; background: url(http://abload.de/img/user_200skzbp.png) !important}
.soundBadge.playlist .soundBadge__avatarLink {background-image: url(http://abload.de/img/playlist-cover-bg_smaabxf1.png) !important}

.paging-eof:before, .premiumBottomContent__quota:after, .premiumContentFooter:before {background-image: url(data:image/png;base64,) !important}

.g-back-link {background-image: url(data:image/png;base64,) !important}
.soundBadge__remove, .stream__hideNotice, .mobileApps__dismiss, .announcement__dismiss img, .modal__closeButton, .gritter-close {
    -moz-appearance: none !important;
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAPAQMAAAABGAcJAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAGUExURf///93d3RxnYCYAAAABdFJOUwBA5thmAAAAIklEQVQI12NggAEBAQYOBQYWBwamBgZGBhAJZANFgOIwAAAu6AIeipbA5wAAAABJRU5ErkJggg==) no-repeat center #000 !important;
    border: 1px solid #333 !important;
    border-radius: 20px !important;
    box-shadow: 0 0 2px #000 !important;
    opacity: 0.5 !important}
.announcement__dismiss img {width: 0 !important; padding-left: 15px !important; margin-top: -3px !important}
.gritter-close {height: 15px !important; top: 13px !important;}
.soundBadge__remove:hover, .stream__hideNotice:hover, .announcement__dismiss:hover img, .mobileApps__dismiss:hover, .modal__closeButton:hover, .gritter-close:hover {opacity: 1 !important}

.blockCheckbox__icon {background-image: url(http://abload.de/img/big-checkboxyayvg.png) !important}

.carouselSlider__item {background: none !important; background-color: #111 !important;}
}
