css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("koding.com") {

body {
background: #2e3537 !important;
}
#main-header, #main-header .inner-container {
  height:29px !important;
  -webkit-box-shadow: 0 0px 0 rgba(0,0,0,0.15) !important;
-moz-box-shadow: 0 0px 0 rgba(0,0,0,0.15) !important;
box-shadow: 0 0px 0 rgba(0,0,0,0.15) !important;
}
#koding-logo {
  width: 64px !important;
  height: 22px !important;
  padding-top: 8px !important;
  padding-left: 26px !important;
  background: none !important;
  text-decoration:none !important;
  font-size: 12px !important;
}
#koding-logo cite {
  display:none !important;
}
#koding-logo:after {
  content: 'Koding' !important;
  color:#1aaf5d !important;
  font-weight:bold !important;
}
#main-nav .icon {
  width:55px !important;
  height:29px !important;
}
#main-nav a {
  width:55px !important;
  height:29px !important;
  border-right: 0 !important;
}
#main-nav a:hover {
  -webkit-box-shadow: inset 0 2px 0 #fff !important;box-shadow: inset 0 2px 0 #fff !important;
-moz-box-shadow: inset 0 2px 0 #fff !important;
}  
#main-nav .running {
  width:33px;
}
#main-nav {
height: 29px;
}
#main-nav .activity .icon {
 background:url('data:image/png;base64,') no-repeat !important;
}

#main-nav .teamwork .icon {
 background:url('data:image/png;base64,') no-repeat !important;
}

#main-nav .terminal .icon {
 background:url('data:image/png;base64,') no-repeat !important;
}

#main-nav .editor .icon {
 background:url('data:image/png;base64,') no-repeat !important;
}

#main-nav .apps .icon {
 background:url('data:image/png;base64,') no-repeat !important;
}

#main-nav .kodepad .icon {
  background-size: 100% !important;
  background-position:0px -1316px !important;
}
#main-header #dock {
  height:30px !important;
  margin-left: 71px !important;
}
#main-header .account-area {
  height: 33px !important;
}
#main-header .account-area .acc-dropdown-icon.help .icon {
  margin: 1px 0 0 8px !important;
background:url('data:image/png;base64,') no-repeat !important;
  width:39px !important;
}
#main-header .account-area .notifications .icon {
  margin: 6px 0 0 10px !important;
  width:42px !important;
  background-size: 100% !important;
  background-position:0px -308px !important;
  height:17px !important;
}
#main-header .account-area .acc-dropdown-icon .count {
  top:5px !important;
  right:6px !important;
  font-size: 8px !important;
  line-height: 13px !important;
  width: 12px !important;
  height: 12px !important;
}
#main-header .account-area .acc-dropdown-icon {
  width: 38px !important;
  height: 30px !important;
}

.avatararea-popup .tab {
  top: -33px !important;
  width: 30px !important;
  height: 33px !important;
}

.avatararea-popup {
  top:30px !important;
}
.avatararea-popup.group-switcher .content {
  margin-top:0px !important;
}
.avatararea-popup.notifications {
  right: 277px !important;
}
  
.avatararea-popup.notifications .tab span {
  background:transparent !important;
}
.avatararea-popup .tab {
background:transparent !important;
}
.account-area .avatar-area {
  background-color: #2e3537 !important;
  padding: 5px 10px !important;
  height: 30px !important;
}
.account-area .avatar-area section {
  margin-top: -1px !important;
}
.account-area .avatar-area section h2 {
  font-size: 11px !important;     
}    
.account-area .avatar-area section h3 {
  display:none !important;
}
.account-area .avatar-area section h2 a {
  padding-top:5px !important;
}
.account-area .avatarview img {
  width:21px !important;
  height:21px !important;
}
.account-area .acc-dropdown-icon.groups .icon {
  margin: 13px 0 0 10px !important;
}
.avatararea-popup.group-switcher {
  right: 39px !important;
}
.account-area .acc-dropdown-icon.search .icon {
  margin: 7px 0 0 8px !important;
}
.account-area .search-form-container {
  height:30px !important;
}
.account-area .search-form-container .kdinput {
  height: 30px !important;
  font-size: 13px !important;
}
.account-area input.kdinput.text {
  padding: 0px 6px !important;
  line-height: 16px !important;
}
.content-page.activity {
  margin:55px auto !important;
}
.content-page.activity aside {
  top: 54px !important;  
  width: -webkit-calc(50% - 290px);
  width: calc(50% - 290px) !important;
  background:#1f2628 !important;
  border-left: 1px solid #222 !important;
  -webkit-border-top-left-radius: 5px !important;
  -moz-border-radius-topleft: 5px !important;
  border-top-left-radius: 5px !important;    
}
.content-page.activity aside .referal-box {
  margin-top: 16px !important;
}
.content-page.activity aside .referal-box .progressbar-container {
  background:#101314 !important; 
  height: 16px !important;
}
.content-page.activity aside .referal-box .progressbar-container span {
  line-height: 16px !important;
  font-size: 11px !important;
}
.content-page.activity main {
  width: auto !important;
  min-width: 750px !important;
  margin-right: -webkit-calc(50% - 265px);
  margin-right: calc(50% - 265px) !important;
} 
.content-page.activity main .activity-content {
  margin: 0 0 0 25px !important;
}
.content-page.activity main .activity-input-widget {
  margin-left: 25px !important;
}
.content-page.activity main .activity-input-widget .input-view {
  border: none !important;     
}
.activity-input-widget .input-view div {
  background:#24292b !important;
  color:#aaa !important;
}     
.activity-input-widget .input-view div:focus {
  outline: none !important;
}
.activity-input-widget .kdbutton.solid.green {
  background:#3c4043 !important;
}
.content-page.activity main .feed-type-selection a {
  font-size:12px !important;
}
.content-page.activity main .new-updates {
  font-size:12px !important;
}
.content-page.activity main .kdbutton.live-updates {
  border-right: 1px solid #222 !important;
}
.activity-item .author-avatar, .activity-item .author-avatar img {
  width: 30px !important;
  height: 30px !important;
  background-size: 30px !important;
}
.activity-item .author-avatar {
  top: 7px !important;
  right: inherit !important;
  left: 7px !important;
}
.activity-item {
 border: 1px solid #1f2628 !important;
 -webkit-box-shadow: none !important;box-shadow: none !important;
 -moz-box-shadow: none !important;
  background: #1e2223 !important; 
}
.activity-item article {
  line-height: 18px !important;
  color:#bbb !important;
}
.activity-item footer time {
  font-size: 10px !important;
  color: #444 !important;
}
.activity-item .comment-container.commented {
  border-top: 1px solid #111 !important;
}
.activity-item .comment-container {
  background:#272b2c !important;
}
.activity-item .comment-container.commented .show-more-comments {
  border:0px !important;
}
.activity-item .profile {
  padding: 13px 43px 12px !important;
  font-size: 21px !important;
}
.comment-contents .profile {
  padding: 10px 0px 5px !important;
  font-size: 15px !important;
}
.activity-item article hr, .activity-input-widget .update-preview hr {
  background: #333 !important;
  }
.activity-item .kdlistitemview-comment .comment-contents {
  color:#aaa !important;
}
.activity-item .item-add-comment-box .item-add-comment-form textarea, .activity-item .edit-comment-box .item-add-comment-form textarea {
  background:#242728 !important;
  border:0px !important;
}
.show-more-comments.in {
  border:0px !important;
  background:#2b2f30 !important;
  color:#eee !important;
}
.show-more-comments.new {
  border:0px !important;
  background:#2eb398 !important;
  color:#eee !important;
}
.show-more-comments a {
 color:#eee !important;
}
.activity-item .kdlistitemview-comment~.kdlistitemview-comment {
  border-top: 1px solid #1f2628 !important;
}
.activity-item article p code, .activity-input-widget .update-preview p code {
  border:0px !important;
  background-color: #111 !important;
}
.activity-actions a {
  color:#2d97de !important;
}
.activity-actions a:hover {
  color:#aaa !important;
  text-decoration:underline !important;
}
aside .right-block-box {
  background:transparent !important;
}
.kdscrollview {
  overflow: hidden;
}
.content-page.activity aside .kdlistitemview-activity-ticker-item, .content-page.activity aside .kdlistitemview-member {
  border-bottom:0px !important;
}
.content-page.activity > aside .right-block-box h3 {
border-bottom: 1px solid #3c4752 !important;
}
.content-page.activity > aside .kdlistitemview-activity-ticker-item .follow-btn:hover, .content-page.activity > aside .kdlistitemview-member .follow-btn:hover {
  background: transparent !important;
}
.application-page {
  padding: 33px 0 0 !important;
}
button.app-settings-menu {
  top:33px !important;
}
.appstore h1 {
    display:none !important;
}
.appstore .feeder-header > p, .topics .feeder-header > p {
  color:#fff !important;
}
.lazy-loader {
    color:#fff !important;
}
.embed-link-view .preview-text {
    background: transparent !important;
}
.header-notification.in {
top: 31px !important;
}
}
