css:
@-webkit-document domain(v.youku.com) {

div.videomsg,
div.yk-ucenter,
div.yk-upload,
div.youkuhtml5playerbookmark2-title,
span.options div im,
div.yk-box,
div.yk-footer-con,
div.yk-guide,
div.yk-notifications a,
div.yk-playrecords,
a.expandlink.hover,
a.feedBack,
div.dropdown.dropdown-open,
div.listArea,
div.listSkip,
.player_html5,
#vpofficiallistv5,
#qqshare img ,
#btn-gotop,
#qheader,
#DramaTab,
.qtips.qtips_tl,
#QR a,
.youkuhtml5playerbookmark2-btn,
#sideTool{
display: none;
}

/*视频内容区域*/
.play_area,#movie_player,#player,#playerBox{
width: 100%!important;
}
/*非视频区域*/
div.mainCol,div.items,.sideCol{
opacity:0.03;

-webkit-filter: blur(2px);
-moz-filter: blur(2px);
-o-filter: blur(2px);
-ms-filter: blur(2px);
filter: blur(2px);

-moz-transition:0.1s ease-out;
-webkit-transition:0.1s ease-out;
-o-transition:0.1s ease-out;
transition:0.1s ease-out;
}

div.mainCol:hover,div.items:hover,div.mainCol:focus,div.items:focus,.sideCol:hover,.sideCol:focus{
opacity:1;

-webkit-filter: blur(0px);
-moz-filter: blur(0px);
-o-filter: blur(0px);
-ms-filter: blur(0px);
filter: blur(0px);
}
/*------*/
}
