css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tumblr.com") {

:root {
    /* Default: #36465d; Style: #29053D */
	--backgroundColor: rgba(41, 5, 61, .65);/* #29053D; */
    --backgroundColorFullOpacity: rgba(41, 5, 61, .0);
    
    /* Style: url(http://img-fotki.yandex.ru/get/9322/110173670.0/0_9f6d6_1cf00830_orig) */
	--backgroundImage: url(http://img-fotki.yandex.ru/get/9322/110173670.0/0_9f6d6_1cf00830_orig);
    
    /* Default: #529ecc; Style: #8040A6 */
    --buttonColor: #8040A6;
}




/* MAIN HEADER WITH SEARCH PANEL */
div.l-header-container {
	
	background-color: var(--backgroundColor) !important;
}

/* MAIN WALL - ROUND CORNERS COLOR */
div.l-content {
	
	background-color: var(--backgroundColor) !important;
}

/* MAIN WALL - LEFT COLUMN (WALL) COLOR */
div#left_column {
	
	background-color: var(--backgroundColorFullOpacity) !important;
}

/* MAIN WALL - RIGHT COLUMN COLOR */
div#right_column {
	
	background-color: var(--backgroundColorFullOpacity) !important;
    top: 20px !important;
}

/* MAIN WALL - RIGHT COLUMN - THIS IS ALSO INTERESTING COLOR */
.rapid-recs-container .rapid-recs {
	background-image: linear-gradient(180deg, rgba(41, 5, 61, 0) 0, var(--backgroundColorFullOpacity) 15%, var(--backgroundColorFullOpacity) 85%, rgba(41, 5, 61, 0)) !important;
}

/* MAIN PAGE - LEFT COLUMN */
.post_avatar {

	background-color: var(--backgroundColorFullOpacity) !important;
}

/* MAIN PAGE - RIGHT COLUMN - HIDE FOLLOW LIST BLUE EFFECTS */
a.follow_list_item_blog:before {
	
	background-image: none !important;
	border-right: none !important;
}

/* MAIN PAGE - RIGHT COLUMN - HIDE RADAR BLUE EFFECTS */
a.radar_avatar:before {
	
	background-image: none !important;
	border-right: none !important;
}

/* MAIN WALL - RIGHT COLUMN - HIDE BOTTOM BLUE EFFECTS */
div#right_column.right_column:after {
	
	background-image: none !important;
}





/* COLOR FIXES FOR NOT MAIN PAGE */
