/* LOGIN AND REGISTER PAGE - LOGO */
h1#logo.logo-word.large a.logo_anchor {
    
    background-image: url('https://img-fotki.yandex.ru/get/3109/110173670.0/0_bbae3_68fb877c_orig') !important;
    background-size: 100% !important;
}





/* PLUS BUTTONS */
a.follow.plus-follow-button {
	
	color: var(--backgroundColor) !important;
}

a.follow.plus-follow-button:hover {
	
	color: white !important;
}

.identity .controls_section .item:hover .follow:hover {
    
    background-color: #8040A6 !important;
}

.plus-follow-button:hover {
    
    background-color: #8040A6 !important;
}



/* POSTS' TOP RIGHT CORNER COLOR */
.post_permalink {
	
	border-top-color: var(--backgroundColor) !important;
	border-right-color: var(--backgroundColor) !important;
}



/* OPAQUE DIALOGUE COLOR */
.ui_dialog_lock {
    
    background: rgba(41, 5, 61, .95) !important;   
}

.ui_peepr_glass {
    
    background: rgba(41, 5, 61, .95) !important;   
}

.post-forms-glass {
    background: rgba(41, 5, 61, .95) !important; 
}

.blue#glass_overlay, .blue#hello_glass, .blue.glass {
 	background: rgba(41, 5, 61, .95) !important; 
}



/* ANY BUTTON COLOR */
/*.ui_dialog*/
.chrome.blue {
    
    border-color: var(--buttonColor) !important;
    background-color: var(--buttonColor) !important;
}



/* CREATE POST BUTTON */
.compose-button {
    
    fill: var(--buttonColor) !important;
}



/* AVATARS' BACKGROUND */
.post_avatar .post_avatar_link {
    
    background-color: var(--backgroundColor) !important;
}



/* COLORED TEXT */
.reblog_follow_button .follow-text, .worded-follow-button .follow-text {
    
    color: var(--buttonColor) !important;
}



/* SWITCHES' COLOR */
.binary_switch input[type="checkbox"]:checked ~ .binary_switch_track {
    
    background: var(--buttonColor) !important;
}

