/* messagerie */
	/* contenu des messages */
	.dataBox .message .contenu {
		background-color: #4b644b !important;
		background-image: none !important;
		width: 414px !important;
		height: 299px !important;
		border-bottom: 1px solid #0a250a !important;
	}
	/* avatard */
	.dataBox .message .contenu .avatar {
		border-bottom: 2px solid #0a250a !important;
		border-right: 2px solid #0a250a !important;
	}

	/* historique messagerie */
		.dataBox .message .zone_conversation {
			border: 1px solid #0a250a !important;
		}
		/* header */
		.dataBox .message .zone_conversation div {
			background: rgba(10,37,10,0.5) !important;
			border-bottom: 1px solid #0a250a !important;
			text-align: center;
		}
		.dataBox .message .zone_conversation .conversation {
			background: none !important;
			border-bottom: 1px solid #0a250a !important;
			margin-bottom: 0px;
		}

		.dataBox .message .zone_conversation .conversation .ligne1 {
				/*Mettre ici la couleur en Hexa de l'heure d'arrivée du message. */
			color: #a4dba4 !important;
			font-weight: bold;
		}
		.dataBox .message .zone_conversation .conversation .ligne2 {
				/*Mettre ici la couleur en Hexa du nom de l'expéditeur. */
			color: #c0b86f !important;
			font-weight: bold;
		}
		.dataBox .message .zone_conversation .conversation:hover {
				/*Mettre ici la couleur en Hexa du fond de l'historique de message */
			background: rgba(10,37,10,0.5) !important;
		}
	/* /historique messagerie */
