css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("www.rememberthemilk.com") {
    #tasks .xtable {
        color:#222;
        font-size:1.3em;
        line-height:1.4;
        background-color:#fff;
        border:none;
        -moz-osx-font-smoothing: grayscale;
    }
    body {
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    }
    .xtd {
        border:none;
    }
    #searchtogglewrap {
        width:inherit;
    }
    #detailsbox, #onlinehelpwrap, #detailsstatuswrap, #keywrap, #detailswrap, #details, #searchtogglewrap, #searchboxwrap, div#searchbox {
        width: 195px !important;
    }
    #detailswrap, #keywrap{
        border: 1px solid #e3e3e3;
    }
    .prio1 {
        background-color: #FFC600;
    }
    #details {
        background-color: inherit;
    }
    #detailstabs, #tasktabs {
        border-right: 1px solid #e3e3e3;
        background: #e3e3e3;
    }
    #tasks .xtd + .xtd {
        padding-top:0.4em;
        border-top: 1px dotted #bbb;
    }
    #tasks .xtd {
        padding-bottom:0.4em;
    }
    .notewidth {
        width: 100% !important;
    }
    #midcontent > div {
        border:none !important;
    }
    .taskoverdue {
        color:#cc1144;
        text-decoration:none;
    }
    .taskduetoday {
        color:#00aa66;
    }
        .taskcloudcontent .abr-listtabs li.xtab_selected, .xtabs li.xtab_selected a:link, .taskcloudcontent .abr-listtabs li.xtab_selected a:visited, .taskcloudcontent .abr-listtabs li.xtab_selected a:active, .xtabs li.xtab_selected, .xtabs li.xtab_selected a:link, .xtabs li.xtab_selected a:visited, .xtabs li.xtab_selected a:active {
        text-decoration: none;
        color: #ffffff;
        background-color: #2B8CED !important;
    }
    #listtabs li, .xtabs li a, .xtabs .xtab_smartlist a, .xtabs_grey li, .xtabs li{
        background:#e3e3e3;
    }
    .xtabs li + li {
        margin-left: 0.5em;
    }
    .xtabs li a:link, .xtabs li a:visited, .xtabs li a:active {
        text-decoration: none;
        color: #999;
    }
    .xtabs li {
        border-bottom: none;
    }
    #content {
        padding-top:4.2em;
    }
    #content {
        position:relative;
    }
    #appheader {
        position:fixed;
        top:0;
        left:0;
        right:0;
        padding: 6px 6px;
        background-color:#ffffff;
        z-index:3;
        height:2.8em;
        border-bottom:1px solid #2B8CED;
        -moz-box-shadow:    2px 0 1px #999;
        -webkit-box-shadow: 2px 0 1px #999;
        box-shadow:         2px 0 1px #999;
    }
    #appheader > div:nth-child(3) {
        clear: both !important;
        float: left !important;
        margin-left: 7em;
        padding-top: 0px !important;
        line-height: 1.4;
    }
    #appheaderlogo {
        display:block;
        height: 2.4em !important;
        width: 5em !important;
        background-size: cover !important;
        margin-left: 0.6em;
    }
    #topnav {
        float:left !important;
        margin-left: 7em;
        opacity:1;
        -webkit-transition: opacity 200ms ease;
        -moz-transition: opacity 200ms ease;
        -o-transition: opacity 200ms ease;
        -ms-transition: opacity 200ms ease;
        transition: opacity 200ms ease;
    }
    #topnav:hover {
        opacity:1;
    }
    #tasksToolbox {
        opacity:0.5;
        -webkit-transition: opacity 200ms ease;
        -moz-transition: opacity 200ms ease;
        -o-transition: opacity 200ms ease;
        -ms-transition: opacity 200ms ease;
        transition: opacity 200ms ease;
        margin-bottom: 4px;
    }
    #tasksToolbox:hover {
        opacity:1;
    }
    #searchbox {
        position: absolute;
        top: 6px;
        right: 6px;
        padding-top:0;
    }
    #searchtogglewrap {
        font-size: 9px;
        margin-top:0;
    }

    #searchboxwrap > table {
        width:100%;
    }
    #keycontent > div:nth-child(3) {
        visibility: hidden;
    }
    #keycontent > div:nth-child(3):after {
        content: "Due Today";
        display: block;
        visibility:visible;
        margin-top:-1.44em;
        color:#00aa66;
        font-weight: bold;
    }
    #keycontent > div:nth-child(4) {
        visibility: hidden;
    }
    #keycontent > div:nth-child(4):after {
        content: "Overdue";
        display: block;
        visibility: visible;
        margin-top:-1.44em;
        color:#cc1144;
        font-weight: bold;
    }
    #statusbox {
        position:fixed;
        left:6px;
        right:6px;
        top:0.8em;
        padding-left:0 !important;
        z-index:2;
        width: auto !important;
        height: auto !important;
    }
    .orange_rbtop div, .orange_rbtop, .orange_rbbot div, .orange_rbbot {
        height: 2px;
    }
    #sorting {
        margin-top: 0;
    }
    #tools_spacer{
        padding-top: 1em !important;
    }
    .xtr_select {
        background-color: rgba(233, 233, 233, 1);
    }
    .xtr {
         -webkit-transition: background-color 400ms ease;
        -moz-transition: background-color 400ms ease;
        -o-transition: background-color 400ms ease;
        -ms-transition: background-color 400ms ease;
        transition: background-color 400ms ease;
    }
    #tasks .xtr:nth-last-child(1) .xtd + .xtd {
        border-bottom:1px solid #bbb;
    }
    .taskcloudcontent {
        border:none !important;
        padding: 0 !important;
    }
    .taskcloudcontent #listtabs {
        border:1px solid #e3e3e3;
        box-sizing: border-box;
    }
    .taskcloudcontent .abr-listtabs li {
        background: inherit !important;
    }

    #listAdder .taskaddicon {
        margin-left: 8px;
        height: 14px;
    }
    #listAdder .taskadd {
        font-weight: normal;
        text-decoration: none !important;
        color: #999;
    }
    #listAdder {
        background: #e3e3e3;
        padding: 4px 0 !important;
    }
    #listAdder a {
        font-weight: bold !important; 
    }
    .xtr td.prioN {
        background-color: #ffffff;
    }
    #tools, #tools_spacer {
        border: none !important;
    }
    #add-label, #add-text {
        color: #2B8CED;
        font-size: 1.6em;
    }
    .add-box {
        padding: 8px 20px 4px 8px;
        margin-bottom: 12px;
        border: 1px solid #999 !important;
        -moz-box-shadow:    inset 0 1px 1px #ccc;
        -webkit-box-shadow: inset 0 1px 1px #ccc;
        box-shadow:         inset 0 1px 1px #ccc;
        -webkit-border-radius: 3px;
        -moz-border-radius: 3px;
        border-radius: 3px
    }
    .add-box.highlight {
        border: 1px solid #2B8CED !important;
        -moz-box-shadow:    inset 0 0 2px #1B6aED;
        -webkit-box-shadow: inset 0 0 2px #1B6aED;
        box-shadow:         inset 0 0 2px #1B6aED;
    }
    
    .add-box .ab2, .add-box .ab3, .add-box .ab4, .add-box .ab5, .add-box .ab6, .add-box .ab7, .add-box .ab8, .add-box .ab9 {
        background:none !important;
    }
    #add-helpicon {
        right: -10px;
        top: 6px;
    }
    #add-box:before {
position:absolute;
        left: 12px;
        z-index:2;
        content: "+";
        display: block;
        visibility:visible;
        font-size: 30px;
        line-height: 18px;
        font-weight: bold;
        vertical-align: baseline;
        color: #2B8CED;
    }
    .add-field {
        padding: 0 0 0 28px !important;
        margin: 0;
    }
   .add-field input, .add-field-wrap{
        padding: 0 !important;
        margin: 0 !important;
    }
    .highlight .add-field-wrap {
        padding: 0px 0 6px 0 !important;
    }
    .ab1 tr {
        vertical-align: baseline;
    }
    .ab1 tr {
        vertical-align: baseline;
    }
    #tools_spacer {
        display: none;
    }
    #tools {
        padding-top: 0 !important;
    }
    .taskcloudcontent {
        padding: 0 4px !important;
    }

}
