css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {
    .site-center-aligned #player.watch-medium {
		width: 1036px;
	}

	.watch-medium .player-width {
		width: 1036px;
	}

	.watch-medium .player-height {
		height: 614px;
	}

	.site-center-aligned #player.watch-large, .site-center-aligned #player.watch-medium {
		margin-bottom: 0px;
	}

	body{
		background:#303030 !important;
	}

	.yt-uix-button-panel{
		padding-top: 10px;
	}

	#content,#player,#masthead-positioner {
		box-shadow: 0px 0px 10px #000;
	}
}
