css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(gardenweb.com), domain(houzz.com) {

/*new stuff*/
/*temporary and partial fix to changes houzz is currently implementing*/
.hz-header__secondary, .hz-cart__container {
display: none;
}
.hz-header__container, #hz-primary-header-container {
width:85% !important;
}
.discussions-page-header__container {
background-image: none !important;
}
.discussions-page-header__hero-overlay {
background-color: white !important;
}
.question-form__heading {
display:none !important;
}
.hz-header-full, .discussions, .mbl, .card--main, .discussions-page-header__hero-overlay, .mtl, .hz-card, .hz-question-card-full {
    background-image:url(https://image.ibb.co/meCPcF/off_White_Paper.jpg) !important;
    background-repeat: repeat !important;
}

a.hz-question-card__title:link {
    color: #268200 !important;
    text-decoration: none;
}
a.hz-question-card__title:hover {
    color: #888 !important;
    text-decoration: none;
}


a.hz-question-card__title:visited {
    color: #888 !important;
    text-decoration: none;
}

.div.text-s { 
position: relative !important;
top: 0 !important; 
}

.discussions__left {
position: fixed !important;
top: 60px !important; 
z-index: 9999 !important;
overflow-y: hidden; 
overflow-x: hidden;
}

.card--main.hz-card  {
left: 220px !important;
}



.card--main  {
left: 20px !important;
position: relative !important;
}

.discussions-page-header__question-form-wrapper  {
left: 50px !important;
}

div.spf-link > div:nth-child(3) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > a:nth-child(1),
div.spf-link > div:nth-child(3) > div:nth-child(3) > div:nth-child(1) > div:nth-child(1) > a:nth-child(1),
div.spf-link > div:nth-child(3) > div:nth-child(5) > div:nth-child(1) > div:nth-child(1) > a:nth-child(1),
div.spf-link > div:nth-child(3) > div:nth-child(7) > div:nth-child(1) > div:nth-child(1) > a:nth-child(1),
div.spf-link > div:nth-child(3) > div:nth-child(9) > div:nth-child(1) > div:nth-child(1) > a:nth-child(1),
div.spf-link > div:nth-child(3) > div:nth-child(11) > div:nth-child(1) > div:nth-child(1) > a:nth-child(1),
div.spf-link > div:nth-child(3) > div:nth-child(13) > div:nth-child(1) > div:nth-child(1) > a:nth-child(1),
div.spf-link > div:nth-child(3) > div:nth-child(15) > div:nth-child(1) > div:nth-child(1) > a:nth-child(1)
{
   padding-top: 60px !important;
}

.hzui-follow-button {
   padding: 0 !important;
}

.discussions-sidebar__item--lvl1.spf-link {
   display: none !important;
}

.products {
   display: none !important;
}

/*end new stuff*/


/*start houzz specific*/

    .top-commenters, .country-selector, .question-card__commenters, .hz-header__sale-badge, .question__right {
        display: none !important;
    }   
    
    .discussions__right {
        width: 75% !important;
    }

    .hz-question-card-full {
        width: 100% !important;
    }


/*end houzz specific*/.footer-columns--business .footer-column, .footer-columns--business ul.footer-column { 
    width: 20% !important; 
}

.topic-column { 
    width: 25% !important; 
}

.hbs.hz-rebrand-web .colorLink:visited {
    /*[[visitedLinkColor]]*/
}


.houzz-header .navbar-search-bar {
    max-width: unset !important;
} 

.pp__expand, .hz-carded-unpadded-x.hz-carded:nth-of-type(2){
    display: none !important;        
}

.t7 #questionBody, .t7 .comment-count, .t7 .feedComments {
    width:95% !important;        
}

[id*='navSpecialPromoLink'], [id*='header-promo'] {
    display:none !important;
}

#view-question-content > .trackMe.container > .hz-carded {
    display: none !important;
}

.hbs .clearfix::before, .hbs .clearfix::after, .hbs .dl-horizontal dd::before, .hbs .dl-horizontal dd::after, .hbs .btn-toolbar::before, .hbs .btn-toolbar::after, .hbs .btn-group-vertical > .btn-group::before, .hbs .btn-group-vertical > .btn-group::after, .hbs .navbar::before, .hbs .navbar::after, .hbs .navbar-header::before, .hbs .navbar-header::after, .hbs .navbar-collapse::before, .hbs .navbar-collapse::after, .hbs .panel-body::before, .hbs .panel-body::after, .hbs .modal-footer::before, .hbs .modal-footer::after {
    display: unset !important;
}

#navHome, .footer-column   {
    padding-left: 15px !important;
}

.container {
    width: 100% !important;
}

.t9.hbs .hz-carded, .t8.hbs .hz-carded, .t7.hbs .hz-carded {
    box-shadow: unset !important;
}

#ScrollToTopContainer.hide {
    opacity: .4!important;
    height: unset !important;
    right: 0% !important;
}
  
.top-content {
     width: auto !important;
     margin-left: 150px !important;
}

.t7 #questionCard #questionBody, .t7 #questionCard .comment-count, .t7 #questionCard .feedComments {
    margin-left: 0px !important;
    margin-right: 0px !important;
    width: 100% !important;
}

.askQuestionContent {
    width:100% !important;
    float: right !important;
    border: none !important;
}

.related-photos, .similarGalleriesContainer, .related-product-categories {
    display: none !important;
}

.r-sidebar {
    display: none !important;
}

.note-editable {
    min-height: 40px !important;
}

#signUpPromoFooter, .ask-form.claim-account-box {
    display: none !important;
}

.houzz-header .navbar-search-bar {
    width: auto !important;
}
   
.fullContent {
    width: unset !important;
}    
    
.profile-l-sidebar .hzBtn {
    width: 100px !important;
}
    
.profile-overview  {
    overflow: visible !important;
}

.hbs .hide {
    display: block !important;
}

.leftSideBar {
    position:relative !important;
    left: -10px !important;
    width: 150px !important;
    z-index: 999 !important;    
}
    
.footer-column, ul.footer-column {
    width: 25% !important;
}
  
.footer-columns {
    width: 100% !important;
}
    
#footerContainer, .footerContainer {
    min-width: unset !important;
}

#questionUserPhoto {
    margin-right: 20px !important;
}    
    
#questionCard {
    margin-left: auto !important;
    margin-right: auto !important;
    width: 90% !important;
}

.related-forums, #footerContainer, .feedComments .comment, .feedComments .addComment, .feedComments .moreComments, .sidebar.filter-tree.gardenWebTopicFilters.collapsible, .sidebar {
    background: none !important;
}
    
.player-wrapper { width: 100% !important;}    
    
.houzz-header-secondary, .houzz-header-secondary.header-nav-dropdown-menu, .rightSideBar, .hzTipBox, .unstyled.header-nav.main-nav, #navFeature, #navMyCart, #navTalk, #sidebar, #wnBox, wnBox, #wnButton, .adSideBar, .bigBold, .nav-holder, #shareTable, .rss-link {
    display: none !important;
}

.rightSideContent,#questionCard div#questionBody {
    float: unset !important;  
    /* margin-left: 0px !important;*/
}  

.comment.edit-comment-rte {
    border: 2px lightgrey solid !important;
    margin-bottom: 5px !important;
}

#questionCard #questionBody, #questionCard #questionTitle, #questionCard #questionInfo, #questionCard #questionDesc, #questionCard .questionActions, #questionCard #answersCtn, .width-capri, .middleContent, .leftSideContentWithAd, .rightSideContent, container trackMe {
    width:100% !important;
}

html {
    background-color: #5E8901 !important;
    background-image:url(/*[[sideImageURL]]*/) !important;
    background-attachment: fixed !important;
    /*[[sideImageOptions]]*/
}

.largeComments .commentBodyText, #questionCard #questionDesc {
    font-size: 22px !important;
    font-family: "museo-sans","Helvetica Neue",Helvetica,Arial,sans-serif !important;
    font-weight: 500 !important;
}
    
.large-header {
    font-family: "museo-sans","Helvetica Neue",Helvetica,Arial,sans-serif !important;
    font-weight: 900 !important;
    color: brown !important;
}
    
.questionTitle {
    font-size: 24px !important;
    font-family: "museo-sans","Helvetica Neue",Helvetica,Arial,sans-serif !important;
    font-weight: 900 !important;
}
    
#questionCard #questionTitle {
    font-family: "museo-sans","Helvetica Neue",Helvetica,Arial,sans-serif !important;
    font-weight: 900 !important;
}
    
body, .hz-carded {
    height:auto !important;
    /*border:#222222 solid 5px !important;*/
    margin-left:auto !important;
    margin-right:auto !important;
    background-image:url(/*[[centerImageURL]]*/) !important;
    /*[[centerImageOptions]]*/
}

body, .hz-carded, #navHeader {
     width: /*[[centralWidth]]*/ !important;
}

html body div#wrapper div#footer div.footer-holder div.footer-frame {
    padding: 0px !important;
    margin: 0px !important;
    width: 100% !important;
}


}
