css:
@-moz-document domain("netflix.com") {
.slider { 
  overflow-x: auto; 
  overflow-y: hidden; 
}

.slider>.sliderButton { 
  display: none; 
  visibility: hidden; 
}

.slider>.agMovieSet { 
  width: auto; 
  white-space: nowrap; 
}

.agMovieSet>.agMovie { 
  float: none; 
  vertical-align: top; 
}
}
