css:
#titlebar-buttonbox-
container, #titlebar-buttonbox-container toolbarbutton { background:#EEEEEE}

#urlbar,#searchbar .searchbar-textbox  {
    border:none !important;
    font-size:14px;
    padding:2px;
    background: #EEEEEE !important;
    color:black !important;
    border-radius:3px;
}

#appmenu-button,tab,#nav-bar,tabbrowser{
    background-color:#FFFFFF !important;
    background-image:none !important;
    border:none !important;
    text-shadow:none !important;
    
}
#appmenu-button {
    color:#777777 !important;
}
.tabs-newtab-button,.tabs-newtab-button:hover {
    background-image:none !important;
     background:#FFFFFF !important;
     border:none !important;
    
    width:30px;
    margin-left:10px;

}
.tabs-newtab-button:hover {
     background:#EEEEEE !important;
    order:none !important;
}

#navigator-toolbox:after {
     background-color:#FFFFFF !important;
       background-image:none !important;
    border-bottom:solid 1px silver;
    color:black !important;
    text-shadow:none !important;
}

#addon-bar,#PlacesToolbarItems,#TabsToolbar,#titlebar,#titlebar-content,#toolbar-menubar,#navigator-toolbox,#main-window,#PlacesToolbarItems,#personal-bookmarks{
    background-color:white !important;
    background-image:none !important;
    border:none !important;
}

#PersonalToolbar {
     border-right:solid 1px #FFFFFF !important;
     border-left:solid 1px #FFFFFF !important;
}

.tabbrowser-tab,
.tabs-newtab-button, .tab-background {
    background:white !important;
    background-image:none !important;
    border:none !important;
   
}
#main-window #TabsToolbar {
    margin-top:-4px !important;
}
#main-window[sizemode="maximized"] #TabsToolbar {
    margin-top:-4px !important;
}
.tabmail-tab:not([selected]):not(:hover) +
.tabmail-tab:not([selected]):not(:hover)::before,
.tabmail-tab:last-child:not([selected]):not(:hover)::after {
  background: none !important;
}

.tabbrowser-strip {
    background-image: none !important;
    color:white !important;
}

#TabsToolbar {height: 34px !important;    background-image:none !important;border:none !important;}


/* curves */
#TabsToolbar, #TabsToolbar *
{ clip-path: none !important;
margin-bottom: 0 !important; background-image:none !important}

.tab-background-start, .tab-background-end
{ visibility: hidden !important;
box-shadow: none !important; 
    background-image:none !important;
    border:none !important;
}

.tab-background-middle
{  background:#FFFFFF !important;color:white !important; border:0 !important; background-image:none !important; padding:0px !important;margin:0px !important}

tab  {
    font-size:14px !important;
    color:#777777 !important;
    margin:0 !important;
    background-image:none !important;
    padding:0 !important;
    margin-bottom:0 !important;
    padding-bottom:0px !important;
    margin-right:5px !important;
    background:#FFFFFF !important;
   border:none !important;
}

tab:hover {
    background-image:none !important;
    background:#FFFFFF !important;
    font-size:14px !important;
    border:none !important;
    margin-bottom:0px !important;
    border-bottom:solid 2px /*[[tab-underline]]*/ !important;
    padding-bottom:0 !important;
    font-weight:600;   
}

tab[selected="true"] {
    background-image:none !important;
    background:#FFFFFF !important;
    border:none !important;
    border-bottom:solid 3px /*[[tab-underline]]*/ !important;
    margin-bottom:0px !important;
    font-weight:600;
    color:#2f2f2f !important;  
    padding-bottom:0 !important;
    padding-right:0 !important;
}
