css:
@-moz-document domain("subscene.com") {

/* 
 * Name:    Subscene – Darkened
 * Title:   Another darkened website by me
 * Author:  Crystallis
 * Version: 0.1
 *
 */

/* Removes the ads */
div[class="banner"] {
display: none ! important;
}
/* Removes the ads */
body {
background-image: none ! important;
background-repeat: no-repeat,no-repeat,repeat-x;
background-position: center top,center bottom,0 0;
background-color: #141414 ! important;
color: #909090 ! important;
font-size: 80%;
text-align: left;
width: 100%;
border-bottom: 130px solid #1e1e1e ! important;
}
a {
color: #2793e6 ! important;
text-decoration: none ! important;
}
a:hover {
color: #2793e6;
text-decoration: underline ! important;
text-shadow: 0px 0px 3px rgba(39,147,230,1) ! important;
}
#search .text {
border-style: solid;
border-width: 1px;
background: #333333 ! important;
border-color: #202020 #202020 #202020 #202020 ! important;
color: #909090 ! important;
border-radius: 4px;
-moz-border-radius: 4px;
-webkit-border-radius: 4px;
width: 450px;
height: 18px;
padding: 6px 0 4px 4px;
margin-right: 6px;
margin-top: -2px;
font-size: 15px;
}
button, .button {
font-size: 14px;
color: #909090 ! important;
padding: 6px 10px;
background: -moz-linear-gradient(top,#3b3b3b 0%,#1b1b1b) ! important;
background: -webkit-gradient(linear,left top,left bottom,from(#3b3b3b),to(#1b1b1b)) ! important;
background: #1d4257 -ms-linear-gradient(top,#3b3b3b 0%,#1b1b1b 100%) ! important;
border-radius: 3px;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
border: 0;
-moz-box-shadow: 1px 1px 1px rgba(0,0,0,.2),inset 0 0 0 rgba(255,255,255,0);
-webkit-box-shadow: 1px 1px 1px rgba(0,0,0,.2),inset 0 0 0 rgba(255,255,255,0);
}
button:hover, .button:hover {
background: -moz-linear-gradient(top,#3e3e3e 0%,#1b1b1b) ! important;
background: -webkit-gradient(linear,left top,left bottom,from(#3e3e3e),to(#1b1b1b)) ! important;
}
#user-navigation, #forum-navigation, #admin-navigation {
float: right;
background: #1e1e1e ! important;
line-height: 26px;
height: 30px;
padding: 0 11px;
border-radius: 0 0 4px 4px;
-moz-border-radius: 0 0 4px 4px;
-webkit-border-radius: 0 0 4px 4px;
}
h1, h2, h3, .h2 {
font-family: "Century Gothic",Arial;
color: #a1a1a1 ! important;
}
.box {
border-top: solid 1px #1d1d1d ! important;
padding: 0;
padding: 11px 0;
text-align: left;
}
.byTitle h2 {
margin: 12px 0 8px 0;
color: #fff;
background-color: transparent ! important;
font-weight: bold;
height: 26px;
line-height: 26px;
padding: 0 3px 3px 7px;
border-top: solid 1px #1d1d1d ! important;
border-bottom: solid 1px #1d1d1d ! important;
}
.content {
border-top: 1px solid #1d1d1d ! important;
padding: 14px 0 22px 0;
}
.subtitles thead {
background: #1f1f1f ! important;
color: #909090 ! important;
height: 21px;
line-height: 21px;
}
.byFilm .language-filter span {
font-size: 18px;
font-weight: bold;
color: #909090 ! important;
font-family: "Century Gothic",Arial;
}
.byFilm .sort-by {
float: right;
font-size: 13px;
height: 30px;
line-height: 30px;
background-color: #1f1f1f ! important;
border: 1px solid #1d1d1d ! important;
border-bottom: 0;
color: #909090 ! important;
padding: 0 8px 0 6px;
border-radius: 4px 4px 0 0;
}
tr {
display: table-row;
vertical-align: inherit;
border-color: inherit;
background: #202020 ! important;
}
.subtitles td.a1 a:link {
display: block;
background-color: #202020 ! important;
}
.subtitles td.a1 a:hover {
background-color: #232323 ! important;
cursor: pointer;
}
.subtitles td.a1 a:visited {
background-color: #202020 ! important;
}
.subtitles td.a1 a:visited, .subtitles td.a1 .visited {
background-color: #202020 ! important;
}
.window {
background: #202020 ! important;
border-top: 1px solid #1b1b1b ! important;
padding: 11px 13px;
margin: 0 0 13px 0;
clear: both;
}
textarea {
-webkit-appearance: textarea;
background-color: #333333 ! important;
border: 1px solid;
border-image-source: initial;
border-image-slice: initial;
border-image-width: initial;
border-image-outset: initial;
border-image-repeat: initial;
-webkit-rtl-ordering: logical;
-webkit-user-select: text;
flex-direction: column;
resize: auto;
cursor: auto;
padding: 2px;
white-space: pre-wrap;
word-wrap: break-word;
border-color: #202020 ! important;
}
input, input[type="password"], input[type="search"], isindex {
-webkit-appearance: textfield;
padding: 1px;
background-color: #333333 ! important;
border: 2px inset;
border-image-source: initial;
border-image-slice: initial;
border-image-width: initial;
border-image-outset: initial;
border-image-repeat: initial;
-webkit-rtl-ordering: logical;
-webkit-user-select: text;
cursor: auto;
border-color: #202020 ! important;
color: #909090 ! important;
padding: 2px ! important;
}
.subtitle .comment {
-moz-box-shadow: inset 1px 2px 5px #1b1b1b ! important;
-webkit-box-shadow: inset 1px 2px 5px #1b1b1b ! important;
box-shadow: inset 1px 2px 5px #1b1b1b ! important;
padding: 11px 9px;
color: #909090 ! important;
min-height: 40px;
position: relative;
background: #202020 ! important;
margin: 28px 0 8px 0;
line-height: 16px;
}
.subtitles table {
background-color: #202020 ! important;
width: 100%;
}
.subtitle .comment:before {
background: none ! important;
display: block;
height: 27px;
width: 27px;
left: 10px;
top: -20px;
position: absolute;
content: "";
}
.posts .post {
float: right;
width: 616px;
margin-right: 212px;
-moz-box-shadow: inset 1px 2px 5px #1b1b1b ! important;
-webkit-box-shadow: inset 1px 2px 5px #1b1b1b ! important;
box-shadow: inset 1px 2px 5px #1b1b1b ! important;
padding: 8px 11px;
color: #909090 ! important;
min-height: 80px;
background: #202020 ! important;
position: relative;
word-wrap: break-word;
}
.posts .post:before {
background: none ! important;
display: block;
height: 24px;
left: -21px;
position: absolute;
top: 52px;
width: 34px;
content: "";
}
.ratings ul {
background-color: #202020 ! important;
border-radius: 4px 4px 4px 4px;
margin-top: 8px;
padding: 1px 5px;
}
.ratings ul>li {
margin: 5px 0;
-moz-box-sizing: border-box;
background-color: #232323 ! important;
border-bottom: 1px solid #1d1d1d ! important;
border-radius: 5px 5px 5px 5px;
line-height: 25px;
height: 26px;
overflow: hidden;
padding-left: 5px;
}
.user-home .subtitles .list {
background-color: #1d1d1d ! important;
border-radius: 4px 4px 4px 4px;
margin-top: 6px;
padding: 1px 3px;
width: 532px;
}
.user-home .subtitles ul>li, .user-home .footer {
float: left;
width: 173px;
text-align: center;
height: 240px;
margin-bottom: 56px;
margin: 4px 3px 0 4px;
padding-bottom: 5px;
height: 300px;
padding-top: 1px;
background-color: #232323 ! important;
border-bottom: 1px solid #1d1d1d ! important;
border-radius: 5px;
-webkit-box-sizing: border-box;
-moz-box-sizing: border-box;
box-sizing: border-box;
width: 170px;
position: relative;
}
.pagination {
text-align: right;
background: #202020 ! important;
border-top: 1px solid #1d1d1d ! important;
padding: 11px 13px;
margin: 0 0 13px 0;
clear: both;
font-size: 14px;
font-weight: bold;
}
.pagination .active {
background-color: #232323 ! important;
font-size: 20px;
}
.sand-box {
background-color: #1d1d1d ! important;
border-radius: 4px 4px 4px 4px;
margin-top: 8px;
padding: 1px 5px;
}
.sand-box>ul>li, .sand-box .area {
-moz-box-sizing: border-box;
background-color: #232323 ! important;
border-bottom: 1px solid #1d1d1d ! important;
border-radius: 5px 5px 5px 5px;
margin: 5px 0;
overflow: hidden;
padding-left: 5px;
min-height: 60px;
}
.byFilm .rating-guide span.visited {
background: #202020 ! important;
padding: 3px 18px 3px 18px;
line-height: 21px;
height: 21px;
border: 2px solid #1d1d1d ! important;
}
footer {
background: #202020 ! important;
height: 39px;
clear: both;
border-radius: 4px;
-moz-border-radius: 4px;
-webkit-border-radius: 4px;
margin: 200px auto -110px;
padding: 13px;
font-size: 12px;
}
.poster {
border: solid 1px #3e3e3e ! important;
}
.user-home .avatar {
border: 1px solid #3e3e3e ! important;
}
.jumbotron {
background-color: #1e1e1e ! important;
}
hr {
border-top: 1px solid #2e2e2e ! important;
}
.btn-primary {
color: #808080 ! important;
background-color: #2e2e2e ! important;
border-color: #3e3e3e ! important;
}
.btn-primary:hover, .btn-primary:focus, .btn-primary:active, .btn-primary.active, .open .dropdown-toggle.btn-primary {
color: #c0c0c0 ! important;
background-color: #3e3e3e ! important;
border-color: #4e4e4e ! important;
}
}
