css:
@-moz-document domain("primewire.ag"),domain("letmewatchthis.ch"){

body,
.col1,
.col2,    
.container { 
        background:rgba(0,0,0,.6) !important; }

.choose_tabs {
        background: rgba(0,0,0,.2) !important;}
    
.main_body ,
.login_button { 
        background: rgba(100,100,100,.5) !important; }
    
html { 
        background-image: none !important;
        background: transparent !important; }      
      
#username,
#password,
.titles,
.movie_version,
.tolerance_box,
.sub_titles,
.footer,
.comment_box,
.titles > span,    
.sub_titles > span,
.featured_movie_item > a { 
        background: transparent !important; }      
     
#username,
#password,
.login_button { 
        border-color: rgba(150,150,150,.3) !important; }    

.index_item.index_item_ie > a > img,    
.featured_movie_item > a > strong > img,    
td > .movie_thumb { 
        border-color: transparent !important; }

p, 
h2,
.playlist_listing_desc { 
        color: #999999 !important; }

.movie_info_actions > .mlink_splitter { 
        opacity: 0 !important;}
    
.mlink_imdb > a:not(:hover),
.mlink_submitlink > a:not(:hover), 
.trigger > span:not(:hover),
.mlink_buydvd > a:not(:hover)  { 
        opacity: .2 !important; }    

.email_subscribe_box > form > input { 
        opacity: .3 !important; }

    .comments_box_vote ,
.comment_reply_link > a  { 
        opacity: .4 !important; }

.btn,    
#search_term.box,
td > .movie_ratings { 
        opacity: .6 !important; }
    
.index_item.index_item_ie > a > h2 { 
        color: red !important;
        text-shadow: 1px 1px 1px #000, -1px -1px 1px #000 !important; }     
}
