css:
/* i really want this to be global */
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

.new_post_label {
	padding: 71px 1px 8px 0 !important;
	font-family: "Courier", Courier New,Georgia,Serif;
font-weight: bold !important;
}

.new_post_label > I {
  color: transparent !important;
  transition: background-position .08s linear !important;
}
#new_post_label_text,
#new_post_label_photo,
#new_post_label_quote,
#new_post_label_link,
#new_post_label_chat,
#new_post_label_audio,
#new_post_label_video {
  background:
 url("http://i58.tinypic.com/2zjkidh.png") no-repeat !important;
}


#posts .post.new_post .new_post_label{
padding-top: 74px !important;
padding-bottom: 0px !important;
font-family: "Courier", Courier New,Georgia,Serif;
font-weight: bold !important;}

#new_post_label_text {
  background-position: 10px 0px !important;
  border-top-left-radius: 8px !important;
  border-bottom-left-radius: 8px !important;
}
#new_post_label_photo {
  background-position: -68px 0px !important;
}
#new_post_label_quote {
  background-position: -140px 0px !important;
}
#new_post_label_link {
  background-position: -218px 2px !important;
}
#new_post_label_chat {
  background-position: -290px 0px !important;
}
#new_post_label_audio {
  background-position: -368px 0px !important;
}
#new_post_label_video {
  background-position: -458px 0px !important;
  border-top-right-radius: 8px !important;
  border-bottom-right-radius: 8px !important;
}




#new_post_label_text:hover,
#new_post_label_photo:hover,
#new_post_label_quote:hover,
#new_post_label_link:hover,
#new_post_label_chat:hover,
#new_post_label_audio:hover,
#new_post_label_video:hover {
  color: black !important;
  font-weight: bold !important;
}
 


#new_post_label_text:hover {
  background-position: 11px -95px !important;
}
#new_post_label_photo:hover {
  background-position: -68px -95px !important;
}
#new_post_label_quote:hover {
  background-position: -140px -95px !important;
}
#new_post_label_link:hover {
  background-position: -218px -96px !important;
}
#new_post_label_chat:hover {
  background-position: -290px -95px !important;
}
#new_post_label_audio:hover {
  background-position: -368px -95px !important;
}
#new_post_label_video:hover {
  background-position: -454px -95px !important;
}
}


#new_post_label_text:active,
#new_post_label_photo:active,
#new_post_label_quote:active,
#new_post_label_link:active,
#new_post_label_chat:active,
#new_post_label_audio:active,
#new_post_label_video:active {
  color: black !important;
  font-weight: bold !important;
}

#new_post_label_text:active {
  background-position: 10px 0px !important;
  border-top-left-radius: 8px !important;
  border-bottom-left-radius: 8px !important;
}
#new_post_label_photo:active {
  background-position: -68px 0px !important;
}
#new_post_label_quote:active {
  background-position: -140px 0px !important;
}
#new_post_label_link:active {
  background-position: -218px 2px !important;
}
#new_post_label_chat:active {
  background-position: -290px 0px !important;
}
#new_post_label_audio:active {
  background-position: -368px 0px !important;
}
#new_post_label_video:active {
  background-position: -458px 0px !important;
  border-top-right-radius: 8px !important;
  border-bottom-right-radius: 8px !important;
}
