css:
@-moz-document domain(habitrpg.com) {

body {
    line-height: normal !important;
    background: url(http://i.imgur.com/anSVcTO.png) !important;
}
    
    .toolbar {
        display: block !important;
        border: none !important;
        margin: auto !important;
        width: auto !important;
        border-bottom-right-radius: 4px !important;
        /*box-shadow: 0 0 8px rgba(0,0,0,0.5) !important;*/
        background: rgba(0,0,0,0.5) !important;
    }
    .toolbar-bailey,
    .toolbar-wallet,
    .toolbar-options,
    .toolbar-subscribe,
    .toolbar-nav {
        padding: .2em .5em !important;
    }
    
    .toolbar-nav > li {
        margin-right: 2em !important;
    }
    
    
    .toolbar div[ng-if^="_"] {
        box-shadow: 0 0 10px rgba(0,0,0,0.6) !important;
    }
    
    
    
    .toolbar-currency.gold {
        /*font-size: 13px !important;
        font-weight: 400 !important;*/
        color: rgba(255, 190, 0, .75) !important;
    }
    
    
    .toolbar-nav .toolbar-button > a,
    .toolbar-nav .toolbar-button button,
    .toolbar-nav .toolbar-button-dropdown,
    .toolbar-nav .toolbar-button-dropdown > a,
    .toolbar-notifs a,
    .toolbar-sync a,
    .toolbar-settings a {
        border: none !important;
    }
    /* navbar buttons */
    .toolbar-button-dropdown a:nth-of-type(2) {font-size: 16px !important; font-weight: 700 !important; line-height: 20px !important;}
    .toolbar-button-dropdown a:nth-of-type(2),
    .toolbar-notifs,
    .toolbar-sync, 
    .toolbar-settings {
        opacity: 0.4 !important;
        transition: 300ms !important;
    }
    .toolbar-button-dropdown a:nth-of-type(2):hover,
        .toolbar-notifs:hover, 
        .toolbar-sync:hover, 
        .toolbar-settings:hover {
        opacity: 1 !important;
        transition: 300ms !important;
    }
        .toolbar-nav .toolbar-button > a, 
        .toolbar-nav .toolbar-button button,
        .toolbar-nav .toolbar-button-dropdown > a:first-of-type,
        .toolbar-nav .toolbar-button-dropdown > a:nth-of-type(2),
        .toolbar-notifs > a, .toolbar-notifs button,
        .toolbar-sync > a, 
        .toolbar-settings > a, 
        .toolbar-sync button, 
        .toolbar-settings button {
        background: none !important;
        border: none !important;
        color: rgba(255,255,255,0.7) !important;
        font-weight: 400 !important;
        transition: 300ms !important;
    }
    .toolbar-notifs > a > .glyphicon:nth-child(1),
    .toolbar-sync .glyphicon,
    .toolbar-settings .glyphicon {
        color: #FFF !important;
    }
    
        .toolbar-nav .toolbar-button > a:hover, 
        .toolbar-nav .toolbar-button button:hover,
        .toolbar-nav .toolbar-button-dropdown > a:hover, 
        .toolbar-nav .toolbar-button-dropdown button:hover,
        .toolbar-nav a:hover,
            .toolbar-mobile > a:hover,
            .toolbar-nav .toolbar-button-dropdown > a:hover,
            .toolbar-nav .toolbar-button > a:hover,
            .toolbar-sync > a:hover,
            .toolbar-settings > a:hover,
            .toolbar-notifs > a:hover,
            .toolbar-controls .toolbar-controls-button > a:hover,
            .toolbar-subscribe-button > a:hover,
            .toolbar-controls .toolbar-subscribe-button > a:hover,
            .options-menu li > a:hover,
            .options-submenu li > a:hover,
            .toolbar-mobile button:hover,
            .toolbar-nav .toolbar-button-dropdown button:hover,
            .toolbar-nav .toolbar-button button:hover,
            .toolbar-sync button:hover,
            .toolbar-settings button:hover,
            .toolbar-notifs button:hover,
            .toolbar-controls .toolbar-controls-button button:hover,
            .toolbar-subscribe-button button:hover,
            .toolbar-controls .toolbar-subscribe-button button:hover,
            .options-menu li button:hover,
            .options-submenu li button:hover {
            background: none !important;
            color: #FFF !important;
            transition: 300ms !important;
        }
    
    /* special treatment for tasks button */
    .toolbar-button a[ui-sref=tasks] {margin-left: .5em !important;padding: 0 1em !important; font-weight: 600 !important; /*background: rgba(0,0,0,0.5) !important; color: rgba(255,255,255,0.8) !important;*/}
    /*.toolbar-button a[ui-sref=tasks]:hover {background: rgba(0,0,0,0.5) !important; color: #FFF !important;}*/
    
    ul.toolbar-wallet {margin-top: 4px !important; margin-bottom: -2px !important;}
    ul.toolbar-options {margin-top: 5px !important;}
    
    
    
    
    
    
    /* avatar */
    .herobox, .herobox:after, .herobox.isLeader, .hero-stats {
        background: none !important;
        border: none !important;
        outline: none !important;
    }
    
       .herobox .label-default {
           background: rgba(0,0,0,0.4) !important;
       }
       
       /* HP, exp, mana bars */
    .hero-stats {
        padding: 0 1em !important;
        min-width: 165px !important;
    }
       .hero-stats .meter {
           box-shadow: 1px 1px 8px rgba(0,0,0,0.6),
               inset 4px 0px 8px rgba(0,0,0,0.3) !important;
           border-radius: 1.5px !important;
           outline: none !important;
           font-size: 14px !important; /* dets height */
           color: rgba(0,0,0,0.55) !important;
           font-weight: 600 !important;
           margin: 0 0 .9em !important;
       }
             .meter-text {font-family: Open Sans, Lato, sans-serif !important;}
       .hero-stats .bar {
           border-top-left-radius: 1.5px !important;
           border-bottom-left-radius: 1.5px !important;
           border-top-right-radius: 0 !important;
           border-bottom-right-radius: 0 !important;
           box-shadow: 0 0 5px 2px rgba(0,0,0,0.14) !important;
           transition: 300ms !important;
           border: none !important;
       }
    
    
    /* Experimental animations! */





.current-pet {
    animation-duration: 60s !important;
    -webkit-animation-name: boingg !important;
    animation-name: boingg !important;
    animation-delay: 45s !important;
    animation-iteration-count: infinite;
}
    @keyframes boingg {
      0%, 2.1% {transform: translateY(0) scaleX(1) scaleY(1);}
      1% {transform: translateY(8px) scaleY(0.9) scaleX(1.05);}
      1.5% {transform: translateY(-24px) scaleY(1.06) scaleX(1);}
      1.8% {transform: translateY(8px) scaleY(0.95) scaleX(1.2);}
      
    }

.herobox span[class^="Mount_Head"] {
    animation-duration: 80s !important;
    -webkit-animation-name: rawrr !important;
    animation-name: rawrr !important;
    animation-delay: 75s !important;
    animation-iteration-count: infinite;
}
    @keyframes rawrr {
        0%,7.5% {transform: rotate(0);}
        2.5% {transform: rotate(-.015turn) translateX(-2px) translateY(3px);}
        3.5% {transform: rotate(.01turn) translateX(-1px) translateY(4px);}
    }
.herobox .character-sprites span[class^="weapon_"] {
    animation-duration: 100s;
    animation-name: becareful;
    animation-iteration-count: infinite;
    animation-delay: 57s !important;
}
    @keyframes becareful {
          1%,1.3% {transform: rotate(30deg); transform-origin: 47px 68px;}
          1.5%,1.6% {transform: rotate(-100deg) translateY(-4px); transform-origin: 47px 68px;}
          3.5% {transform: rotate(0); transform-origin: 47px 68px;}
    }
.herobox .character-sprites span[class^="headAccessory_"] {
        animation-duration: 28s;
       animation-name: twitchy;
       animation-iteration-count: infinite;
    animation-delay: 19s !important;
    }
    @keyframes twitchy {
        0%,3% {transform: skewX(0)}
        1.5% {transform: skewX(3deg)}
        2% {transform: skewX(-3deg)}
        2.3% {transform: skewX(3deg)}
    }

    
    
    
    
    
    
    /* whoops, this breaks scrolling to the bottom! 
    #undefined-sticky-wrapper {height: unset !important;}*/

.header-wrap {
    padding: 44px 0 0 0 !important;
    box-shadow: inset 0 0 32px rgba(0,0,0,.8) !important;
    background-size: cover !important;
    background: #000 url(http://i.imgur.com/5sAqH6l.jpg) right center;
    /*[[Location]]*/
/* credits to Lac-Tic (http://lac-tic.deviantart.com) for the amazing background! */
}

/* hide completed dailies */
    .task.daily.completed {display: none !important;}


/* hide gems and subscribe */
ul.toolbar-subscribe, a.gem-wallet {
    display: none !important;
}
    
.header-wrap {
    border:none !important;
}

.tasks-lists .row .col-md-3 {
    padding: 0 .1em !important;
}
    /* make rewards column skinnier */
    .col-md-3:not(:last-child) {
        width: 26.666% !important;
    }
    .col-md-3:last-child {
        width: 20% !important;
    }
    
    
/* tags */
    .filters {
        margin: 6px 12px 0 0 !important;
/*        background: rgba(0,0,0,0.6) !important;
        display: table !important;
        width: auto !important;
        position: relative !important;
        z-index: 0 !important;
        border-bottom-right-radius: 4px !important;
        border-bottom-left-radius: 4px !important;
        height: 5px !important;
    }
        .filters a {color: #FFF !important;}
    .filters:hover {
        margin: 3px 12px 0 0 !important;
        transition: 300ms !important; */
    }
    
.pull-left.filter-description {display: none !important;} /* Hide "Tags:"" */

    .nav-pills > li > a {
        opacity: 0.6 !important;
        padding: 4px 12px !important;
    }
    .nav-pills > li > a:hover {opacity: 1 !important;}
    .nav-pills .glyphicon-pencil,
    .nav-pills .glyphicon-remove-sign {opacity: 0.3 !important;}
    .nav-pills .glyphicon-remove-sign:hover {opacity: 1 !important;}
    
    
/* TASKS */
    
.container-fluid {padding: 0 10px !important;}
    .row {margin: 0 !important;} 
    
.task-column {
    padding: 4px 8px 0px !important;
    border-radius: 2px !important;
    background: none !important;
    border: none !important;
}
    .task-column hr {display: none !important;}
    
h1, h2, h3 {
    line-height: 1.4em !important;
}
h2.task-column_title {
    font-size: 1.4em !important;
    margin: 4px 0 0em !important;
    text-align: center !important;
    font-weight: 400 !important;
    line-height: 1 !important;
    font-family: Open Sans, Lato, sans-serif !important;
    color: #777 !important;
    text-shadow: 0px 1px 1px rgba(255,255,255,0.7);
}

.addtask-form, .addtask-field, .addtask-field input {
    /*display: table !important;
    margin-left: auto !important;
    width: auto !important;
    margin-right: auto !important; */
    
}
    
    /* add new */
    .addtask-form {
        outline: none !important;
        border: 0px solid rgba(0,0,0,0.1) !important;
        border-radius: 2px !important;
        margin: 0 0 5px !important;
        background: none !important;
    }
    .addtask-form:focus {
        border-color: #4595E5 !important;
	      box-shadow: 0 0 4px #4595E5 !important;
    }
    .addtask-field input {
        height: auto !important;
        padding: 0px 0 0px 6px !important;
        background: none !important;
        transition: 300ms !important;
        color: rgba(0,0,0,0.3) !important;
        font-size: 13px !important;
    }
       .addtask-field input:focus {
           margin: 6px 0 6px 0 !important;
           padding: 4px 0 6px 6px !important;
           box-shadow: 0 0 2px #4595E5 !important;
           background: rgba(255,255,255,0.7) !important;
           border: none !important;
           transition: 200ms !important;
           border-color: #4595E5 !important;
	         box-shadow: 0 0 3px #4595E5 !important;
           color: rgba(0,0,0,0.8) !important;
           font-size: inherit !important;
       }
    .addtask-btn {
        height: auto !important;
        display:none !important;
    }



/* TASKS */
    .task {
        border-radius: 2px !important;
        margin-bottom: 4px !important;
        box-shadow: inset 0px 0px 0px 1px rgba(0,0,0,0.15),
        1px 1px 3px rgba(0,0,0,0.1)    !important;
        padding: 6px 4px !important;
        width: 100% !important;
    }
    .habits .task, .dailys .task {
        display: table !important;
    }
    
    .btn-buy, .task-primary, .task-controls, .task-checker, .task-checker label {
        border-top-left-radius: 2px !important; 
        border-bottom-left-radius: 2px !important;
    }


    /* + / - */
    a.task-action-btn {
    width: 2em !important;
    height: calc(100%) !important;
        top: -1px !important;
        position: relative !important;
    padding: 0 !important;
    font-family: open sans, Lato, sans-serif !important;
    font-weight: 700 !important;
    color: rgba(0,0,0,0.4) !important;
    font-size: 18px !important;
    line-height: 26px !important;
        box-shadow: inset 0 0 0 1px rgba(0,0,0,0.1) !important;
}

        /* plus + */

        .task-action-btn:first-child {
            border-top-left-radius: 2px !important; 
            border-bottom-left-radius: 2px !important;
            margin: 1px 0 0 0px !important;
            border: none !important;
        }
        /* minus - */
        .task-action-btn:nth-child(2) {
            margin: 1px 0 0 0px !important;
        }

    /* checkboxes — dailies (separate section for todos) */
    .task-checker label {
        width: 2.5em !important;
        outline: none !important;
        box-shadow: inset 0px 0 0 1px rgba(0,0,0,0.1) !important;
    }
    
    /* ☐ */
    .dailys .action-yesno label:after, 
        .dailys .action-yesno label:before {
        display: table !important;
        line-height: 0 !important;
        margin: 9px 12px !important;
    }
    
        .action-yesno label:hover:after {
            height: 100% !important;
            width: 100% !important;
            margin: auto !important;
            top: 0px !important;
            position: absolute !important;
            vertical-align: middle !important;
            border: none !important;
    }
    .btn-buy {
        min-height: unset !important;
        box-shadow: inset 0 0 0px 1px rgba(0,0,0,0.1) !important;
        padding: .5em !important;
        display: inline-block !important;
    }

/* task box symbols */
.task-meta-controls {
    margin: 0px !important;
}
    /* hide unused controls */
    .task-meta-controls .glyphicon-pencil {
        position: absolute !important;
        top: 0 !important;
        opacity: 0;
        line-height: 2em !important;
        right: 4px !important;
        height: 100% !important;
    }
    .glyphicon-pencil:hover {opacity: .7 !important;}
    .glyphicon-tags, .glyphicon-signal, .glyphicon-trash, .task-notes .glyphicon-comment, .glyphicon-forward {display: none !important;}
    
    /* don't hide controls
    .glyphicon-pencil:hover {opacity: .7 !important;}
    .glyphicon-tags, .task-notes .glyphicon-comment, .glyphicon-forward {display: none !important;}
    div[ng-show^="charts"] div {width: unset !important;}
    */
    
/* to-do: remaining / completed tabs */
.todos .tabs-below {display:none !important;}

/* expand/collapse list 
a.badge {
    margin: auto !important;
    float: right !important;
    position: absolute !important;
    top: 0.25em !important;
    right: 1.5em !important;
    background: #333 !important;
}*/
    .task-meta-controls a.badge {
    }

.task-text {
    color: rgba(0,0,0,0.65) !important;
    text-align: center !important;
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    margin: auto auto !important;
    display: block !important;
    height: 100% !important;
    line-height: 1.1 !important;
}
    .habits .task-text {padding: 0 0 0 6em !important;}
    .task-text img, .emoji {
        padding-top: 2px !important;
    }

/*li.task.completed {
    opacity: 0.5 !important;
}
li.task.completed .emoji {
    opacity: 0.5 !important;
}*/
    
/* TODOS */
 /* checkboxes */
    .todos .action-yesno label:after {
        margin: 8px auto !important;
    }
    .todos .action-yesno label:hover:after, .action-yesno label:focus:after  {
        margin: 0 !important;
        top: 2px !important;
        display: table !important;
        padding: 0 !important;
        line-height: 0em !important;
        height: auto !important;
        font-size: 2.5em !important;
    }

.todos .task {
    /*margin-bottom: 4px !important;*/
    min-height: 0 !important;
}
.todos .task-text {
    font-size: 13px !important;
}
    
    
    /* list items */
    .task-text fieldset.option-group {
        display: table !important;
        margin: auto !important;
        width: auto !important;
        border: none !important;
    }
    .task-text fieldset.option-group label {
        display: table !important;
        margin: 5px 0 !important;
        width: auto !important;
    }
    .dailys .task-text fieldset input[type="radio"], 
    .dailys .task-text fieldset input[type="checkbox"] {
        margin: 2px 0 0 -18px !important;
    }
    .todos .task-text fieldset input[type="radio"], 
    .todos .task-text fieldset input[type="checkbox"] {
        margin: 0px 0 0 -18px !important;
    }
    
footer {
    display:none !important;
}
 
    

/* Rewards */


.rewards { /* a .task-column */
    margin: 0 0 12px 0 !important;
}
    .rewards .task
    {
        display: table !important;
    }



    



/* Hide Anki tasks */
    li[id="task-Anki Correct Answer"], 
    li[id="task-Anki Timebox Reached"] {
    visibility: collapse !important;
}
   
    
    
    
    
    
    
    
    
    
    
/* pop-up alerts */
    .ui-pnotify {
        outline: none !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: inset 0 0 1px rgba(0,0,0,0.1) !important;
    }
    .ui-pnotify .ui-pnotify-shadow {
        box-shadow: 1px 1px 6px rgba(0,0,0,.1) !important;
            
    }
    
    
    
    
    
    
    
    
/* TAVERN CHAT */
    .alert.alert-info.alert-sm {display: none !important;}
    .tavern-chat li, .party-chat li {
        padding: .5em .75em !important;
        border: none !important;
        box-shadow: inset 0 0 0px 1px rgba(0,0,0,0.15),
            0 0 4px rgba(0,0,0,0.15) !important;
        border-radius: 6px !important;
            margin: 5px 0 !important;
        color: #111 !important;
        font: 14px/1.4 Open Sans, Lato, sans-serif !important;   
    }
    .tavern-chat .own-message, .party-chat .own-message {
        /*border-left: 6px solid #36c !important;*/
        box-shadow: 0 0 4px 1px #327CDB,0 0 4px rgba(0,0,0,0.15) !important;
        
    }
    
    .tavern-chat li:nth-child(even), 
        .party-chat li:nth-child(even) {
        background: #FFF !important;
    }
    .tavern-chat li:nth-child(odd), 
        .party-chat li:nth-child(odd) {
            background: #F0F7FA !important;
        }
    
    
    li.chat-message .label {
        opacity: 0.8 !important;
    }
    li.chat-message .label-default {
        background-color: rgba(0,0,0,0.6);
        font-size: 85%;
        top: .7em;
    }
    /* likes */
    li.chat-message a[ng-show="countExists(message.likes)"] {
        background: rgba(0,0,0,0.0) !important;
        color: #36c !important;
    }


        .label-contributor-1,.label-contributor-2{
          background-color:#222 !important;
        }
        .label-contributor-3,.label-contributor-4{
          background-color:#077409 !important;
          color:#fff
        }
        .label-contributor-5,.label-contributor-6{
          background-color:#125ba2 !important;
          color:#fff
        }
        .label-contributor-7{
          background-color:#7313b4 !important;
          color:#fff
        }
        .label-contributor-8{
          background-color:#ff8000 !important;
          color:#fff
        }
        .label-npc{
          background-color:#cd5c5c !important;
          color:#fff
        }
    
    .tavern-chat li .scrollable-message {
        max-height: 300px !important;
        overflow: visible !important;
    }
    
    .muted.time {
        font-size: 80% !important;
        color: rgba(0,0,0,0.4) !important;
    }
    
    





}
