@namespace url(http://www.w3.org/1999/xhtml);@-moz-document domain("habrastorage.org"){
.wrapper .header .logo{opacity:1!important; background-image: url(data:image/png;base64,)!important}

.content #upload_cloud{width: 143px; border-radius: 71px; background-image: url(data:image/png;base64,)}
.content #upload_cloud img{visibility: hidden}
}
@-moz-document domain("toster.ru"){.sidebar .navbar .logo{background-position:0 0!important; background-image: url(data:image/png;base64,)!important}}
@-moz-document domain("tmtm.ru"){.n_layout .inner .logo{background-position:0 0!important; background-image: url(data:image/png;base64,)!important}
.n_content .slide_column.column .slide+.slide+.slide{background-image: url(data:image/png;base64,)!important; background-position:center center!important; background-repeat: no-repeat}
.n_content .slide_column.column .slide img[src*=slide_3]{visibility: hidden}
}
@-moz-document domain("freelansim.ru"){.layout__navbar .logo_wrap .logo{background-image: url(data:image/png;base64,)!important}
}
@-moz-document domain("habrahabr.ru"){#navbar .logo{width:71px!important;height:71px!important; background-image: url(data:image/png;base64,)!important; background-position: 0 0!important; background-size: 71px 71px!important}
}
