css:
/*
A theme by siliconic
for wikipedia.org
ver 0.1.1

(forked from ilkkah's great work @ stylebot.me/styles/29)

find more styles + updates + give me some feedback @
userstyles.org/users/258518
github.com/siliconic
*/

@-moz-document domain(wikipedia.org) {

#bodyContent {
    font-size: 18px;
    z-index: 99;
}

#bodyContent .rellink, #bodyContent .dablink {
    font-size: .9em;
    line-height: 1.4em;
}

#bodyContent .searchresults ul {
    font-size: 1rem;
}

#bodyContent dl, #bodyContent p {
    /*font-size: 16px;
    line-height: 26px;
    margin-bottom: 15px;*/
    font-size: 16px;
    line-height: 1.6em;
    /* 1.4em */
    margin-bottom: 1em;
}

#bodyContent h2, #bodyContent h3, #bodyContent h4, #bodyContent h5, #bodyContent h6 {
    border-bottom: none;
    letter-spacing: -1px;
    /* added 030113 */
}

#bodyContent ol, #bodyContent ul {
    font-size: .85rem;
    line-height: 1.4rem;
    margin-bottom: 1em;
}

#bodyContent td.description p {
    font-size: 14px;
    line-height: 20px;
    margin-bottom: 15px;
}

#content {
    background: #fbfbf9;
    background-image: none;
    border: 0;
    color: black;
    font-family: "minion-pro-1", Cambria, Palatino, "Palatino Linotype", Helvetica, Arial, Georgia;
    margin: auto;
    margin-bottom: 0;
    margin-top: 0;
    max-width: 1000px;
    padding: 1em 2em;
    position: relative;
    right: -150px;
    z-index: 44;
}

#firstHeading {
    margin-top: 50px;
}

#footer {
    display: none;
}

#left-navigation {
    clear: both;
    margin-bottom: 0;
    margin-left: 11px;
    margin-top: 0;
}

#mw-content-text blockquote {
    font-style: italic;
}

#mw-head .vectorTabs, #mw-head .vectorMenu {
    font-size: .8em;
}

#mw-head, #mw-panel {
    display: block;
    padding-left: .5em;
    position: static;
}

#mw-navigation {
    left: 0;
    position: absolute;
    top: 0;
}

#mw-page-base {
    display: none;
}

#mw-panel {
    -webkit-transition: all 0.15s ease-out;
    opacity: .25;
    top: 30px;
    z-index: 8;
}

#mw-panel.mw-gettingstarted-margin-vshift {
    margin-top: 0;
}

#mw-panel:hover {
    opacity: 1;
}

#p-logo {
    display: none;
}

#p-logo a {
    display: none;
}

#p-personal > ul {
    max-width: 10em;
    padding-left: 0;
    padding-top: .33em;
}

#p-personal li:last-child {
    margin-bottom: 1em;
}

#p-personal, #left-navigation, #p-views, #p-cactions {
    opacity: .2;
    transition: all 0.15s ease-out;
}

#p-personal:hover, #left-navigation:hover, #p-views:hover, #p-cactions:hover {
    opacity: 1;
}

#p-search {
    clear: both;
    float: none;
    margin-left: 4px;
    opacity: .2;
    padding-top: 1em;
    position: static;
    transition: opacity .8s ease-out 2s;
}

#p-search:hover {
    opacity: 1;
    transition: opacity 0.15s ease-out;
}

#p-personal, #left-navigation, #right-navigation, #p-namespaces {
    display: block;
    float: none;
    left: auto;
    position: inherit;
}

#right-navigation {
    clear: both;
    margin-left: 11px;
    margin-top: 0;
}

#siteNotice {
    opacity: .7;
}

#siteSub {
    display: none;
}

.mw-editsection {
    -webkit-transition: all 0.15s ease-out;
    float: right;
    line-height: 27px;
    opacity: .1;
    vertical-align: baseline;
}

.mw-editsection a {
    color: black;
}

.mw-editsection:hover {
    opacity: 1;
}

.mw-editsection:hover a {
    color: #0645ad;
}

.thumbcaption {
    color: rgba(0,0,0,.7);
    font-size: .64em;
}

a.internal.hoverZoomLink img {
    display: none;
}

a.variable_lp.cn-full-banner-click {
    display: none;
}

blockquote.templatequote div {
    color: #4d4d4d;
    font-size: 14px;
    font-style: italic;
    line-height: 20px;
}

body {
    min-width: 1000px;
}

body.ns-special #mw-head .vectorTabs {
    /* cleanup nav tabs space on 'special pages' */
    visibility: hidden;
}

body.ns-subject #mw-content-text > p:first-of-type {
    margin-top: 2.2em;
}

body.ns-subject #mw-content-text .ambox {
    color: rgba(0,0,0,.9);
}

body.ns-subject #mw-content-text .ambox .mbox-text-span {
    padding-top: .6em;
    display: block
    
;
}

body.ns-subject #mw-content-text .ambox .hide-when-compact {
    color: rgba(0,0,0,.5);
    /* default table text color */
    margin: .64em 0 0 1em;
    display: block;
    font-style: italic;
    font-size: .9em;
}

body.ns-subject #mw-content-text table.ambox {
    margin: 0;
}

body.ns-subject #mw-content-text .ambox .hide-when-compact a {
    font-size: .9em;
}

body.ns-subject #mw-content-text table.ambox .mbox-text-span small * {
    color: rgba(0,0,0,.5);
    font-size: .85em;
    /* default table text color */
}

body.ns-subject #mw-content-text .ambox .hide-when-compact + small {
    margin: 0 0 0 1em;
    display: block;
}

body.ns-subject #mw-content-text .ambox .mbox-image {
    vertical-align: top;
}

body.ns-subject #mw-content-text .reflist {
    color: rgba(0,0,0,.9);
}

body.page-Main_Page #mp-tfp table {
    color: inherit;
    /* not applying the general article rule for table text color here */
    margin: 0 3px 3px;
}

body.page-Main_Page #mp-tfp table td:nth-child(even) {
    padding: 0 6px 0 0;
}

body.page-Main_Page #mp-tfp table td:nth-child(odd) {
    padding: 0 0.9em 0 0;
}

body.page-Main_Page #mw-content-text > table, body.page-Main_Page table#mp-left, body.page-Main_Page table#mp-right, body.page-Main_Page table#mp-bottom, body.page-Main_Page #mp-tfp > table {
    margin: 0;
    padding: 0;
}

body.page-Main_Page table#mp-lower {
    margin: 4px 0 0 0;
}

body.page-Main_Page table#mp-other {
    padding-bottom: 2px;
    padding-top: 4px;
}

body.page-Main_Page table#mp-topbanner {
    margin: 1.2em 0 6px 0;
}

body.page-Main_Page table#mp-upper {
    margin: 4px 0 0 0;
}

body.page-Main_Page td.MainPageBG > table tr:nth-child(4) td {
    padding: 2px 5px 5px;
}

body.page-Main_Page td.MainPageBG > table tr:nth-child(odd) td {
    padding: 2px;
}

caption.fn {
    margin-bottom: 5px;
}

div#simpleSearch {
    width: 10em;
}

div.catlinks {
    display: none;
}

div.references-small {
    font-family: Helvetica, Helvetica, Arial, sans-serif;
}

div.references-small.references-column-count.references-column-count-2 {
    font-family: Helvetica, Helvetica, Arial, sans-serif;
}

div.references-small.references-column-width {
    font-family: Helvetica, Helvetica, Arial, sans-serif;
}

div.thumbinner {
    background-color: #fcfcfc;
    border-style: none;
    font-family: Helvetica, Arial, "Lucida Grande", Verdana, sans-serif;
}

h1,h2,h3,h4,h5,h6 {
    font-weight: bold;
}

h1.firstHeading {
    font-family: Cambria, Palatino, "Palatino Linotype", serif;
}

img.thumbimage {
    border-style: none;
}

pre {
    font-size: 14px;
}

span.editsection {
    color: #bdbdbd;
    display: none;
}

span.editsection a {
    color: #d6d6d6;
}

sup.reference a {
    color: #777;
    font-size: 10px;
    font-weight: 300;
}

table {
    background: #fcfcfc;
    border-radius: 4px;
    border-style: none;
    box-shadow: 0px 0px 6px #eee;
    color: rgba(0,0,0,.5);
    font-family: Helvetica, Arial, "Lucida Grande", Verdana, sans-serif;
    font-size: 12px;
    line-height: 20px;
    margin-bottom: 15px;
    margin-left: 2.2em;
    margin-top: 20px;
    padding: 10px;
}

table * {
    font-size: 12px;
    line-height: 20px;
}

table tr td {
    background-color: none;
    padding: 2px 5px;
}

table.metadata.mbox-small.plainlinks {
    display: none;
}

table.navbox, table.navbox-inner {
    /* aligns footer boxes with content */
    margin-left: 0;
    padding: 0;
}

table.navbox.vcard {
    display: none;
}

table.toc.tochidden {
    margin-bottom: 15px;
    margin-top: 20px;
}

table.tracklist {
    color: rgba(0,0,0,.7);
}

tbody tr td {
    border-color: #e0e0e0;
}

tbody tr th {
    background-color: #fcfcfc;
    border-color: #a3a3a3;
    font-family: Helvetica, Arial, "Lucida Grande", Verdana, sans-serif;
}

th.navbox-title {
    background-color: #e3e3e3;
    font-family: Helvetica, Helvetica, Arial, sans-serif;
}

}
