css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mobile.opennet.ru"), domain("pda.opennet.ru") ,domain("m.opennet.ru") {

div[onclick*="do_show_thread"]{
font-size: 75% !important;
}
A.h:link { text-decoration: none !important; color: #0000AD !important; } 
A.h:visited { text-decoration: none !important; color: #0000AD !important; }
A.h:hover {color : #0080C0 !important; text-decoration: underline !important;}
A:link { text-decoration: none !important; color: #5050A0 !important; } 
A:visited { text-decoration: none !important; color: #606090 !important; }
A:hover {color : #0080C0 !important; text-decoration: underline !important;}
TD {color : #303030 !important; font-size: 16px !important;padding-bottom: 1px !important}
small, table.tlist td {font-size : 13px !important;}
.small {font-size : 13px !important;}
P.a, table.tlist { TEXT-ALIGN: left !important; TEXT-INDENT: -5px !important;margin-bottom: 2px !important;margin-top: 2px !important;
margin-left: 13px !important; margin-right: 6px !important;font-size: 13px !important}
.cnt { color: #707088 !important;font-size: 9px !important;}
.hdr {font: bold 16px Arial, sans-serif !important; border-bottom: 1px solid #b0b190 !important; padding: 2px !important; padding-left: 8px !important;}
div.tdelim {padding: 1px !important; height: 2px !important; background: #cdd4b9 !important; margin-left: -13px !important; width: 80px !important;}
.ctxt{
font-size: 14px !important;
}
.chdr {
font-size: 12px !important;
}
.chtext {
padding-right: 15px !important;
padding-left: 10px !important;
}
.chtext2 {
color: rgb(80, 80, 80) !important;
font-size: 15px !important;
}
td[style="width: 100%; padding: 5px; background-color: #C7CBB1; font-size: 12pt;"] {
font-size: 14px !important;
height: 40px !important;
}
textarea {
max-width: none !important;
}
a[href="http://mobile.opennet.ru/"]:after {
content: " для КПК";
white-space: nowrap !important;
}
td[style="width: 100%; padding: 5px; background-color: #C7CBB1; font-size: 12pt;"] br {
display: none !important;
}
}
@-moz-document url("http://mobile.opennet.ru/"), url("http://pda.opennet.ru/") {
body table[width="100%"] {
padding-left: 8px !important
}
}
@-moz-document url-prefix("http://mobile.opennet.ru/openforum"), url-prefix("http://pda.opennet.ru/openforum") {
TD {
font-size: 14px !important;
}
}
