body{
margin-top:60px;
}
#left-sidebar>* {
background-color: none;
margin: 0px;
margin-left: 5px;
padding: 0px;
}
nav {
position:absolute;
left:-5px;
top:0px;
margin:0px;
padding:0px;
width:100%;
text-align: center;
}
nav ul {
list-style-type: none;
margin: 0;
padding: 0;
display: block;
background-color:#282828;
height:60px;
overflow: hidden;
}
nav ul li a:not(#show-advanced-search){
float:left;
display: block;
  position: relative;
  margin: 0;
  border-top: 1px dotted #3a3a3a;
  border-bottom: 1px dotted #1b1b1b;
  padding: 20px 20px 0px 20px;
  background: #282828;
  height:38px;
  vertical-align: middle;
}
nav a:visited, nav a:link, nav a:active{
  font-family: Helvetica, Arial, sans-serif;
  color: #d8d8d8;
  text-decoration: none;
  text-transform: uppercase;
  text-shadow: 0 1px 1px #000;
  font-size: 13px;
  font-weight: 300;
}
 nav  ul li:first-child, nav ul li:first-child a{
  color:#FfFfFf;
  float:right;
  width:200px;
}
 nav  ul li a:hover:not(#show-advanced-search){
  color:#FfFfFf;
  text-shadow: 0 1px 0 rgba(0, 0, 0, 0.3);
  background: #008bc7;
  background: -webkit-linear-gradient(#008bc7,#0064aa);
  background: -moz-linear-gradient(#008bc7,#0064aa);
  background: linear-gradient(#008bc7,#0064aa);
  border-color: transparent;
}
#search-submit{
  width:40px;
}
#advanced-search{
    color: d8d8d8;
    position: absolute;
    top:35px;
    right:0px;
    width:300px;
 	background-color:#282828;
 	border-bottom-left-radius: 4px;
  	border-bottom-right-radius: 4px;	
  }
}
