css:
@-moz-document url-prefix("http://www.google.com/calendar/"), url-prefix("https://www.google.com/calendar/") {
body:before {
        content: "" !important;
        position: fixed !important;
        top: -10px !important;
        left: 0 !important;
        width: 100% !important;
        height: 10px !important;
        z-index: 1000 !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.5) !important;
   }

    html, .html, body, .body, #html, #body {
        font-family: 'Open Sans','Segoe UI','Helvetica',sans-serif !important;
        text-decoration:none !important;
        word-spacing:0.1em;outline :none;
    }
    


    #mainlogo {
        font-variant:small-caps !important;
        font-size:16px !important;
        line-height: 22px !important;
        height: 22px !important;
        font-weight: 800;
        letter-spacing: 2px;
        color:#555 !important;
        text-shadow: -1px -1px 0px rgba(0, 0, 0, .2);
        font-family: 'Segoe UI','Open Sans', 'Helvetica', sans-serif !important;
        border-top-right-radius: 2px;
        border-bottom-right-radius: 2px;
    }

    .applogo {
        line-height: 22px !important;
        height:22px !important;
        width:150px !important;
        left:0 !important;
        height: 40px !important;
        padding-top: 5px !important;
        padding-left: 0px !important;
    }
    
    .applogo span {
        background: linear-gradient(to bottom,  rgba(252,255,244,1) 0%,rgba(233,233,206,1) 100%) !important;
        padding: 2px 15px !important;  
        margin-left: 0px !important;  
        margin-top: 0 !important;  
        position: relative !important;  
        width: 80% !important;
        box-shadow: 
            inset 0px 1px 2px rgba(0,0,0,.5), 
            40px -20px 50px lime, 
            40px 30px 50px red, 
            -40px 30px 50px yellow, 
            -40px -30px 50px blue !important;
        color: #454545 !important;  
        text-shadow: 0 1px 0 white !important;
    }
    


    #gb {
        font-family: 'Segoe UI', 'open sans', 'Helvetica', sans-serif !important;
        font-size: 12px !important;
        font-weight: 600 !important;
    } 
    
    #gbgs3 {
        background-image: linear-gradient(to bottom,  rgba(252,255,244,1) 0%,rgba(233,233,206,1) 100%) !important;
    }

    #gbqfw {
        line-height:19px !important;
        font-family: 'Segoe UI', 'open sans', 'Helvetica', sans-serif !important;
    }
    
    .gbqfb, .gbqfba, .gbqfbb {
        height:19px !important;
        line-height: 19px !important;
        font-family: calibri, 'open sans', 'Helvetica', sans-serif !important;
        text-shadow:none !important;
    }
    .gbqfi {
        height: 13px !important;
        margin: 2px 19px !important;
        width: 14px !important;
        font-family: 'Segoe UI', 'open sans', 'Helvetica', sans-serif !important;
    }

    #gbx1 {
        box-shadow:0 1px 0px rgba(0,0,0,.2), inset 0 0px 2px rgba(0,0,0,.3) !important;
        background: linear-gradient(to bottom,  #ffffff 0%,#e5e5e5 100%) !important;
        border-bottom:none !important;
        border-top:none !important;
    }
    
    #gbx3 {
        background: linear-gradient(to bottom,  #ffffff 0%,#e5e5e5 100%) !important;        
        color: #555 !important;
        border:none !important;
        box-shadow: inset 0 -1px 2px rgba(0,0,0,.2) !important;
    }
    
    #gbz .gbz0l .gbts {
        color : #555 !important;
    }
    
    #gb_24 {
        background: linear-gradient(to bottom,  #fcfff4 0%,#e2e2e2 46%,#bcbcbc 100%) !important;
        box-shadow: inset 0px 1px 3px rgba(0,0,0,.8) !important;
    }
    
    
    #gbz .gbzt, #gbz .gbgt {
        color: #555 !important;
        font-weight: bold;
        text-shadow:0 1px 0 white !important;
    }

    #vr-nav {
        border-bottom:none !important;
    }
    
    .gbqfif, .gbqfsf, #gbqfq, #gbqfqb, #gbqfqc, #gbqfqw {
        font-size: 13px !important;
        height: 19px !important;
        font-family: 'Segoe UI', 'open sans', 'Helvetica', sans-serif !important;
    }
    
    #gbqfq, #gbqfqb, #gbqfqc {
        margin-top: 0 !important;
    }
    
    .gb_W {
        height: 20px !important;
        line-height: 20px !important;
        width: 20px !important;
    }
    
    .gb_X {
        background-size: 20px auto !important;
        height: 20px !important;
        width: 20px !important;
    }

    #gbu .gbt {
        margin-left: 0 !important;
    }
    
    .gb_gbnh .gbmab, .gb_gbnh .gbmac {
        left: 6px !important;
    }
    
    #gbq, #gbu, #gbg6 #gbi4t, #gbg4 #gbgs4d {
        text-shadow: 0 1px 0 rgba(255,255,255,1) !important;
    }

    .gb_aa {
        background-size: 20px auto !important;
        height:20px !important;
    }



    #calcontent.eui-t #vr-nav .goog-imageless-button, #calcontent.eui-t .date-nav-today .goog-imageless-button {
        min-width: 50px !important;
    }

    .goog-imageless-button, .navbuttonouter {
        border-radius:0px !important;
        color: #555 !important;
        cursor: pointer;
        font-size: 11px;
        font-weight: 600 !important;
        line-height: 22px !important;
        height: 22px !important;
        text-align: center;
        text-shadow: 0 1px 0px rgba(255,255,255,1);
        background: linear-gradient(to bottom,  rgba(252,255,244,1) 0%,rgba(233,233,206,1) 100%) !important;
        box-shadow: 0 1px 0 rgba(0,0,0,.3) !important;
        
    }

    .goog-imageless-button-hover, .navbuttonouter:hover {
        box-shadow:inset 0px 1px 2px rgba(0,0,0,.2) !important;
    }

    .goog-imageless-button-checked {
        box-shadow:
            inset 0px 2px 3px rgba(0,0,0,.5),
            inset 0 1px 1px rgba(0,0,0,.5),
            0px 1px 0px rgba(0,0,0,.3) !important;
       
        background: linear-gradient(
            to bottom, 
            rgba(244,242,210,.75) 0%,
            rgba(206,201,173,.75) 50%,
            rgba(196,184,153,.75) 51%,
            rgba(221,219,204,.75) 100%) !important;
    }

    .goog-imageless-button-focused, .navbuttonouter:focus {
       border: 1px solid rgba(0, 0, 0, 0.1) !important;
    }

    .navbutton {
       margin-top: 2px !important;
    }

    #mg-settings .goog-imageless-button-outer-box {
       top:3px !important;
    }
    
    .icon-button { 
        top:2px !important;
    }

    .qnb-container .goog-imageless-button, .action-btn-wrapper .goog-imageless-button {
        
        background: linear-gradient(to bottom,  rgba(252,255,244,1) 0%,rgba(233,233,206,1) 100%) !important;
        border: 1px solid rgba(0, 0, 0, 0.1) !important;
        text-shadow: 0 1px 0 rgba(255, 255, 255, 1) !important;
        text-transform:capitalize !important;
        box-shadow: 1px 1px 2px rgba(0,0,0,.5) !important; 
    }



    .date-top {
        color: #555 !important;
        font-family: 'open sans', 'Segoe UI Light', 'Helvetica', sans-serif !important;
        font-weight:100 !important;
        font-size: 14px !important;
        letter-spacing: 1px;
        text-transform:capitalize;
        text-shadow: 0px 1px 1px rgba(0,0,0,.3);
        
    }

    .date-nav-prev {
        padding-left: 6px !important;
    }

    .date-picker-off {
        padding-left: 30px !important;
    }



    #mothertable .st-dtitle-today, #container .st-dtitle-today,
    .st-dtitle-today, .wk-full-mode .wk-today {
        background-image: linear-gradient(to bottom, #FD8, #FD5) !important;
        color: #555 !important;
        font-weight: 600 !important;
        text-shadow:0 1px #fff !important;
        border-left: 1px solid #AAA !important;
        border-top: 1px solid #FD5 !important;
        border-bottom: 1px solid #FA0 !important;
        
    }

    .st-bg-today, .st-bg-td-first, .st-bg-td-last, .tg-today {
        background-image: linear-gradient(to bottom, #FFB, #FF8) !important;
        
    }

    .st-bg-next, .st-dtitle-next {
        border-left: 1px solid #AAA !important;
    }

    .st-dtitle-down {
        border-top: 1px solid #ddd !important;
    }

    .lv-today .lv-datecell {
        background-color: #FD8 !important;
        border-right: 1px solid #FD8 !important;
    }

    .lv-today .lv-eventcell {
        background-color: #FFB !important;
    }

    .dp-today {
        background-color: #FFB !important;
        border-color: #FFB !important;
        color: #F70 !important;
    }
    .dp-today-selected {
        background-color: #FFB !important;
        border-color: #FD8 !important;
        color: #F70 !important;
    }





    .wk-scrolltimedevents, .tg-times-pri, .tg-times-sec {
        font-size: 10px !important;
        color: #555 !important;
        text-shadow:0px 1px 1px rgba(0,0,0,.5);
    }
    
    .wk-daynames, .wk-weektop {
        text-transform:capitalize;
        font-family: 'Open Sans', 'Segoe UI', 'Helvetica', sans-serif !important;
    }

    .wk-daynames th {
        background-color:#f7f7f7;
    }

    .wk-dayname {
        background-color: #4D90FE !important;
        background-image: linear-gradient(to bottom, #4D90FE, #4787ED) !important;
        border-left: 1px solid #1154C4 !important;
        border-bottom: 1px solid #1154C4 !important;
        border-top: 1px solid #1154C4 !important;
        color: #FFF !important;
        text-shadow: 0 1px 0 #000;
        margin: 0;
        text-transform:capitalize !important;
        font-size:10px !important;
        letter-spacing:1px;
        line-height:15px !important;
        font-weight:bold !important;
        box-shadow: 0 2px 2px rgba(0,0,0,.5) !important;
    }

    .wk-today {
        text-shadow: 0 1px 0px #757575 !important;
    }

    .wk-daynames th[title^="Sat"],
    .wk-daynames th[title^="sam."],
    .wk-daynames th[title^="Sun"],
    .wk-daynames th[title^="dim."] {
        background-color: #f0f0f0 !important;
    }

    .wk-daynames th[title^="Sat"] span,
    .wk-daynames th[title^="sam."] span,
    .wk-daynames th[title^="Sun"] span,
    .wk-daynames th[title^="dim."] span {
        color: #CCF !important;
    }
   
    .tg-col {
        border-left: 1px solid #AAA !important;
    }
    
    .tg-time-pri {
        border-left: 1px solid #AAA !important;
        box-shadow: 2px 0 2px rgba(0,0,0, .3);
        background-image:linear-gradient(to bottom, #FFF, #EEE)
    }



    .st-c-pos {
        font-family:'calibri','open sans', 'helvetica', sans-serif !important;
        font-size: 10px !important;
    }
    
    .chip dt {
        color: #fff;
    }



    .mv-container {
        border: 1px solid #1154C4 !important;
    }

    .mv-event-container {
        border-right: 1px solid #1154C4 !important;
    }

    

    .mv-daynames-table .mv-dayname {
        background-color: #4D90FE !important;
        background-image: linear-gradient(to bottom, #4D90FE, #4787ED) !important;
        border-left: 1px solid #1154C4 !important;
        border-bottom: 1px solid #1154C4 !important;
        color: #FFF !important;
        text-shadow: 0 1px 1px rgba(0,0,0,.5);
        margin: 0;
        text-transform:capitalize !important;
        font-size:10px !important;
        letter-spacing:1px;
        line-height:20px !important;
        font-weight:bold !important;
    }

    

    .st-dtitle {
        font-size:9px;
        border-right:none !important;
        border-left:1px solid #AAA !important;
    }

        

    .st-bg {
        border-left:1px solid #AAA !important;
    }

    .st-bg-fc  {
        border-left:1px solid #E5E5E5 !important;
    }

    .st-grid .st-dtitle {
        box-shadow:0 1px 1px rgba(0,0,0,.2)
    }

    
    .st-dtitle {
        font-weight:600 !important;
        text-shadow:0 1px 0 rgba(255,255,255,1)
    }
    
    .st-dtitle-nonmonth {
        color: #555 !important;
        font-weight:100 !important;
    }

    .st-bg-table td:nth-child(n+6).st-bg:not(.st-bg-today) {
        background: #f5f5f5 !important;
    }

    .st-grid td:nth-child(n+6).st-dtitle:not(.st-dtitle-today) {
        background-image: linear-gradient(to bottom, #D8E2F2, #BFCFEA) !important;
        border-bottom:1px solid #A0B9E0 !important;
    }

    .st-grid td:nth-child(n+6).st-dtitle {
        color: #3470FA !important;
        font-weight:600 !important;
    }

    .st-grid td:nth-child(n+6).st-dtitle-nonmonth {
        color: #5F89FA !important;
        font-weight:100 !important;
    }

    .tg-weekend {
        background-color: #f5f5f5 !important;
        border-width: 1px 1px !important;
        border-color: #AAA !important;
    }

    .st-grid td:nth-child(-n+5).st-dtitle:not(.st-dtitle-today){
        background-image: linear-gradient(to bottom, #F5F5F5, #DDD) !important;
        border: 1px solid #AAA;
    }



    .calListLabel, .calListLabel-sel {
        font-family:'Segoe UI', 'Open Sans', sans-serif !important;
        font-size:10px !important;
        color:#404040 !important;
    }

    .dp-cell {
        font-family:calibri, 'Open Sans', sans-serif!important;
        font-size:9px !important;
    }

    .dp-cell.dp-dayh {
        font-size:10px !important;
        font-weight:600 !important;
        color:#fff !important;
        text-shadow: 0 1px 0 #000;
        background-color: #4D90FE !important;
        background-image: linear-gradient(to bottom, #4D90FE, #4787ED) !important;
        border-top: 1px solid #1154C4 !important;
    }

    .calHeaderSpace{
        font-family:'segoe UI Semilight', 'Open Sans', sans-serif !important;
        font-size:16px !important;
        font-weight:100;
        text-transform:capitalize !important;
        color: #404040;
    }

    .monthtableBody tbody {
        box-shadow: 0px 1px 5px rgba(0,0,0,.5);
    }

    [id^="dp_0_row_"] td:last-child,
    .dp-days td:last-child,
    [id^="dp_0_row_"] td:nth-child(6),
    .dp-days td:nth-child(6) {
        color: #7575FE !important;
    }

    [id^="dp_0_row_"] td:last-child.dp-offmonth,
    [id^="dp_0_row_"] td:nth-child(6).dp-offmonth {
        color: #000075 !important;
    }

    .lv-row {
        font-family:calibri,'Open Sans', 'Helvetica', sans-serif !important;
        font-size:12px !important;
    }
    
    .listv .lv-firstevent:nth-child(even){
        background-image:linear-gradient(to bottom, #F4F4F4, #EAEAEA) !important;
    }
}
