css:
@-moz-document domain("primewire.ag"),domain("letmewatchthis.ch"){

body,
.main_body,
.container  {  
    background: linear-gradient(to bottom, rgba(77,81,85,.4), 
                #121519) fixed !important;}
    
html { 
    background-image: none !important;
    background: transparent !important; }      
      
#username,
#password,
.titles,
.movie_version,
.tolerance_box,
.sub_titles,
.footer,
.comment_box,
.titles > span,    
.sub_titles > span,
.featured_movie_item > a { 
    background: transparent !important; }      
     
#username,
#password,    
.login_button { 
background: rgba(150,150,150,.1) !important;
    border-color: rgba(150,150,150,.3) !important;}    

.index_item.index_item_ie > a > img,    
.featured_movie_item > a > strong > img,    
td > .movie_thumb { 
    border-color: transparent !important; }
    
.tv_episode_item > a,
.latest_comments.com_class_tv > a:hover,
.latest_comments.com_class_mov > a:hover    {
    color: black !important; }
        
p, 
h2,
.playlist_listing_desc,
.tv_episode_item > a > .tv_episode_name { 
    color: #999999 !important; }

.movie_info_actions > .mlink_splitter { 
    opacity: 0 !important;}
    
.mlink_imdb > a:not(:hover),
.mlink_submitlink > a:not(:hover), 
.trigger > span:not(:hover),
.mlink_buydvd > a:not(:hover)  { 
opacity: .2 !important; }    

.email_subscribe_box > form > input { 
    opacity: .3 !important; }

.comments_box_vote ,
.comment_reply_link > a  { 
    opacity: .4 !important; }

.btn,    
#search_term.box,
td > .movie_ratings { 
    opacity: .6 !important; }
    
.index_item.index_item_ie > a > h2 {
    font-family: trebuchet ms !important;    
    color: rgba(255,0,0,.95) !important;
    text-shadow: 1px 1px 2px rgba(0,0,0,.6) !important;}
    
td > .version_host {
    color: rgba(255,0,0,.9) !important;
    text-shadow: 1px 1px 2px rgba(0,0,0,.6) !important; }
    
.download_link {
    display: none !important; }

.movie_info_genres > a {
    color: rgba(64,204,0,.8) !important; }
}
