css:
@-moz-document domain("wikipedia.org"), domain("wikimedia.org") {
html, body {
    margin: 0 auto !important;
    position: relative;
    max-width: 1200px;
}
p {
margin: 1em 0 1.6em;
line-height: 1.6;
}
#mw-page-base {
 background:none
    
}
div#content {
  box-shadow: 0 16px 64px rgba(0,0,0,.2);
}
div#mw-panel .portal{  transition: all 1s;

opacity: 0;
}
div#mw-panel:hover .portal{
  transition: all 1s;
opacity: 1;
}
div#mw-head {
 right: inherit;
}
#toc, .toc {
display: -moz-inline-block;
float: left;
display: table;
zoom: 1;
border: none;
background-color: #f9f9f9;
padding: 1em;
font-size: 95%;
box-shadow: 0 4px 8px rgba(0,0,0,.25), inset 0 -3px 0 rgba(0,0,0,.1);
border-radius: .5em;
margin: 0 1em 1em -3em;
width: 30%;
box-sizing: border-box;
}

div.infobox_v3
{
margin: 0 -3em 1em 1em;
  border: 1px solid #a7d7f9;
}

div#mw-content-text p:first-of-type {
font-size: 140%;
}

.infobox_v3 .thumbimage {
border: none;
max-width: 99%;
height: auto !important;
border: 8px solid #fff;
box-shadow: 0 4px 8px rgba(0,0,0,.2);
-webkit-transform: rotate(6.25deg);
}

div.thumbinner {
border: 1px solid #ccc;
padding: 8px !important;
background-color: #f9f9f9;
font-size: 94%;
text-align: center;
overflow: hidden;
box-shadow: 0 8px 16px rgba(0,0,0,.2);
}
div.thumbinner:nth-child(2n+1) {
transform-origin: 0 0;
-webkit-transform: rotate(2.25deg) translateX(-1em) skew(-6deg, 4deg) translateX(2px);
}
div.thumbinner:nth-child(3n+1) {
transform-origin: 0 0;
-webkit-transform: rotate(-2.25deg) translateX(-1em) skew(6deg, -4deg) translateX(2px);
}
}
