css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:newtab")




 {
body {

    background-color: #242424 !important;
  
}
#newtab-scrollbox{
background:#252525 !important;
}
#newtab-search-logo {display:none !important;}
.newtab-cell{
background:#151515!important;
border:none !Important;
box-shadow:none !important;
}
.newtab-site{
box-shadow:none !important;
}


.newtab-site:hover::before{
width:100%;

background:rgba(20,20,20,0.5) !important;
height:30px;
z-index:1000;
top:0 !important;



}


#newtab-search-text {
    height: 32px;
    -moz-box-flex: 1;
    padding: 0px 8px;
    background: none repeat scroll 0% 0% padding-box rgba(54, 54, 54, 0.9)!important;
    border-width: 1px;
    border-style: solid;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    border-color: rgba(84, 84, 84, 0.15) rgba(89, 89, 89, 0.17) rgba(66, 66, 66, 0.2)!important;
    box-shadow: 0px 1px 0px rgba(68, 68, 68, 0.02) inset, 0px 0px 2px rgba(66, 66, 66, 0.1) inset, 0px 1px 0px rgba(255, 255, 255, 0.2)!important;
    border-radius: 2.5px 0px 0px 2.5px;
}
input {
    color: #999 !important;
}
#newtab-search-submit {
    background:#222 !important;
border-color: rgba(51, 51, 51, 1)!important;
box-shadow: none !important;

}

#newtab-margin-bottom {
    background-color: transparent !important;
}

.newtab-control{
-moz-transition-duration:0s !important;
z-index:10000;
height:30px !important;
width:30px !important;
top:0 !important;
margin:0 !important;
padding:0 !important;
}
.newtab-control-pin{
background:url("data:image/png;base64,") !important;
left:0 !important;
}
.newtab-control-pin[pinned="true"]{
background:url("data:image/png;base64,") !important;
}
.newtab-control-block{
background:url("data:image/png;base64,") !important;
right:0 !important;
}

.newtab-title{
font-size:14px !important;
height:30px !important;
line-height:30px !important;
background:rgba(30,30,30,0.98) !important;
color:#bbb !important;
}
#newtab-grid > .newtab-row > .newtab-cell .newtab-thumbnail{
background-size:auto auto !Important;
}

.newtab-grid > newtab-row:nth-child(1) > newtab-cell:nth-child(1) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(1) > newtab-cell:nth-child(2) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(1) > newtab-cell:nth-child(3) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(2) > newtab-cell:nth-child(1) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(2) > newtab-cell:nth-child(2) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(2) > newtab-cell:nth-child(3) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(3) > newtab-cell:nth-child(1) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(3) > newtab-cell:nth-child(2) newtab-thumbnail{background-image:none !important;}
.newtab-grid > newtab-row:nth-child(3) > newtab-cell:nth-child(3) newtab-thumbnail{background-image:none !important;}

 .newtab-thumbnail {
    opacity: 0.99!important;
    transition: opacity 100ms ease-out 0s;
}   
#newtab-grid {  -moz-box-flex: 4!important;}    
.newtab-cell { -moz-margin-end: 40px!important; }

.newtab-row { margin-bottom: 30px;}










}
