@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:home"){

html{
  background: #252525 !important;
}

html body{
  font-family: sans-serif;
  background: #252525  repeat !important;
  color: rgb(166,166,166);
  height: 100%;
}

html a{
	color: rgb(0,173,238) !important;
	text-decoration: none;
	font-weight:lighter;
}
html a:hover{
	color: rgb(0,173,238) !important;
	text-shadow: 0px 0px 3px rgba(0,173,238,.5);
	text-decoration:underline;
	-moz-transition: .1s ease-in;
}
html a:active, html a:focus{
	color: rgb(0,173,238) !important;
	text-shadow: 0px 0px 3px rgba(0,173,238,.5);
	text-decoration: none;
	outline:none;
	-moz-transition: .1s ease-in;
}

#searchEngineLogo{
  opacity: 0 !important;
}


div#searchLogoContainer{
  background:none!important; }


#searchText{
  font-weight: lighter;
  width: 100%;
  height: 24px;
  padding: 3px 6px;
  border: 0px solid rgb(0,0,0) !important;
  box-shadow:inset 1px 1px 2px #222!important;
  font-size: 1.2em;
  color: rgb(166,166,166) !important;
  background: #353535 !important;
  border-top-left-radius: 3px !important;
  border-bottom-left-radius: 3px !important;
}

#searchSubmit{
  background: -moz-linear-gradient(rgb(41,41,41), rgb(40,40,40)) !important;
  padding: 4px 7px 6px 7px !important;
  height: 32px !important;
  border: 0px solid rgba(0,0,0,1) !important;
  box-shadow: none !important;
  font-size: 0.8em !important;
  color: rgb(166,166,166) !important;
  text-shadow: 0px -1px 0px rgb(0,0,0);
  cursor: pointer;
  font-family: Verdana, Arial, Tahoma, Calibri !important;
  border-top-right-radius: 3px !important;
  border-bottom-right-radius: 3px !important;
}

#searchSubmit:hover{
  background: -moz-linear-gradient(rgb(37,37,37), rgb(36,36,36)) !important;
  color: rgb(0,173,238) !important;
  text-shadow: 0 0 3px rgba(0,173,238,.5) !important;
  -moz-transition: .1s ease-in;
}

#searchSubmit:active, #searchSubmit:focus{
  color: rgb(0,173,238) !important;
  text-shadow: 0px 0px 3px rgba(0,173,238,.5) !important;
  box-shadow: 0 0 6.5px rgba(0,0,0,.4) inset,
              0 0 5px rgba(0,0,0,.4) inset !important;
  -moz-transition: .1s ease-in;
}

body[dir=rtl] #searchSubmit:active{
  box-shadow: -1px 1px 0px rgb(0,0,0) !important;
}

#snippets{
    color: rgb(166,166,166) !important;
}

#snippet-4148 .show-video{
    color: rgb(0,173,238) !important;
}

#contentContainer{
    background-image: none !important;
}

#launcher{
position:relative!important;
height:100px important;
    border-top: 0px solid rgb(0,0,0) !important;
    box-shadow: none !important;
    background-color: #252525 !important;
    margin-left: 80px !important;

}

.launchButton{
    border-radius: 8px !important; 
}

.launchButton {
color: #252525!important;
    opacity: 0.5 !important;


}

.launchButton:hover{
    background-color:#222 !important;
    border-radius: 1px !important;
    box-shadow: 0px 0px 15px #222 !important;
    color: rgba(255,255,255,.99) !important;
    
    -moz-transition: .2s ease-in;
}

.launchButton:focus{
    background-color: rgba(0,0,0,.2) !important;
    border-radius: 5px !important;
    box-shadow: 0px 0px 15px rgba(255,255,255,.75) !important;
    -moz-transition: .2s ease-in;
}

.launchButton:active{
    background-color: rgba(0,0,0,.35) !important;
    border-radius: 5px !important;
    box-shadow: 0px 3px 7px rgba(0,0,0,.75)inset !important;
    -moz-transition: .2s ease-in;
}
.launchButton:active:hover{
    border: 1px solid transparent !important;
    -moz-transition: .2s ease-in;
}

#aboutMozilla:before{
    content: url("data:image/png;base64,") !important;
}
#downloads:before{
    content: url("data:image/png;base64,") !important;
}
#bookmarks:before{
    content: url("data:image/png;base64,") !important;
}
#history:before{
    content: url("data:image/png;base64,") !important;
}
#apps::before{
    content: url("data:image/png;base64,") !important;
}
#addons:before{
    content: url("data:image/png;base64,") !important;
}
#sync:before{
    content: url("data:image/png;base64,") !important;
}
#settings:before{
    content: url("data:image/png;base64,") !important;
}
body[narrow] #restorePreviousSession::before{
    content: url("data:image/png;base64,") !important;
}
#restorePreviousSession::before{
    content: url("data:image/png;base64,") !important;
}

#restorePreviousSession {
    max-width: 48px !important;
    font-size: 90%;
    overflow:hidden !important;
}
.lau
#brandLogo {opacity:0 !important;}
#brandLogo{
               display:none !important;
}
#aboutMozilla ,#snippets,#restorePreviousSessionSeparator{
   display:none!important;
}

