css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://secure.solve360.com/") {
div.ax-tasklist-title {
   background-color: rgb(232,232,232)!important;
   }


div.ax-task {
   -moz-transition: border-color 1s ease, border-width 1s ease;
   padding-top: 5px!important;
   padding-bottom: 5px!important;
   margin-right: 0px!important;
   border-width: 0px 0px 1px 0px!important;
   border-color: rgb(232,232,232)!important;
   border-style: solid!important;
   }

div.ax-task:hover {
   -moz-transition: border-color 1s ease, border-width 1s ease;
   border-width: 1px 0px 1px 0px!important;
   border-color: rgb(232,0,0)!important;
   font-size:110%!important;
   padding-top: 8px!important;
   padding-bottom: 8px!important;
   }

div.ax-task div.checkbox {
   float:none!important;
   }

div.ax-task div.ax-task-color {
   float:left!important;
   }

div.ax-task div.title {
   padding-bottom: 0px!important;
   margin-bottom: 0px!important;
   float:left!important;
   max-width: 400px!important;
   height: 13px!important;
   overflow:hidden!important;
   }

div.ax-task:hover div.title {
   max-width: 90%!important;
   height: 100%!important;
   float:none!important;
   padding-bottom: 3px!important;
   }

div.ax-task:hover div.title .inner-title {
  font-size: 115%!important;
  font-weight: 300!important;
  padding: 0px 10px 3px 0px!important;
  display:block!important;
  }
div.ax-task:hover div.title .user {
  padding: 0px 10px 3px 0px!important;
  line-height: 150%!important;
  }
div.ax-task:hover div.title .due {
  padding: 0px 10px 6px 0px!important;
  }

div.ax-task:hover div.title .time-rec-hours {
  padding: 0px 10px 6px 0px!important;
  }

div.ax-task div.comment {
   float:left!important;
   display:block!important;
   max-width: 70px!important;
   max-height: 13px!important;
   overflow:hidden!important;
   margin-left:5px!important;
   margin-right:5px!important;
   font-style:italic!important;
   color:#555555!important;
   padding-left:0px!important;
   }

div.ax-task:hover div.comment {
   max-width: 90%!important;
   max-height: 100%!important;
   padding-top: 3px!important;
   padding-bottom: 3px!important;
   }

div.ax-task table.x-act-field {
   padding: 0px 0px 0px 0px!important;
   margin-top: 0px!important;
   margin-bottom: 0px!important;
   float:none!important;
   display:block!important;
   color:#779999!important;
   }

div.ax-task:hover table.x-act-field {
   clear:left!important;
   }

div.ax-task table.x-act-field td{
   padding-top: 0px!important;
   }

div.ax-task table.x-act-field td.x-act-field-title {
   display:none!important;
   }

div.ax-task table.x-act-field td.x-act-field-val {
   border-left-style: dotted!important;
   border-left-width: 1px!important;
   border-left-color: #999999!important;
   padding-left:3px!important;
   }

div.ax-task div.ax-comments {clear:left!important;
   display:block!important;
   }




td.ax-col-type {
   width: 20px!important;
   }   

td.ax-col-content {
   background-color: rgb(242,242,242)!important;
   }   

td.ax-event .comment {
   background-color: rgb(242,0,0)!important;
   max-height: 1px!important;
   overflow: hidden!important;
   text-decoration: none!important;
   }   

td.ax-event .comment .completed {
   text-decoration: none!important;
   }   

table.ax-view:hover .ax-event .comment {
   background-color: rgb(242,242,242)!important;
   max-height: 100%!important;
   }   

td.ax-col-user {
   width: 50px!important;
   padding: 0px 2px 0px 4px!important;
   background-color: rgb(237,242,232)!important;
   }   

td.ax-col-date {
   width: 50px!important;
   padding: 0px 0px 0px 2px!important;
   background-color: rgb(232,237,242)!important;
   }   

.ax-linkedemails .x-grid3-row {
   max-height: 26px!important;
   margin-bottom: 8px!important;
   overflow: hidden!important;
   }

.ax-linkedemails .x-grid3-row:hover {
   max-height: 100%!important;
   }

.ax-linkedemails .x-grid3-row:hover span, .ax-linkedemails .x-grid3-row:hover .x-grid3-cell-inner {
   font-size: 110%!important;
   line-height: 125%!important;
   }

div.x-panel div.x-panel-bwrap table div.x-item-view-item {
   max-height: 0px!important;
   overflow: hidden!important;
   padding-top: 0px!important;
   padding-bottom: 0px!important;
   margin-top: 0px!important;
   margin-bottom: 0px!important;
   }

div.x-item-view:hover div.x-panel-bwrap table div.x-item-view-item {
   max-height: 500px!important;
   overflow: hidden!important;
   padding-top: 3px!important;
   padding-bottom: 3px!important;
   margin-right: 0px!important;
   }

}
