@-moz-document domain("greasyfork.org") {
  p.notice:not(:empty),
  p.alert:not(:empty) {
    margin: -1em 0 0;
    padding: 24px 0 40px 64px;
    background-repeat: no-repeat;
    background-position: 0 50%;
  }

  p.notice:not(:empty):not(.important),
  p.alert:not(:empty):not(.important) {
       -moz-animation: fade-away ease-in 5s;
         -o-animation: fade-away ease-in 5s;
    -webkit-animation: fade-away ease-in 5s;
            animation: fade-away ease-in 5s;
    opacity: 0;
    padding: 0;
    font-size: 0;
  }

  .dialog {
    min-height: 128px;
  }

  .dialog p {
    width: 20em;
    margin: 1em auto;
  }

  .dialog h1:not(:only-child)::before,
  .dialog h1:only-child::before,
  .dialog h1:only-child::after {
    position: absolute;
  }

  .dialog h1:not(:only-child)::before,
  .dialog h1:only-child::before {
    top: 5em;
  }

  p.notice:not(:empty) {
    background-image: url(https://lh4.googleusercontent.com/-N5z0BajYuzI/UygAi-yogmI/AAAAAAAAEEg/rpSP9W0Tvss/s64/despicable-me-2-Minion-icon-7.png);
  }

  p.alert:not(:empty) {
    background-image: url(https://lh6.googleusercontent.com/-a7uBnDDJ_s8/UygAhhBTppI/AAAAAAAAEEU/X1SRBd-SVhI/s64/Curious-Minion-Icon.png);
  }

  .dialog h1:not(:only-child)::before {
    content: url(https://lh6.googleusercontent.com/-a7uBnDDJ_s8/UygAhhBTppI/AAAAAAAAEEU/X1SRBd-SVhI/s128/Curious-Minion-Icon.png);
    left:    -moz-calc(50% - 17em);
    left: -webkit-calc(50% - 17em);
    left:         calc(50% - 17em);
  }

  .dialog h1:only-child::before {
    content: url(https://lh5.googleusercontent.com/-hN3X6Sx2r1c/UygAhov2c3I/AAAAAAAAEEM/773EpKatuf4/s128/Angry-Minion-icon-look-down.png);
    right:    -moz-calc(50% - 17em);
    right: -webkit-calc(50% - 17em);
    right:         calc(50% - 17em);
  }

  .dialog h1:only-child::after {
    content: url(https://lh6.googleusercontent.com/-yhHpUAPymhs/UygAhvTXwfI/AAAAAAAAEEc/kydJ1AOuoGk/s128/agnes-sleeping-icon.png);
    top: 7em;
    left:    -moz-calc(50% - 64px);
    left: -webkit-calc(50% - 64px);
    left:         calc(50% - 64px);
  }

  @-moz-keyframes fade-away {
    0%, 75% {
      opacity: 1;
      padding: 24px 0 40px 64px;
      font-size: inherit;
    }

    100% {
      opacity: 0;
      padding: 0 0 0 64px;
      font-size: 0;
    }
  }

  @-o-keyframes fade-away {
    0%, 75% {
      opacity: 1;
      padding: 24px 0 40px 64px;
      font-size: inherit;
    }

    100% {
      opacity: 0;
      padding: 0 0 0 64px;
      font-size: 0;
    }
  }

  @-webkit-keyframes fade-away {
    0%, 75% {
      opacity: 1;
      padding: 24px 0 40px 64px;
      font-size: inherit;
    }

    100% {
      opacity: 0;
      padding: 0 0 0 64px;
      font-size: 0;
    }
  }

  @keyframes fade-away {
    0%, 75% {
      opacity: 1;
      padding: 24px 0 40px 64px;
      font-size: inherit;
    }

    100% {
      opacity: 0;
      padding: 0 0 0 64px;
      font-size: 0;
    }
  }
}

@-moz-document regexp("^https?://userstyles\.org/styles/98621/.*") {
  #screenshots img.screenshot {
    visibility: hidden;
  }

  #main-screenshot a {
    display: block;
    background-color: transparent !important;
  }

  #main-screenshot {
    background-color: #efefef;
    background-image: url(https://lh6.googleusercontent.com/-qi1hKFD6l6o/UyiflXO4FQI/AAAAAAAAEFE/-LPiL3OanZs/s0/thank-you-2.png);
    background-repeat: no-repeat;
    background-position: 0 50%;
  }
}

@-moz-document regexp("^https?://userstyles\.org/styles/browse.*") {
  .listing-left-info a[href^="/styles/98621/"] {
    display: block;
    background-color: transparent !important;
    background-image: url(https://lh6.googleusercontent.com/-vhKVefGu0AI/Uyihkwa2l5I/AAAAAAAAEFU/dOL4HDBdqn8/s340/style-installed.png);
  }

  .listing-left-info img[src*="thumbnails/98621_"].screenshot {
    visibility: hidden;
  }
}
