/* this style is only for Google Chome users */
html[manifest*="/_/chrome/newtab/manifest?"]  {
height: 100%;
}
html[manifest*="/_/chrome/newtab/manifest?"]  body {
background: #000000;
background: -webkit-linear-gradient(top, #0c0c0c,#2b2c37)!important;
  min-width: 1050px;
}

html[manifest*="/_/chrome/newtab/manifest?"] #gb#gb a.gb_e, html[manifest*="/_/chrome/newtab/manifest?"]    #gb#gb a.gb_f {
color: #fff!important;
text-decoration: none;
}
html[manifest*="/_/chrome/newtab/manifest?"] #gb#gb a.gb_e, html[manifest*="/_/chrome/newtab/manifest?"]    #gb#gb a.gb_f {
color: #404040;
text-decoration: none;
}
html[manifest*="/_/chrome/newtab/manifest?"] .gb_f {
font-weight: bold;
text-shadow: 0 1px 1px rgba(0,0,0,.6);
}
html[manifest*="/_/chrome/newtab/manifest?"] .gb_q .gb_r {
background-position: -117px -207px;
opacity: 1;
}
html[manifest*="/_/chrome/newtab/manifest?"] .gb_va .gb_Ea {
background-position: -207px -69px;
}
html[manifest*="/_/chrome/newtab/manifest?"] .gb_r .gb_Ea {
background-position: -324px -207px;
opacity: 1;
}
html[manifest*="/_/chrome/newtab/manifest?"] .gb_r:hover .gb_Ea, html[manifest*="/_/chrome/newtab/manifest?"]    .gb_r:focus .gb_Ea {
opacity: .85;
}
html[manifest*="/_/chrome/newtab/manifest?"] .gb_o .gb_mb, html[manifest*="/_/chrome/newtab/manifest?"]    .gb_mb {
background-position: -380px -312px;
visibility: visible;
}
html[manifest*="/_/chrome/newtab/manifest?"] .gb_mb {
background-image: url('http://ssl.gstatic.com/gb/images/v1_e0fab53b0.png');
background-size: 472px 432px;
padding: 5px 10px 7px 6px;
top: -5px;
left: -6px;
}
html[manifest*="/_/chrome/newtab/manifest?"] .gb_ob {
background-position: -311px 0;
opacity: .55;
visibility: visible;
height: 30px;
width: 30px;
}
  
html[manifest*="/_/chrome/newtab/manifest?"]  #gb#gb a.gb_e, html[manifest*="/_/chrome/newtab/manifest?"]  #gb#gb a.gb_f {
    color: #ccc!important;}
  
html[manifest*="/_/chrome/newtab/manifest?"]  #gb#gb a.gb_f:hover,html[manifest*="/_/chrome/newtab/manifest?"]  #gb#gb a.gb_f:focus {
      color: #fff!important;}
.disconnect-collapsed {
display: block!important;
}

  
html[manifest*="/_/chrome/newtab/manifest?"]  #lga {
 height: inherit!important;
}

html[manifest*="/_/chrome/newtab/manifest?"]    #lga img{
    padding-top: 0!important;
  }
html[manifest*="/_/chrome/newtab/manifest?"]    #fkbx {
width: 1026px!important;
  max-width: inherit!important;
}

html[manifest*="/_/chrome/newtab/manifest?"]  #mv-tiles {
width: 988px!important;
  height: 380px!important;
  overflow: visible;
}
  
html[manifest*="/_/chrome/newtab/manifest?"]  .mv-tile,html[manifest*="/_/chrome/newtab/manifest?"]  .mv-thumb,html[manifest*="/_/chrome/newtab/manifest?"]  .mv-mask{
height: 150px;
width: 230px;
}

html[manifest*="/_/chrome/newtab/manifest?"]  .mv-tile{
width: 230px;
border:0;
border-radius: 0;
  -webkit-margin-start: 0;
}
html[manifest*="/_/chrome/newtab/manifest?"]  .mv-fav {
left: 50%!important;
margin-left: -8px!important;
}


html[manifest*="/_/chrome/newtab/manifest?"]  .mv-row{
  margin: 0px 0px 10px;
  -webkit-perspective:1000px;
}
html[manifest*="/_/chrome/newtab/manifest?"]  .mv-row:nth-child(1) { -webkit-perspective-origin: 50% 100%;}
  
html[manifest*="/_/chrome/newtab/manifest?"]  .mv-row:nth-child(2) { -webkit-perspective-origin: 50% 0%}


html[manifest*="/_/chrome/newtab/manifest?"]  .mv-row .mv-tile:nth-child(1) { 
          -webkit-transform: rotateY( 30deg)translateZ(95px);
      margin-left: -11px;
    }
        
html[manifest*="/_/chrome/newtab/manifest?"]  .mv-row .mv-tile:nth-child(2) { 
           -webkit-transform: rotateY( 10deg);
margin-left: 40px;
        }
          
html[manifest*="/_/chrome/newtab/manifest?"]  .mv-row .mv-tile:nth-child(3) { 
          -webkit-transform: rotateY(-10deg);
              margin-left: 10px;
    }
              
html[manifest*="/_/chrome/newtab/manifest?"]  .mv-row .mv-tile:nth-child(4) { 
          -webkit-transform: rotateY(-30deg)translateZ(95px) ;
              margin-left: 40px;
        }
                  
         
              
              
html[manifest*="/_/chrome/newtab/manifest?"]  .mv-row + .mv-row .mv-tile { -webkit-box-reflect: below -20px -webkit-linear-gradient(top, rgba(0,0,0,0) 60%,rgba(102,102,102,.8) 100%); }
                
 html[manifest*="/_/chrome/newtab/manifest?"]  .mv-row + .mv-row .mv-page { -webkit-box-reflect: below -28px -webkit-linear-gradient(top, rgba(0,0,0,0) 60%,rgba(102,102,102,.8) 100%); }
               
