css:
/* codemirror/theme/blackboard.css */

@namespace url(http://www.w3.org/1999/xhtml);
@namespace x url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url-prefix('about:stylish-edit') {
  x|*#main-area {background: #0C1021; color: #888; /*.CodeMirror-linenumber*/ }
  x|button.devtools-toolbarbutton {color: inherit; /*.cm-atom*/ border-color: #888 !important; }
  x|*.devtools-textinput {background-color: inherit; color: inherit;}
  x|*.devtools-textinput:not(:hover):not(:focus) {border-color: transparent;}
}

@-moz-document url-prefix("chrome://stylish/content/edit.xul"), /* so Userstyles sorts it as an editor style */
  url-prefix("about:") /* so Stylish popup lists this style on any about: page */ { }
@-moz-document regexp("data:text/html.*rel='stylesheet'%20href='chrome://browser/content/devtools/codemirror/codemirror.css'.*")
{
  div.CodeMirror-dialog  {background: #0C1021; color: #888;}

/* Port of TextMate's mozilla theme */

div.cm-s-mozilla { background: #0C1021; color: #F8F8F8; }
.cm-s-mozilla div.CodeMirror-selected { /**/background: #555 }
.cm-s-mozilla.CodeMirror-focused  div.CodeMirror-selected { background: #253B76; }
.cm-s-mozilla .CodeMirror-gutter { background: #0C1021; border-right: 0; }
/*.cm-s-blackboard .CodeMirror-gutter-text*/
.cm-s-mozilla div.CodeMirror-linenumber { color: #888; }
.cm-s-mozilla .CodeMirror-lines div.CodeMirror-cursor { border-left: 1px solid #A7A7A7; }

.cm-s-mozilla span.cm-keyword { color: #FBDE2D; }
.cm-s-mozilla span.cm-atom { color: #D8FA3C; }
.cm-s-mozilla span.cm-number { color: #D8FA3C; }
.cm-s-mozilla span.cm-def { color: #8DA6CE; }
.cm-s-mozilla span.cm-variable { color: #FF6400; }
.cm-s-mozilla span.cm-variable-2 { /**/color: #FF6400; }
.cm-s-mozilla span.cm-variable-3{ /**/color: #FF6400; }
.cm-s-mozilla span.cm-property { /**/}
.cm-s-mozilla span.cm-operator { color: #FBDE2D;}
.cm-s-mozilla span.cm-comment { color: #AEAEAE; }
.cm-s-mozilla span.cm-string { color: #61CE3C; }
.cm-s-mozilla span.cm-string-2 { color: #61CE3C; }
.cm-s-mozilla span.cm-meta { color: #D8FA3C; }
.-cm-s-mozilla span.cm-error { background: #9D1E15; color: #F8F8F8; }
.cm-s-mozilla span.cm-qualifier { /**/ }
.cm-s-mozilla span.cm-builtin { color: #8DA6CE; }
.cm-s-mozilla span.cm-bracket { /**/ }
.cm-s-mozilla span.cm-tag { color: #8DA6CE; }
.cm-s-mozilla span.cm-attribute { color: #8DA6CE; }
.cm-s-mozilla span.cm-header { color: #FF6400; }
.cm-s-mozilla span.cm-quote { /**/ }
.cm-s-mozilla span.cm-hr { color: #AEAEAE; }
.cm-s-mozilla span.cm-link { color: #8DA6CE; }
.cm-s-mozilla span.cm-special { /**/ }
.cm-s-mozilla pre:not(hing) {color: inherit;}

.cm-s-mozilla.CodeMirror .CodeMirror-matchingbracket { /*color: #0f0;*/ }
.cm-s-mozilla.CodeMirror .CodeMirror-nonmatchingbracket { /*color: #f22;*/ }

.cm-s-mozilla div.CodeMirror-activeline-background { /**/ }

}
