css:
/* codemirror/theme/cobalt.css */

@namespace url(http://www.w3.org/1999/xhtml);
@namespace x url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url-prefix('about:stylish-edit') {
  x|*#main-area {background: #002240; color: #d0d0d0; /*.CodeMirror-linenumber*/ }
  x|button.devtools-toolbarbutton {color: inherit; /*.cm-atom*/ border-color: #d0d0d0 !important; }
  x|*.devtools-textinput {background-color: inherit; color: inherit;}
  x|*.devtools-textinput:not(:hover):not(:focus) {border-color: transparent;}
}

@-moz-document
  url-prefix("chrome://stylish/content/edit.xul"),
/* so Userstyles sorts it as an editor style */
  url-prefix("about:") { }
/* so Stylish popup lists this style on any about: page */

@-moz-document regexp("data:text/html.*rel='stylesheet'%20href='chrome://browser/content/devtools/codemirror/codemirror.css'.*")
{
  div.CodeMirror-dialog  {background: #002240; color: #d0d0d0;}

div.cm-s-mozilla { background: #002240; color: white; }
.cm-s-mozilla div.CodeMirror-selected { /**/ }
.cm-s-mozilla.CodeMirror-focused  div.CodeMirror-selected { background: #b36539; }
.cm-s-mozilla .CodeMirror-gutter { background: #002240; border-right: 1px solid #aaa; }
.cm-s-mozilla div.CodeMirror-linenumber { color: #d0d0d0; }
.cm-s-mozilla .CodeMirror-lines div.CodeMirror-cursor { border-left: 1px solid white; }

.cm-s-mozilla span.cm-comment { color: #08f; }
.cm-s-mozilla span.cm-atom { color: #845dc4; }
.cm-s-mozilla span.cm-number, .cm-s-mozilla span.cm-attribute { color: #ff80e1; }
.cm-s-mozilla span.cm-keyword { color: #ffee80; }
.cm-s-mozilla span.cm-string { color: #3ad900; }
.cm-s-mozilla span.cm-string-2 { /**/ }
.cm-s-mozilla span.cm-meta { color: #ff9d00; }
.cm-s-mozilla span.cm-variable { /**/ }
.cm-s-mozilla span.cm-variable-2,
.cm-s-mozilla span.cm-tag,.cm-s-mozilla span.cm-property.cm-error { color: #9effff; }
.cm-s-mozilla span.cm-variable-3, .cm-s-mozilla span.cm-def { color: white; }
.-cm-s-mozilla span.cm-error { color: #9d1e15; }
.cm-s-mozilla span.cm-bracket { color: #d8d8d8; }
.cm-s-mozilla span.cm-builtin, .cm-s-mozilla span[class="cm-error"],
.cm-s-mozilla span.cm-special { color: #ff9e59; }
.cm-s-mozilla span.cm-link { color: #845dc4; }
.cm-s-mozilla span.cm-property { /**/ }
.cm-s-mozilla span.cm-operator { color: inherit; }
.cm-s-mozilla span.cm-qualifier { /**/ }
.cm-s-mozilla span.cm-header { /**/ }
.cm-s-mozilla span.cm-quote { /**/ }
.cm-s-mozilla span.cm-hr { /**/ }
.cm-s-mozilla pre:not(hing) {color: inherit;}

.cm-s-mozilla.CodeMirror .CodeMirror-matchingbracket { color: inherit; border: thin solid #845dc4; }
.cm-s-mozilla.CodeMirror .CodeMirror-nonmatchingbracket { /**/ }
.cm-s-mozilla div.CodeMirror-activeline-background {background-color: MidnightBlue; }

}
