css:
/* codemirror/theme/eclipse.css */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
  url-prefix("chrome://stylish/content/edit.xul"),
/* so Userstyles sorts it as an editor style */
  url-prefix("about:") { }
/* so Stylish popup lists this style on any about: page */

@-moz-document regexp("data:text/html.*rel='stylesheet'%20href='chrome://browser/content/devtools/codemirror/codemirror.css'.*")
{

.cm-s-mozilla span.cm-meta {color: #FF1717;}
.cm-s-mozilla span.cm-keyword { line-height: 1em; font-weight: bold; color: #7F0055; }
.cm-s-mozilla span.cm-atom {color: #219;}
.cm-s-mozilla span.cm-number {color: #164;}
.cm-s-mozilla span.cm-def {color: #00f;}
.cm-s-mozilla span.cm-variable {color: black;}
.cm-s-mozilla span.cm-variable-2 {color: #0000C0;}
.cm-s-mozilla span.cm-variable-3 {color: #0000C0;}
.cm-s-mozilla span.cm-property {color: black;}
.cm-s-mozilla span.cm-operator {color: black;}
.cm-s-mozilla span.cm-comment {color: #3F7F5F;}
.cm-s-mozilla span.cm-string {color: #2A00FF;}
.cm-s-mozilla span.cm-string-2 {color: #f50;}
.-cm-s-mozilla span.cm-error {color: #f00;}
.cm-s-mozilla span.cm-qualifier {color: #555;}
.cm-s-mozilla span.cm-builtin,
.cm-s-mozilla span[class="cm-error"] {color: #30a;}
.cm-s-mozilla span.cm-bracket {color: #cc7;}
.cm-s-mozilla span.cm-tag,
.cm-s-mozilla span.cm-property.cm-error {color: #170;}
.cm-s-mozilla span.cm-attribute {color: #00c;}
.cm-s-mozilla span.cm-link {color: #219;}
.cm-s-mozilla pre:not(hing) {color: inherit;}

.cm-s-mozilla.CodeMirror .CodeMirror-matchingbracket {
	/*border*/ outline:1px solid grey;
	color:black;
}

}
