css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("tvguide.com") {


#header
,.watchlistBanner
,#listings-provider
,.ad-728x90-top
,.section-header
,.listings-sidebar
,.listings-channel-favorite
,.listings-ad-footer
,#mostpop-wrap
,#footer
,.listings-ad-row
,#listings-filter-tabs
,#listings-search
 {
  height: 0px !important; 
  display:none !important;
}

.listings-channel-row
,.listings-channel-row *
{
  vertical-align: middle !important;  
  height: auto !important;
  line-height: 1 !important;
  padding-top: 0 !important;
  padding-bottom: 0 !important;  
}


.listings-program-attr {
  position: relative !important;
  text-align: right !important;
  float: right !important;
  width: 25% !important;
}
.listings-program-time {
  position: relative !important;
  float: left!important;
  width: 75% !important;
}

.container
,.listings-header-links
 {
  margin: 0 !important; 
  padding: 0 !important;
  height: auto !important;
 }

.listings-content {
  width: 100% !important;
}

.listings-details-subtitle-details
,.listings-program-time {
  color: #666 !important;
}

body {
  margin-top: 0 !important; 
  padding-top: 0 !important; 
  border-top: 0px !important;
  margin-left: 0 !important; 
  padding-left: 0 !important;
  position: absolute;
  top: -35px;
 }

* { 
  padding-bottom: 0 !important;
}

/* below ends the URL definition tag */
}
