css:
/* codemirror/theme/night.css */

@namespace url(http://www.w3.org/1999/xhtml);
@namespace x url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url-prefix('about:stylish-edit') {
  x|*#main-area {background: #0a001f; color: #f8f8f8; /*.CodeMirror-linenumber*/ }
  x|button.devtools-toolbarbutton {color: inherit; /*.cm-atom*/ border-color: #f8f8f8 !important; }
  x|*.devtools-textinput {background-color: inherit; color: inherit;}
  x|*.devtools-textinput:not(:hover):not(:focus) {border-color: transparent;}
}
@-moz-document
  url-prefix("chrome://stylish/content/edit.xul"),
/* so Userstyles sorts it as an editor style */
  url-prefix("about:") { }
/* so Stylish popup lists this style on any about: page */

@-moz-document regexp("data:text/html.*rel='stylesheet'%20href='chrome://browser/content/devtools/codemirror/codemirror.css'.*")
{
  div.CodeMirror-dialog  {background: #0a001f; color: #f8f8f8;}

/* Loosely based on the Midmozilla Textmate theme */

div.cm-s-mozilla { background: #0a001f; color: #f8f8f8; }
.cm-s-mozilla div.CodeMirror-selected,
.cm-s-mozilla.CodeMirror-focused div.CodeMirror-selected { background: #447; }
.cm-s-mozilla .CodeMirror-gutter { background: #0a001f; border-right: 1px solid #aaa; }
.cm-s-mozilla div.CodeMirror-linenumber { color: #f8f8f8; }
.cm-s-mozilla .CodeMirror-lines div.CodeMirror-cursor { border-left: 1px solid white; }

.cm-s-mozilla div.CodeMirror-activeline-background { /**/background-color: #284040; }

.cm-s-mozilla span.cm-comment { color: #6900a1; }
.cm-s-mozilla span.cm-atom { color: #845dc4; }
.cm-s-mozilla span.cm-number, .cm-s-mozilla span.cm-attribute { color: #ffd500; }
.cm-s-mozilla span.cm-keyword { color: #599eff; }
.cm-s-mozilla span.cm-string { color: #37f14a; }
.cm-s-mozilla span.cm-meta { color: #7678e2; }
.cm-s-mozilla span.cm-variable-2,
.cm-s-mozilla span.cm-tag, .cm-s-mozilla span.cm-property.cm-error { color: #99b2ff; }
.cm-s-mozilla span.cm-variable-3,
.cm-s-mozilla span.cm-def { color: white; }
.-cm-s-mozilla span.cm-error { color: #9d1e15; }
.cm-s-mozilla span.cm-bracket { color: #8da6ce; }
.cm-s-mozilla span.cm-comment { color: #6900a1; }
.cm-s-mozilla span.cm-builtin, .cm-s-mozilla span[class="cm-error"],
.cm-s-mozilla span.cm-special { color: #ff9e59; }
.cm-s-mozilla span.cm-link { color: #845dc4; }
.cm-s-mozilla pre:not(hing) {color: inherit;}
.cm-s-mozilla span.cm-operator { color: inherit; }
.cm-s-mozilla span.cm-qualifier { color: #599eff; }

}
