css:
/* codemirror/theme/rubyblue.css */

@namespace url(http://www.w3.org/1999/xhtml);
@namespace x url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url-prefix('about:stylish-edit') {
  x|*#main-area {background: #112435; color: #fff; /*.CodeMirror-linenumber*/ }
  x|button.devtools-toolbarbutton {color: inherit; /*.cm-atom*/ border-color: #fff !important; }
  x|*.devtools-textinput {background-color: inherit; color: inherit;}
  x|*.devtools-textinput:not(:hover):not(:focus) {border-color: transparent;}
}
@-moz-document
  url-prefix("chrome://stylish/content/edit.xul"),
/* so Userstyles sorts it as an editor style */
  url-prefix("about:") 
/* so Stylish popup lists this style on any about: page */ { }
@-moz-document regexp("data:text/html.*rel='stylesheet'%20href='chrome://browser/content/devtools/codemirror/codemirror.css'.*")
{
  div.CodeMirror-dialog  {background: #112435; color: #fff;}

div.cm-s-mozilla { font:13px/1.4em Trebuchet, Verdana, sans-serif; }	/* - customized editor font - */

div.cm-s-mozilla { background: #112435; color: white; }
.cm-s-mozilla div.CodeMirror-selected,
.cm-s-mozilla.CodeMirror-focused div.CodeMirror-selected { background: #38566F; }
.cm-s-mozilla .CodeMirror-gutter { background: #1F4661; border-right: 7px solid #3E7087; min-width:2.5em; }
.cm-s-mozilla div.CodeMirror-linenumber { color: white; }
.cm-s-mozilla .CodeMirror-lines div.CodeMirror-cursor { border-left: 1px solid white; }

.cm-s-mozilla span.cm-comment { color: #999; font-style:italic; line-height: 1em; }
.cm-s-mozilla span.cm-atom { color: #F4C20B; }
.cm-s-mozilla span.cm-number,
.cm-s-mozilla span.cm-attribute { color: #82C6E0; }
.cm-s-mozilla span.cm-keyword { color: #F0F; }
.cm-s-mozilla span.cm-string { color: #F08047; }
.cm-s-mozilla span.cm-meta { color: #F0F; }
.cm-s-mozilla span.cm-variable { /**/ }
.cm-s-mozilla span.cm-variable-2,
.cm-s-mozilla span.cm-tag,
.cm-s-mozilla span.cm-property.cm-error  { color: #7BD827; }
.cm-s-mozilla span.cm-variable-3,
.cm-s-mozilla span.cm-def { color: white; }
.cm-s-mozilla span.cm-error { color: #AF2018; }
.cm-s-mozilla span.cm-bracket { color: #F0F; }
.cm-s-mozilla span.cm-link { color: #F4C20B; }
.cm-s-mozilla.CodeMirror span.CodeMirror-matchingbracket { color:#F0F; }
.cm-s-mozilla span.cm-builtin, .cm-s-mozilla span[class="cm-error"],
.cm-s-mozilla span.cm-special { color: #FF9D00; }
.cm-s-mozilla pre:not(hing) {color: inherit;}
.cm-s-mozilla span.cm-qualifier { color: #F0F; }
.cm-s-mozilla span.cm-operator { color: inherit; }

.cm-s-mozilla div.CodeMirror-activeline-background { background-color: black }

}
