css:
/* codemirror/theme/twilight.css */

@namespace url(http://www.w3.org/1999/xhtml);
@namespace x url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url-prefix('about:stylish-edit') {
  x|*#main-area {background: #141414; color: #aaa; /*.CodeMirror-linenumber*/ }
  x|button.devtools-toolbarbutton {color: inherit; /*.cm-atom*/ border-color: #aaa !important; }
  x|*.devtools-textinput {background-color: inherit; color: inherit;}
  x|*.devtools-textinput:not(:hover):not(:focus) {border-color: transparent;}
}

@-moz-document
  url-prefix("chrome://stylish/content/edit.xul"),
/* so Userstyles sorts it as an editor style */
  url-prefix("about:") { }
/* so Stylish popup lists this style on any about: page */

@-moz-document regexp("data:text/html.*rel='stylesheet'%20href='chrome://browser/content/devtools/codemirror/codemirror.css'.*")
{
  div.CodeMirror-dialog  {background: #141414; color: #aaa;}

div.cm-s-mozilla { background: #141414; color: #f7f7f7; }
.cm-s-mozilla div.CodeMirror-selected ,
.cm-s-mozilla.CodeMirror-focused div.CodeMirror-selected { background: #323232; }

.cm-s-mozilla .CodeMirror-gutter { background: #222; border-right: 1px solid #aaa; }
.cm-s-mozilla div.CodeMirror-linenumber { color: #aaa; }
.cm-s-mozilla .CodeMirror-lines div.CodeMirror-cursor { border-left: 1px solid white; }

.cm-s-mozilla span.cm-keyword {  color: #f9ee98; }
.cm-s-mozilla span.cm-atom { color: #FC0; }
.cm-s-mozilla span.cm-number { color:  #ca7841; }
.cm-s-mozilla span.cm-def { color: #8DA6CE; }
.cm-s-mozilla span.cm-variable { /**/ }
.cm-s-mozilla span.cm-variable-2, .cm-s-mozilla span.cm-tag { color: #607392; }
.cm-s-mozilla span.cm-variable-3, .cm-s-mozilla span.cm-def { color: #607392; }
.cm-s-mozilla span.cm-operator { color: #cda869; }
.cm-s-mozilla span.cm-comment { color:#777; font-style:italic; font-weight:normal; }
.cm-s-mozilla span.cm-string { color:#8f9d6a; font-style:italic; }
.cm-s-mozilla span.cm-string-2 { color:#bd6b18 }
.cm-s-mozilla span.cm-meta { background-color:#141414; color:#f7f7f7; }
.-cm-s-mozilla span.cm-error { border-bottom: 1px solid red; }
.cm-s-mozilla span.cm-builtin,
.cm-s-mozilla span[class="cm-error"] { color: #cda869; }
.cm-s-mozilla span.cm-tag,
.cm-s-mozilla span.cm-property.cm-error { color: #997643; }
.cm-s-mozilla span.cm-attribute { color: #d6bb6d; }
.cm-s-mozilla span.cm-header { color: #FF6400; }
.cm-s-mozilla span.cm-hr { color: #AEAEAE; }
.cm-s-mozilla span.cm-link { color:#ad9361; font-style:italic; /*font-underline*/text-decoration:none; }
.cm-s-mozilla pre:not(hing) {color: inherit;}
.cm-s-mozilla span.cm-qualifier {  color: #f9ee98; }

.cm-s-mozilla div.CodeMirror-activeline-background { background-color: #642; }

}
