css:
/* codemirror\theme\xq-dark.css */

@namespace url(http://www.w3.org/1999/xhtml);
@namespace x url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url-prefix('about:stylish-edit') {
  x|*#main-area {background: #0a001f; color: #f8f8f8; /*.CodeMirror-linenumber*/ }
  x|button.devtools-toolbarbutton {color: inherit; /*.cm-atom*/ border-color: #f8f8f8 !important; }
  x|*.devtools-textinput {background-color: inherit; color: inherit;}
  x|*.devtools-textinput:not(:hover):not(:focus) {border-color: transparent;}
}
@-moz-document
  url-prefix("chrome://stylish/content/edit.xul"),
/* so Userstyles sorts it as an editor style */
  url-prefix("about:") { }
/* so Stylish popup lists this style on any about: page */

@-moz-document regexp("data:text/html.*rel='stylesheet'%20href='chrome://browser/content/devtools/codemirror/codemirror.css'.*")
{
  div.CodeMirror-dialog  {background: #0a001f; color: #f8f8f8;}

