div.cm-s-mozilla { background: #0a001f; color: #f8f8f8; }
.cm-s-mozilla div.CodeMirror-selected { background: /*#a8f*/#ccc; }
.cm-s-mozilla.CodeMirror-focused div.CodeMirror-selected { background: #a8f; }
.cm-s-mozilla .CodeMirror-gutter { background: #0a001f; border-right: 1px solid #aaa; }
.cm-s-mozilla div.CodeMirror-linenumber { color: #f8f8f8; }
.cm-s-mozilla .CodeMirror-lines div.CodeMirror-cursor { border-left: 1px solid white; }

.cm-s-mozilla span.cm-keyword {color: #FFBD40;}
.cm-s-mozilla span.cm-atom {color: #6C8CD5;}
.cm-s-mozilla span.cm-number {color: #164;}
.cm-s-mozilla span.cm-def {color: #FFF; text-decoration:underline;}
.cm-s-mozilla span.cm-variable {color: #FFF;}
.cm-s-mozilla span.cm-variable-2 {color: #EEE;}
.cm-s-mozilla span.cm-variable-3 {color: #DDD;}
.cm-s-mozilla span.cm-property {/**/color: inherit;}
.cm-s-mozilla span.cm-operator {/**/color: inherit;}
.cm-s-mozilla span.cm-comment {color: gray;}
.cm-s-mozilla span.cm-string {color: #9FEE00;}
.cm-s-mozilla span.cm-string-2 {/**/color: #9FEE00;}
.cm-s-mozilla span.cm-meta {color: yellow;}
.-cm-s-mozilla span.cm-error {color: #f00;}
.cm-s-mozilla span.cm-qualifier {color: #FFF700;}
.cm-s-mozilla span.cm-builtin,
.cm-s-mozilla span[class="cm-error"] {color: #30a;}
.cm-s-mozilla span.cm-bracket {color: #cc7;}
.cm-s-mozilla span.cm-tag,
.cm-s-mozilla span.cm-property.cm-error {color: #FFBD40;}
.cm-s-mozilla span.cm-attribute {color: #FFF700;}
.cm-s-mozilla pre:not(hing) {color: inherit;}

.cm-s-mozilla.CodeMirror span.CodeMirror-matchingbracket {color: #cc7;}
.cm-s-mozilla.CodeMirror span.CodeMirror-nonmatchingbracket {/*color: #cc7;*/}
.cm-s-mozilla div.CodeMirror-activeline-background { /**/background-color: #643; }
}
